css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("anond.hatelabo.jp") {
/* --------------------
  reset    
-------------------- */
#globalheader div#bannersub table td a{
 color            : #F3F1EF !important;
}
#headtable,
#intro,
.afc,
.pager-r a,
.sanchor,
h1 img {
 display          : none !important;
}
.body {
 border           : none !important;
 margin           : 0 !important;
 padding          : 0 !important;
}
.box-curve {
 margin           : 0 !important;
 padding          : 0 !important;
 width            : 100% !important;
}
.caption,
h1,
h4,
table td {
 border           : none !important;
}
.hatena-body {
 margin           : 0 !important;
 width            : 100% !important;
}
.refererlist ul,
h2 {
 background-color : inherit !important;
 padding          : 0 !important;
}
h4,
h5{
 margin           : 0 !important;
 padding          : 0 !important;
}
.section {
 margin           : 0 !important;
}
a.keyword {
 border           : none !important;
 color            : inherit !important;
}
p {
 text-indent      : 0 !important;
}
pre {
 margin           : 0 !important;
 padding          : 0 !important;
 background-color :inherit !important;
 font-size        :100% !important;
}
div.section table {
 border           : 0 !important;
 font-size        : 100% !important;
 margin           : 0 !important;
}
div.section table th {
 background-color : inherit !important;
 border           : none !important;
}


/* --------------------
  layout
-------------------- */
body{
  margin:0 auto !important;
  width:75% !important;
  background-color:#FFFEFE !important;
  color:#2B1F10 !important;
}


/* --------------------
  header
-------------------- */
div#bannersub table{
  border:0 !important;
}
#globalheader div#bannersub table td {
  background-color:#806E58 !important;
  color:#F3F1EF !important;
}
.username{
  padding-left:10px !important;
}

/* --------------------
  h1-
-------------------- */
h1{
  margin-top:0 !important;
  padding-left:10px !important;
  background-color:#806E58 !important;
  -moz-border-radius-bottomleft:10px;
  -moz-border-radius-bottomright:10px;
  color:#F3F1EF !important;
  letter-spacing:1px !important;
}
h3{
  margin-top:0 !important;
  margin-left:-3em !important;
  height:2em !important;
  overflow:hidden !important;
  background-color:#FFFEFE !important;
  line-height:2em !important;
}
h4:before{
  content:"**";
}
h5:before{
  content:"***";
}
h4,
h5{
  margin-top:1em !important;
  font-size:100% !important;
  letter-spacing:2px !important;
}


/* --------------------
  hatena-body
-------------------- */
#breadcrumbs{
  padding:4px !important;
  padding-left:10px !important;
  background-color:#806E58 !important;
  -moz-border-radius-bottomleft:10px;
  -moz-border-radius-bottomright:10px;
  color:#F3F1EF !important;
  font-size:120% !important;
  font-weight:bold !important;
  line-height:135% !important;
  letter-spacing:1px !important;
}
#body > div:first-child + div.pager-l,
.navigation{
  display:none !important;
}
#body{
 background-color:inherit !important;
}
.date{
  display:none !important;
}
div.section,
.refererlist > ul{
  margin-left:2em !important;
  padding-left:1em !important;
  border-left:solid 10px #EADECF;
  font-size:95% !important;
}
.box-curve > div.section{
  margin-left:0 !important;
  padding-left:0 !important;
  border:none !important;
  font-size:100% !important;
}
p{
  margin:auto 0 !important;
  line-height:1.1em !important;
}
p.sectionfooter{
  text-align:right !important;
}
blockquote {
  margin:5px 0 !important;
  padding:0.5em 1em !important;
  border: solid 1px #2B1F10 !important;
  color:inherit !important;
  font-size: 100% !important;
}
.caption{
  margin-top:0 !important;
  line-height:2em !important;
  font-weight:bold !important;
}


/* --------------------
  form
-------------------- */
#breadcrumbs input{
  background-color:inherit !important;
  color:#F3F1EF !important;
  font-size:120% !important;
  font-weight:bold !important;
  line-height:135% !important;
  letter-spacing:1px !important;
}
form table td{
  padding:0 !important;
}
input#sbi{
  padding:0 5px !important;
  width:20em !important;
  -moz-appearance: none !important;
  -moz-border-radius:10px;
  border:solid 2px #806E58 !important;
  background:inherit !important;
  color:#2B1F10 !important;
  font-family:sans-serif !important;
  font-size:9pt !important;
}
input#sbb{
  -moz-appearance: none !important;
  -moz-border-radius:10px;
  background:#FAF9F8 !important;
  border:solid 2px #806E58 !important;
  color:#2B1F10 !important;
  font-family:sans-serif !important;
  font-size:9pt !important;
  font-weight:bold !important;
}
input#ss0,
input#ss1,
label font{
  color:#2B1F10 !important;
}


/* --------------------
  a
-------------------- */
a:link{
  color:#2B1F10 !important;
}
a:visited{
  color:#805C30 !important;
}
#breadcrumbs a:link,
#breadcrumbs a:visited{
  color:#F3F1EF !important;
}


/* --------------------
  autopagerize
-------------------- */
.autopagerize_page_separator{
 border:dashed 1px #EADECF;
}
}
