css:
@-moz-document regexp("https?://(www.)?torrentsm((d.(com|eu|me))|(oldova.(com|org|net))).*") {
#menutable img {
    height: 0;
}

#tbl_menu {
    font-size: 40px;
    line-height: 30px;
    text-shadow: 0 1px 0 #ccc,
               0 2px 0 #c9c9c9,
               0 3px 0 #bbb,
               0 4px 0 #b9b9b9,
               0 5px 0 #aaa,
               0 6px 1px rgba(0,0,0,.1),
               0 0 5px rgba(0,0,0,.1),
               0 1px 3px rgba(0,0,0,.3),
               0 3px 5px rgba(0,0,0,.2),
               0 5px 10px rgba(0,0,0,.25),
               0 10px 10px rgba(0,0,0,.2),
               0 20px 20px rgba(0,0,0,.15);
}

#tbl_menu a {
    font-size: 16px;
}

#tmd_logo {
    display: none;
}

.markItUpEditor,input {
    background: transparent;
    border: 2px solid #69aeee;
    border-radius: 5px;
    box-shadow: 0 4px 15px -6px black;
    color: #7091c2;
    font-family: Aller Light;
    font-size: 15px;
    margin-bottom: 1px;
    transition: background .3s ease-in-out, box-shadow .3s ease-in-out;
}

#torrents > table > tbody > tr  {
    background-color:transparent !important;
}

.markItUpEditor:hover,input:hover {
    background: #a3dff5;
    box-shadow: 0 6px 17px -4px black;
}

a, a:visited, a:focus, a:link {
    color: #69aeee;
    text-decoration: none;
    transition: all .3s ease-in-out;
    border-bottom: none;
}

a:active {
    border-bottom: none;
    text-decoration: none;
    text-shadow: 0px 0px 15px #69aeee;
}

body {
    background: #f4f4f4;
    color: #7091c2;
}

div#top_menu>table>tbody>tr>td:first-child>a:before {
    content: "torrents.md";
    font-size: 55px;
    text-shadow: 0 1px 0 #ccc,
               0 2px 0 #c9c9c9,
               0 3px 0 #bbb,
               0 4px 0 #b9b9b9,
               0 5px 0 #aaa,
               0 6px 1px rgba(0,0,0,.1),
               0 0 5px rgba(0,0,0,.1),
               0 1px 3px rgba(0,0,0,.3),
               0 3px 5px rgba(0,0,0,.2),
               0 5px 10px rgba(0,0,0,.25),
               0 10px 10px rgba(0,0,0,.2),
               0 20px 20px rgba(0,0,0,.15);
}

h2 {
    background: #f4f4f4;
    border-bottom: 1px solid #69aeee;
}

input[type="checkbox"] {
    box-shadow: 0 4px 15px -6px black;
    transition: background .3s ease-in-out, box-shadow .3s ease-in-out;
}

input[type="checkbox"]:hover {
    box-shadow: 0 6px 17px -4px black;
}

input[type="submit"] {
    background: transparent;
    border: 2px solid #69aeee;
    border-radius: 5px;
    box-shadow: 0 4px 15px -6px black;
    color: #69aeee;
    cursor: pointer;
    font-family: Aller Light;
    font-size: 14px;
    transition: background .3s ease-in-out, box-shadow .3s ease-in-out;
}

input[type="submit"]:hover {
    background: #a3dff5;
    box-shadow: 0 4px 20px -2px black;
}

select {
    background: transparent;
    border: 1px solid #69aeee;
    border-radius: 5px;
    box-shadow: 0 4px 15px -6px black;
    color: #7091c2;
    font-family: Aller Light;
    font-size: 14px;
    margin-bottom: 1px;
    transition: background .3s ease-in-out, box-shadow .3s ease-in-out;
}

select:hover {
    background: #a3dff5;
    box-shadow: 0 4px 20px -2px black;
}

table {
    border-color: #d8edf0;
}

}
