css:
@-moz-document url-prefix("https://jira"), domain("jira.atlassian.com") {
:root {
  --text-main: #999;
  --text-highlight: #fff;
  --text-darkened: #555;

  /* Colors */
  --color-green: #00a404;
  --color-red: #900;
  --color-orange: #CF9410;
  --color-purple: #C336FF;
  --color-blue: #008f99;
  --color-dblue: #1b4677;

  /* Status Colors */
  --status-todo: var(--color-blue);
  --status-progress: var(--color-orange);
  --status-done: var(--color-green);

  /* Blacks */
	--dark-100: #000;
	--dark-200: #040405;
	--dark-300: #0c0d0e;
	--dark-400: #141517;
	--dark-500: #1c1d20;
	--dark-600: #242629;
	--dark-700: #2c2e32;
	--dark-800: #34363b;
	--dark-900: #3b3e44;

  /* Borders */
  --border-default: var(--dark-300);
  --border-dark: var(--dark-200);

  /* Issue Colors */
  --issue-text: var(--text-main);
  --issue-default: var(--dark-300);
  --issue-hover: var(--dark-200);
  --issue-border: rgba(0,0,0,0.2);

  /* Done Colors */
  --done-text: #006b04;
  --done-default: #001c01;
  --done-hover: #000b00;
  --done-border: #000b00;

  /* Flagged Colors */
  --flagged-text: #b4444f;
  --flagged-default: #200C0E;
  --flagged-hover: #160A0C;
  --flagged-border: #140709;

  /* Shadows */
  --shadow-one: 0 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24);
  --shadow-two: 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23);
  --shadow-three: 0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);
  --shadow-four: 0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22);
  --shadow-five: 0 19px 38px rgba(0,0,0,0.30), 0 15px 12px rgba(0,0,0,0.22);
  --shadow-six: 0 25px 50px rgba(0,0,0,0.33), 0 20px 14px rgba(0,0,0,0.22);
  --shadow-one-inset: inset 0 1px 3px rgba(0,0,0,0.12), inset 0 1px 2px rgba(0,0,0,0.24);
  --shadow-two-inset: inset 0 3px 6px rgba(0,0,0,0.16), inset 0 3px 6px rgba(0,0,0,0.23);
  --shadow-three-inset: inset 0 10px 20px rgba(0,0,0,0.19), inset 0 6px 6px rgba(0,0,0,0.23);
  --shadow-four-inset: inset 0 14px 28px rgba(0,0,0,0.25), inset 0 10px 10px rgba(0,0,0,0.22);
  --shadow-five-inset: inset 0 19px 38px rgba(0,0,0,0.30), inset 0 15px 12px rgba(0,0,0,0.22);
  --shadow-six-inset: inset 0 25px 50px rgba(0,0,0,0.33), inset 0 20px 14px rgba(0,0,0,0.22);
}

}

@-moz-document url-prefix("https://jira"), domain("jira.atlassian.com") {
/* Issues */
.ghx-issue,
.ghx-issue-compact {
  color: var(--issue-text);
	background: var(--issue-default);
  border-color: var(--issue-border);
  transition: background 0.3s ease-in-out, transform 0.3s ease-in-out;
}
.ghx-issue:hover,
.ghx-issue.ghx-selected,
.ghx-issue.ghx-selected:hover,
.ghx-issue-compact:hover,
.ghx-issue-compact.ghx-selected,
.ghx-issue-compact.ghx-selected:hover,
.ghx-issue-compact .ghx-selected-group {
	background: var(--issue-hover);
  backface-visibility: hidden;
  transform: translateX(5px);
  box-shadow: var(--shadow-one);
}
/* Bug */
.ghx-issue.ghx-type-1 .ghx-grabber,
.ghx-issue-compact.ghx-type-1 .ghx-grabber {
  background-color: var(--color-red) !important;
}
/* Task */
.ghx-issue.ghx-type-3 .ghx-grabber,
.ghx-issue-compact.ghx-type-3 .ghx-grabber {
  background-color: var(--color-blue) !important;
}
/* Story */
.ghx-issue.ghx-type-6 .ghx-grabber,
.ghx-issue-compact.ghx-type-6 .ghx-grabber {
  background-color: var(--color-orange) !important;
}
.ghx-issue .ghx-end,
.ghx-issue-compact .ghx-end,
.ghx-issue:hover .ghx-end,
.ghx-issue.ghx-selected .ghx-end,
.ghx-issue-compact:hover .ghx-end,
.ghx-issue-compact.ghx-selected .ghx-end,
.ghx-issue-compact .ghx-selected-group .ghx-end,
.ghx-issue.ghx-flagged.ghx-selected .ghx-end {
  background: transparent !important;
  box-shadow: none !important;
}

.ghx-issue.ghx-flagged,
.ghx-issue-compact.ghx-flagged {
  color: var(--flagged-text);
	background: var(--flagged-default);
  border-color: var(--flagged-border);
}
.ghx-issue.ghx-flagged:hover,
.ghx-issue.ghx-selected.ghx-flagged,
.ghx-issue.ghx-selected.ghx-flagged:hover,
.ghx-issue-compact.ghx-flagged:hover,
.ghx-issue-compact.ghx-selected.ghx-flagged,
.ghx-issue-compact.ghx-selected.ghx-flagged:hover,
.ghx-issue-compact .ghx-selected-group.ghx-flagged {
	background: var(--flagged-hover);
}

.ghx-issue.ghx-done,
.ghx-issue-compact.ghx-done {
  color: var(--done-text);
	background: var(--done-default);
  border-color: var(--done-border);
}
.ghx-issue.ghx-done:hover,
.ghx-issue.ghx-selected.ghx-done,
.ghx-issue.ghx-selected.ghx-done:hover,
.ghx-issue-compact.ghx-done:hover,
.ghx-issue-compact.ghx-selected.ghx-done,
.ghx-issue-compact.ghx-selected.ghx-done:hover,
.ghx-issue-compact .ghx-selected-group.ghx-done {
	background: var(--done-hover);
}

}

@-moz-document url-prefix("https://jira"), domain("jira.atlassian.com") {
/* Header */
.issue-header.detached {
	box-shadow: var(--shadow-two);
}

.issue-header .issue-header-content > .command-bar {
	border-color: var(--border-default);
}

}

@-moz-document url-prefix("https://jira"), domain("jira.atlassian.com") {
/* Body */
.navigator-issue-only .issue-body-content {
	border-color: var(--border-default);
}

}

@-moz-document url-prefix("https://jira"), domain("jira.atlassian.com") {
/* Progress Bars */
.ghx-classification-item:hover .ghx-classification-progress,
.ghx-classification-item.ghx-selected .ghx-classification-progress,
.ghx-classification-progress, .ghx-classification-progress:first-child {
	background: var(--dark-600);
}

.ghx-classification-progress .ghx-implemented {
	background: var(--color-blue);
}

/* Time Calendar */
.tt-bootstrap-component .btn-default,
.tt-bootstrap-component .btn-default:hover,
.tt-bootstrap-component .btn:not(.aui-button):hover,
.tt-bootstrap-component .btn:not(.aui-button):focus,
.tt-bootstrap-component .btn:not(.aui-button).focus {
	background: var(--dark-500);
	color: var(--text-main);
	border: none;
	box-shadow: var(--shadow-one);
}

#ttw-worklog-calendar-app header {
	box-shadow: var(--shadow-two);
}

#ttw-worklog-calendar-app header,
#ttw-worklog-calendar-app h3 {
	background: var(--dark-400);
	border-color: var(--border-default);
}

#ttw-worklog-calendar-app .drop-zone {
	background: var(--dark-500);
	border-color: var(--border-default);
}

#ttw-worklog-calendar-app [class*="_canvas__add_worklog_button_"] {
	background: var(--dark-400);
	color: var(--text-main);
	border-color: var(--border-default);
}

#ttw-worklog-calendar-app [class*="_canvas__add_worklog_button_"]:hover {
	background: var(--dark-200) !important;
	color: var(--text-main);
}

#ttw-worklog-calendar-app [class*="_worklogcard__worklog_card_wrapper_"],
#ttw-worklog-calendar-app [class*="_suggestioncard__suggestion_card_"] {
	background: var(--dark-400);
	color: var(--text-main);
	border-color: var(--border-default);
}

#ttw-worklog-calendar-app [class*="_worklogcard__worklog_card_wrapper_"] p {
	color: var(--text-darkened);
}

#ttw-worklog-calendar-app [class*="_worklogcard__time_spent_seconds_"],
#ttw-worklog-calendar-app [class*="_suggestioncard__suggested_time_"] {
	color: var(--color-blue);
}

#ttw-worklog-calendar-app [class*="_worklogcard__worklog_meta_background_"],
#ttw-worklog-calendar-app [class*="_suggestioncard__suggestion_meta_background_"],
#ttw-worklog-calendar-app [class*="_suggestioncard__suggestion_meta_background_today_"] {
	background-image: linear-gradient(180deg, transparent 10%, var(--dark-400) 55%);
}

#ttw-worklog-calendar-app [class*="_canvas__suggestion_header_content_"] {
	border-color: var(--border-default);
}

#ttw-worklog-calendar-app [class*="_canvas__suggestion_header_content_"] h4 {
	background: var(--dark-300);
	color: var(--text-main);
	padding: 3px 6px;
}

#ttw-worklog-calendar-app [class*="_sidebar__filter_selector_"] {
	background: var(--dark-400);
	color: var(--text-main);
	border-radius: none;
}

#ttw-worklog-calendar-app [class*="_sidebar__filter_name_"],
#ttw-worklog-calendar-app [class*="_sidebar__sidebar_wrapper_"],
#ttw-worklog-calendar-app [class*="_sidebar__select_container_"] {
	background: var(--dark-400);
	border-color: var(--border-default);
	color: var(--text-main);
}

#ttw-worklog-calendar-app [class*="_sidebar__sidebar_header_"] {
	border-color: var(--border-default);
	box-shadow: var(--shadow-two);
}

#ttw-worklog-calendar-app [class*="_sidebar__bucket_item_"],
#ttw-worklog-calendar-app [class*="_sidebar__sidebar_toggle_"],
#ttw-worklog-calendar-app [class*="_sidebar__sidebar_load_more_"] {
	background: var(--dark-400);
	border-color: var(--border-default);
	color: var(--text-main);
}

.ps-container > .ps-scrollbar-y-rail > .ps-scrollbar-y {
	background-color: var(--color-blue) !important;
}

/* Time Sheet */
.content-container,
.tempo .tempo-stalker-content.stalker-content.issue-header-content .aui-page-header {
	background: var(--dark-400);
}

.tempo-timesheet #issuetable td.tt-nonworking-days, .tempo-calendar #issuetable td.tt-nonworking-days {
	background: var(--dark-300);
}

.tempo-timesheet #issuetable {
	background: var(--dark-400);
}

.tempo-timesheet #issuetable thead tr.rowHeader th {
	color: var(--text-main);
}

.tempo-timesheet #issuetable thead tr.rowHeader th a {
	color: var(--text-darkened);
}

.refresh-report .total-work-container .tempo-progress-bar-new .progress-background-color {
	background: var(--dark-500);
	box-shadow: var(--shadow-two);
}

.tempo-progress-bar-new .progress-fill-color {
	background: var(--color-blue);
}

.tempo-timesheet #issuetable td {
	transition: background-color 0.3s ease-in-out;
}

.content-container,
.tempo-timesheet #issuetable td,
.tempo-calendar #issuetable .tt-right-border,
.tempo-timesheet #issuetable .tt-right-border,
.tempo-timesheet #issuetable tbody tr td,
.tempo-timesheet #issuetable th.tt-end-of-week,
.tempo-timesheet #issuetable td.tt-end-of-week,
.tempo-timesheet #issuetable .issuerow:hover td.tt-right-border,
.tempo-timesheet #issuetable .issuerow td.tt-end-of-week,
.tempo-timesheet #issuetable th.headerrow-empty-actions,
.tempo-timesheet #issuetable tfoot th,
table.total-work-bottom th,
table.total-work-bottom td {
	border-color: var(--border-default);
	border-left-color: var(--dark-300);
	border-right-color: var(--dark-300);
}

.tempo-timesheet #issuetable .issuerow:hover td:not(.tt-nonworking-days),
.tempo-timesheet #issuetable .issuerow:hover td.tt-right-border:not(.tt-nonworking-days),
.tempo-timesheet #issuetable .issuerow:hover td.tt-end-of-week:not(.tt-nonworking-days) {
	background: var(--dark-500);
	border-color: var(--border-default);
	border-left-color: var(--dark-400);
	border-right-color: var(--dark-400);
}

.tempo-timesheet #issuetable td.tt-nonworking-days.edit-day:hover {
	background-color: var(--dark-600);
}

.tempo-timesheet #issuetable td.edit-day:focus,
.tempo-timesheet #issuetable td.edit-day:hover,
.tempo-timesheet #issuetable td.edit-day:active {
	background-color: var(--color-blue) !important;
}

.tempo-timesheet #issuetable thead th.tt-current-day.normal {
	border-top-width: 2px !important;
	border-top-color: var(--color-blue) !important;
}

.tempo-timesheet #issuetable tbody tr:last-child td.tt-current-day {
	border-bottom-width: 2px !important;
	border-bottom-color: var(--color-blue) !important;
}

.tempo-timesheet #issuetable thead th.tt-current-day, .tempo-timesheet #issuetable td.tt-current-day {
	border-left-width: 2px !important;
	border-right-width: 2px !important;
	border-left-color: var(--color-blue) !important;
	border-right-color: var(--color-blue) !important;
}

.aui-inline-dialog .buttons-container.content-footer input.cell-add {
	color: #fff;
	background: var(--color-dblue);
	text-shadow: none;
	border-radius: 0;
	opacity: 0.8;
	border: none;
	padding: 4px 10px;
	transition: background 0.3s ease-in-out, opacity 0.3s ease-in-out;
}

.aui-inline-dialog .buttons-container.content-footer input.cell-add:hover {
	color: #fff;
	background: var(--color-dblue);
	text-shadow: none;
	opacity: 1;
}

/* Time Tracking */
#timetrackingmodule dl:nth-child(1),
#timetrackingmodule dl:nth-child(2) {
	display: none;
}

#timetrackingmodule #tt_single_graph_spent td:nth-child(2) {
	background: var(--dark-600) !important;
}

#timetrackingmodule #tt_single_graph_spent td:nth-child(1) {
	background: var(--color-green) !important;
}

/* Work Logs */
.worklogs-module.clear tr.issuerow:hover td {
	background: var(--dark-400);
}

.worklogs-module.clear span.tempo-inline-edit:hover {
	background-color: var(--dark-500) !important;
	border: none !important;
}

/* Calendar */
.pika-single {
	background: var(--dark-500);
	color: var(--text-main);
	border: none;
	box-shadow: var(--shadow-two);
}

.pika-title,
.pika-label {
	background: var(--dark-600);
	color: var(--text-main);
}

.pika-button {
	background: var(--dark-500);
	color: var(--text-main);
}

/* Garbage To Do */

.aui-navgroup-horizontal {
	border-color: var(--border-default);
}

/* Buttons */
.aui-button[aria-pressed="true"],
.aui-buttons .aui-button[aria-pressed="true"],
.aui-buttons .aui-button[aria-pressed="true"].aui-button-primary,
.aui-buttons .aui-button[aria-pressed="true"].aui-button-subtle {
	background: var(--gray-100);
}

html,
body,
html body,
.aui-dialog2-content,
.aui-dialog2-header,
.aui-dialog2-footer,
.activity-item-description .user-content,
#page,
#dashboard,
#ghx-header,
#ghx-backlog,
#issue-content,
.aui-dialog,
