css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {

body {
 background: #2C2C2C !important;
 color: #eee !important;
}
#ad { display: none !important; }
.postarea { padding: 0px !important; }
a {
 color: #efefef !important;
}

hr {
 display: none !important;
}
.thread {background: #333 !important;}

body.read, body.read .thread {background: #333 !important}
#navtop a:hover {
 background: #000000 !important;
}

.logo,.logo font b span{
 color: #fff !important;
}

form {
 margin: 1em !important;
}

.inputtext, textarea {
 width: 295px !important;
}

.rules{
 display: none !important;
}

td.postblock {
 background: #444 !important;
 border: 1px solid #666 !important;
 color: #aaa !important;
}

td.reply {
background-color:#333 !important;
width: 100% !important;
padding: .2em !important;
border: 1px solid #555 !important;
color: #eee !important;
-moz-border-radius:3px !important;
}

td.replyhl {
background-color:#4B4B4B !important;
width: 100% !important;
padding: .2em !important;
border: 1px solid #4B4B4B !important;
color: #eee !important;
-moz-border-radius:3px !important;
}

.postername, .commentpostername, .omittedposts, .abbr {
 color: #fff !important;
}

.filetitle, .replytitle {
color: #e15014 !important;
font-size: large;
}

.doubledash {
visibility: hidden !important;
}


th {
 background: #222 !important;
 padding: .2em !important;
 font-size: small;
 width: 200px !important;
}

#hd {
display: none;
}

.boxbar {
background: #444 !important;
color: #eee !important;
}

.box-outer {
border: none !important;
background: #333 !important;
color: #eee !important;
}

.box-inner {
 background: #333 !important;
color: #eee !important;
}

#filter-button, #option-button {
display: none;
}

#ft{
display: none;
}

.pages {
 background: #444 !important;
 border: none !important;
}

.pages td, .pages td b {
color: #eee !important;
}

#recent-images li{
 background: #333 !important;
}
pre, textarea {
   color: white !important;
   background: #333333 !important;
   font-size: 12pt ! important;
   border-left-color: gray !important;
   border-right-color: gray !important;
   border-top-color: gray !important;
   border-bottom-color: gray !important;
}
input:not([type="button"]):not([type="checkbox"])
:not([type="submit"]):not([type="reset"]), 
select {
    color: white !important;
    background: #333333 !important;
    border-left-color: gray !important;
    border-right-color: gray !important;
    border-top-color: gray !important;
    border-bottom-color: gray !important;
} 
.postarea input[type="file"]{
opacity:0.22 !important;
}

.quote:hover
{
 background: #3e3e3e !important
}

h1 {color: white !important;}
.head {background: #333 !important;}
.navlinks a {
color: gray;
}
.postheader * {background: #333 !important;}
.post {
background: #333 !important;
border: 1px solid #333333 !important;
padding-bottom: .1em;
}
.postblock {background: #333333;}
}
