css:
@-moz-document domain(golem.de) {
  
body {
  font-family: 'Helvetica Neue', Arial, sans-serif !important;
  font-size: 1.05em !important;
}

#header div.dh1 {
  position: absolute;
  top: -5px;
  width: 100% !important;
}

.g4 {
  width: 100%;
}

h1 {

  color: #999;
  font-weight: normal !important;
  padding-left: 46px;
}

.head4::before {
  content: "golem.de ";
  color: rgba(255,255,255,.8);
  font-weight: bold;
  /*text-shadow: -0px 0 rgba(15,60,160,.0), 0 1px rgba(15,60,160,1), 2px 0 rgba(15,60,160,1), 0 -0px rgba(15,60,160,.0);
  position:relative;
  top: -1px;*/
  color: rgba(15,60,160,1)
}

.list-tickers h3 span {
  line-height: .2em;
  color: #333 !important;
  text-transform: uppercase;
  font-size: 65%;
  padding-top: 11px;
  padding-bottom: 0px !important;
}

.list-tickers li {
  padding: none !important;
  max-width: 960px;
}

.list-tickers li:hover .meta {
  color: white !important;
  background: black;
  padding: 5px 3px 3px 3px;
  font-size: 1.4em;
  position: absolute;
  left:-19px;
  width: 50px;
  position: absolute;
  top: -5px;
}

.list-tickers h3 a {
  opacity: .6;
}

.list-tickers li .meta {
  opacity: .6;
  margin-top: 6px;
}


/* Link-Ziel erweitern */
.list-tickers li {
  margin-top: 0px !important;
  margin-bottom: 0px !important;
  padding-top: 5px;
  padding-bottom: 5px;
  max-width: 1660px !important;
}


/* Link-Ziel erweitern */
.list-tickers {
  margin-top: 0px !important;
  margin-bottom: 0px !important;
  padding-top: 5px;
  padding-bottom: 5px;
}


/* Link-Ziel erweitern */
.list-tickers li h3 a {
  padding-right: 295px;
}

.list-tickers li:hover h3 a {
/*  font-size: 1.125em; */
}

.list-tickers li:hover h3 a span {
/*  font-size: .6675em !important; */
}

.list-tickers li:hover .meta, .list-tickers li:hover h3 a {
  opacity: 1;
}

article {
  width: 800px;
  padding-left: 50px;
}

.golem-gallery2, .golem-gallery2-list, .golem-gallery2-show, .golem-gallery2-inneritem img { 
  width: 800px;
}


article p { 
  font-family: 'Helvetica Neue', Arial, sans-serif !important;
  font-weight: 600;
  line-height: 1.8em !important;
  padding: 6px 0px;
  margin: 0 !important;
  font-size: 1em !important;
}

article h1, article h2, article h3 { 
  letter-spacing: 0.2px;
  font-weight: 600;
  line-height: 1.7em !important;
  padding: 10px 0px 0px 0px;
  margin: 0 !important;
}

article h1 {
  padding:0 !important;
}

header .head5 {
  font-size: 28px !important;
  line-height: 1.2;
  padding: 2px 0 6px 0;
  font-weight: normal;
}

header .head1 {
  font-size: 14px !important;
  letter-spacing: 2px;
  color: #aaa;
}

header p {
  font-size: 14px !important;
  line-height: 1.5em !important;
  color: grey;
  padding: 3px 6px;
  background: #eee;
}

h3.text1 {
  font-size: 16px !important;
  padding: 0 0 4px 0 !important;
  line-height:1.4em;
}

article h3 {
  color: #fff !important;
  background: #999;
  font-size: 18px !important;  font-weight: normal !important;
  padding: 6px 18px !important;
  margin: 18px 0px 4px 0px !important;
}



.g4 h2 {
  color: white !important;
	background-image: linear-gradient(90deg, #2A4AA4 20%, #bce 55%, #fff 96%) !important;
  background: rgba(15,60,160,0.85);
  font-size: 18px;  
  text-transform: uppercase;
  padding: 8px 0px 0px 13px !important;
  margin: 20px 0px 0px 0px !important;
  border-top-left-radius: 34px;
  border-bottom-left-radius: 4px;
  border-top-right-radius: 0;
  border-bottom-right-radius: 0;
}


.g4 h2::before {
  content: "von ";
  color: #ddd;
  font-weight: normal;
  text-transform: lowercase;
}


li p.meta {
  color: black !important;
  background: #eaeaea;
  border-radius: 3px;
  font-size: 14px;
  padding-right: 9px;
  padding-left: 4px;
  cursor: default;
  font-weight: bold;
}

  

.update::before {
    content: "â†ªï¸Ž";
  text-transform: uppercases;
  color: lime;
  background: #444;
  padding: 1px 5px;
  border-radius: 5px;
  margin-left: -48px;
  display: inline;
  font-size: 14px;
  font-weight: bold;
  font-style:italic;
}

.update {
  background-image: none !important;
}
  







span.icon-audio {
  background-image: none !important;
  cursor: wait;
}

span.icon-audio::after {
    content: "narando";
  text-transform: uppercase;
  color: orange;
  background: #444;
  padding: 2px 2px 1px 3px;
  border-radius: 3px;
  margin-left: -3px;
  letter-spacing: 1px;
}


span.icon-audio::before {
    content: "HÃ¶ren mit";
  margin-left: -5px;
  padding-left: 4px;
}




div.social-bar, span.icon-video {
  display: none;
}

#backhome {
  display: none;
}

figure#gasideimg {
  display: none;
}

a {
  padding: 5px 8px;
  marginleft: -3px !important;
  background: rgba(15,60,160,0.06) !important;
  color: rgba(15,60,140,1);
  border-radius: 3px;
  text-decoration: none;
}

a:hover {
  background: rgba(15,60,160,0.8) !important;
  color: white !important;
  box-shadow: 0px 4px 45px 10px rgba(85,85,85,0.25);
  text-decoration: none;
  cursor: e-resize;
}

a:visited {
  background: #999 !important;
  color: grey !important;
  box-shadow: 0px 0px 0px 0px rgba(255,255,255,0.01);
  cursor: w-resize !important;
}

a:visited:hover {
  background: #grey !important;
  color: white !important;
  box-shadow: 0px 0px 0px 0px rgba(255,255,255,0.01);
}


article a.golem-external-url {
  padding: 1px 3px !important;
  margin: 0px -1px !important;
  border: 1px solid rgba(0,33,255,0.8);
  background: none !important;
  color: inherit;
}

article a.golem-external-url:hover {
  background: none !important;
  cursor: ne-resize;
  box-shadow: 0px 4px 60px 15px rgba(85,85,150,0.4);
  color: inherit !important;
}



a.golem-url-specials { 
  background: rgba(0,0,0,0) !important;
  color: grey;
  border: 1px  solid transparent;
  border-bottom: 1px  dashed silver;
  margin: 0 -4px !important;
  cursor: n-resize;
}

a.golem-url-specials:hover {
  background: rgba(0,0,0,0) !important;
  color: grey !important;
  border: 1px  dashed silver;
  box-shadow: 0px 4px 90px 20px rgba(85,85,85,0.2)
}

#gservices ul li, #gservices ul li form input {
  color: silver !important;
}

#gservices ul li:hover, #gservices ul li form input:hover {
  color: white !important;
}
}
