css:
@-moz-document domain("lightning.force.com") {
/***** Account/Contact/Deal detail page *****/

.listItemBody {
	padding: 2px 6px !important;  
/*     padding: 0px !important;     */
}

.mainBody { /* Parent */
    margin: auto;
}

.mainBody .labelCol { /* Child 1 */
    width: 33%;
    float: left;
}

.mainBody .itemLabel {
    font-weight: bold;
    white-space: normal !important;
}

.mainBody .itemBody { /* Child 2 */
    margin-left: 25%;
/*     background: rgba(0,0,255,0.2); */
    padding-left: 10px;
}

/* Header of a Deal */
ul.slds-grid.slds-page-header__detail-row {
	margin-top: 4px;
    background-color: transparent;
    padding-top: 2px;
    padding-bottom: 2px;
}
li.slds-page-header__detail-block {
    max-width: none !important;
}
li.slds-page-header__detail-block p {
	display: inline-flex;
}
p.slds-text-heading--label-normal.slds-m-bottom--xx-small.slds-truncate {
    font-weight: bold;
    margin-right: 10px;
}


/***** Header *****/

.slds-global-header__logo {
    width: 4rem;
}

/***** Dropdown menu *****/

li.uiMenuItemSeparator {
	background-color: rgba(244, 246, 249,0.5);
    font-weight: bold;
}


/***** Modal *****/

.modal-container.slds-modal__container {
    padding: 20px;
}

.forceDuelingLists .listContainer {
    width: 240px;
}
    
.forceDuelingLists .listContainer .duelingSelect {
    height: 600px;
    max-height: 70vh;
}


/***** Right part of Account/Contact/Deal detail page *****/

@media (min-width: 64em) {
    .region-sidebar-right .slds-card__header, .slds-card__footer, .region-sidebar-right .slds-card__body--inner {
        padding-left: 0.75rem;
        padding-right: 0.75rem;
    }
}

.region-sidebar-right .forceStyle .forceRecordLayout .full.forcePageBlockItemEdit, .region-sidebar-right .forceStyle .forceRecordLayout .mdp.forcePageBlockItemEdit  {
	padding: 0;   
}


/***** List view *****/

.forceListViewManagerPrimaryDisplayManager td.cellContainer { 
    padding: 0px 0px 0px 8px;
    letter-spacing: -0.4px;
}

}
