css:
@-moz-document domain("app.tutanota.com") {
/*Main background:  #2e3440
  Highlight:        #343c4a
  Font:             #c7c6ce
*/

body {
    color: #c7c6ce;
    background-color: #343c4a;
}
header#main.loggedIn {
    background-color: #2e3440;
}
div.menuList {
    background-color: #2e3440;
}
div.buttonBar {
    background-color: #2e3440;
}
body div#searchAndMailListColumn>div#searchAndMailList>section.list {
    background-color: #2e3440;
}
body div#searchAndMailListColumn>div#searchAndMailList section#mailList>div#innerMailList {
    background-color: #2e3440;
}
body div#passwordChannelColumn>div#innerPasswordChannelColumn {
    background-color: #2e3440;
}
body div#searchAndMailListColumn>div#searchAndMailList section#mailList>div#innerMailList div.mailInList.selected {
    background-color: #343c4a;
}
header#main>div#right-navbar>menu>ul#menu_list>li.menu_link.active,
header#main>div#right-navbar>menu>ul#menu_list>li.menu_link.selected,
header#main>div#right-navbar>menu>ul#menu_list>li.menu_link:active {
    background-color: #343c4a;
}
.menuItem.active,
.menuItem.dragOver,
.menuItem.selected,
.menuItem:active {
    background-color: #343c4a;
}
.infoMessage {
    background-color: rgba(248, 248, 248, 0)
}
body section.mail div.mailBodyQuotation.mailBody,
body section.mail>div.mailBody.mailBody {
    color: black;
    background-color: white;
}
body div#conversationColumn>div#innerConversationColumn>section.conversation {
    background-color: #343c4a;
}
body section.mail>header div.bubble.displayRecipient {
    background: rgba(0, 0, 0, 0.25);
}
div.buttonBar>button {
    color: #c7c6ce;
}
.menuItem.fontImage {
    color: #c7c6ce;
}
body>div#tutanota>div#viewContainer>div.content {
    background-color: #2e3440;
}
body div#searchAndContactListColumn>div#searchAndContactList section#contactList>div#innerContactList>div.contactInList>div.contactNameAndMailAddressInList {
    background-color: #343c4a;
}
.inputField {
    color: #c7c6ce;
    background-color: #343c4a;
}
.recordInfo,
label.recordStatus {
    color: #c7c6ce;
}
body div.dataTable>div>input {
    background-color: #343c4a;
    color: #c7c6ce;
}
body div.dataTable>div>select {
    color: #c7c6ce;
    background-color: #343c4a;
}
body div#settingsContent div#changeSettingsColumn>div#changeSettingsInnerColumn>div#changeSettingsData>div>div#adminUserList>div.userList {
    background-color: #343c4a;
}
body div#settingsContent div#changeSettingsColumn>div#changeSettingsInnerColumn>div#changeSettingsData>div>div#adminMessages div#externalWelcomeMessagePreview {
    color: #c7c6ce;
    background-color: #343c4a;
}
body>div#tutanota a,
body>div#tutanota a:ACTIVE {
    color: #c7c6ce;
}
.inputArea {
    color: #c7c6ce;
    background-color: #343c4a;
}
header#main>div#logo {
    mix-blend-mode: luminosity;
}
body>div#tutanota>div#viewContainer {
    background-color: #343c4a;
}
body>div#tutanota a,
body>div#tutanota a:ACTIVE {
    color: -webkit-link;
}
div#modalMoreMenu>div>#menu_more div.inner_menu_more {
    border-top: 5px solid #343c4a;
    border-bottom: 5px solid #343c4a;
    background-color: #f8f8f8;
}
div#modalMoreMenu>div>div#triangle {
    border-bottom: 10px solid #343c4a;
}
div#modalMoreMenu>div>#menu_more div.menuList {
    background-color: #343c4a;
}
.modalDialog>div.dialogWindow {
    background-color: #2e3440;
}
body section.mail div.mailBodyQuotation.mailBody,
body section.mail>div.mailBody.mailBody {
    padding-top: 10px;
    padding-bottom: 10px;
    padding-left: 10px;
    padding-right: 10px;
}
body section.mail>header>div.headerTable>div.headerLine>.value>input.composeSubjectInput {
    background: rgba(0, 0, 0, 0.25);
    color: #c7c6ce;
}
div.bubbleInput>div.input {
    background: rgba(0, 0, 0, 0.25);
}
div.bubbleInput>div.input>div.inputContainer>div.bubbleInputDiv>input {
    background-color: transparent;
}
body section.mail>header div.bubble.secureRecipient {
    background: rgba(0, 255, 0, 0.18);
}
body section.mail>div.composeBody {
    background-color: white;
    color: black;
    padding-top: 10px;
    padding-bottom: 10px;
    padding-left: 10px;
    padding-right: 10px;
}
body div#settingsContent div#changeSettingsColumn>div#changeSettingsInnerColumn>div#changeSettingsData>div>div#adminUserList>div.userList {
    background-color: #2e3440;
}
body div#settingsContent div#changeSettingsColumn>div#changeSettingsInnerColumn>div#changeSettingsData>div>div#adminUserList>div.userList>div.user {
    background-color: #2e3440;
}
body div#settingsContent div#changeSettingsColumn>div#changeSettingsInnerColumn>div#changeSettingsData>div>div#adminUserList>div.userList>div.user.selected {
    background-color: #343c4a;
}
div.notificationBar>div.notificationMessage {
    color: #c7c6ce;
    background-color: #2e3440;
}
}
