css:
@-moz-document domain("app.simplenote.com") {
/* Interface Font */

.app {
    font-family: /*[[IFont]]*/;
}
/* Edit Mode */

div.note textarea {
    font-family: /*[[TFont]]*/!important;
    color: #666!important;
}
/* Preview Mode */

#static_content {
    font-family: /*[[PFont]]*/!important;
}

#static_content h1,
#static_content h2,
#static_content h3,
#static_content h4,
#static_content h5,
#static_content h5 {
    font-family: /*[[HFont]]*/!important;
    padding-top: 0.5em!important;
}

#static_content h1 {
    font-size: 2.2em!important;
}

#static_content h2 {
    font-size: 1.7em!important;
}

#static_content p {
    text-justify: none;
}

#static_content code {
    font-family: /*[[CFont]]*/!important;
    white-space: pre-wrap;
    text-align: left!important;
    background-color: #f5f5f5;
    border-radius: 5px;
    padding: 0px 7px 0px 7px;
    font-size: 0.8em;
}

#static_content p > code:only-child {
    display: block;
    padding: 5px 10px 5px 10px;
}

#static_content ul {
    list-style-type: disc!important;
}

#static_content li {
    list-style-type: disc!important;
    line-height:1.5!important;
    padding-bottom: 0.25em!important;
}

#static_content ul ul {
    list-style-type: circle!important;
}

/* Importing Web Font*/

@font-face {
    font-family: 'Alike';
    font-style: normal;
    font-weight: 400;
    src: local('Alike'), local('Alike-Regular'), url(https://fonts.gstatic.com/s/alike/v8/ucX0vp16K9rM-IkH3z-oWA.woff2) format('woff2')
}
}
/*[[FontSrc]]*/
