css:
@-moz-document domain("etymonline.com") {
/* Based on original code from http://www.etymonline.com/style.css*/
/*All rights reserved their respective holders*/
html, body { width: 100%; }
body {
   background-color: #100002!important;
   color: #fff!important;
   font-family: Georgia, Garamond, Times New Roman, Times, serif;
   font-size: 12pt;
   margin: 0 auto;
   max-width: 1200px;
}
a:link { color: #800020; text-decoration: none; font-weight: bold; }
a:visited { color: #800020; text-decoration: none; font-weight: bold; }
a:hover { color: #800020; text-decoration: none; font-weight: bold; }
a:active { color: #800020; text-decoration: none; font-weight: bold; }
p { margin-top: 0; }

button.link {
   background: transparent;
   border: 0;
   color: #800020!important;
   cursor: pointer;
   font-family: Georgia, Garamond, Times New Roman, Times, serif;
   font-size: 12pt;
   font-weight: bold;
   margin: 0;
   padding: 0;
   text-decoration: none;
}

input { padding: 0.25em;}

.crossreference { font-style: italic; }
.foreign { font-style: italic; }
.sponsors-term { font-weight: bold; font-style: italic; }

a.dictionary, a.edit {
   font-style: italic;
   font-size: xx-small;
   margin-left: 1em;
}
a.dictionary img {
   border: 0;
   vertical-align: middle;
}

#container {
   background-color: #200005!important;
   border: 1px solid #000!important;
   margin: 1em;
   padding: 1em;
}
#navigation, #center_message {
   background-color: #000000!important;
   font-size: 1em;
   color: white!important;
   font-weight: bold;
   margin-bottom: 1em;
   padding: 1em;
   text-align: center;
   border: 1px solid #000!important;
}
#center_message p:last-child {
   margin-bottom: 0;
}

/*For Firefox*/
@-moz-document domain("etymonline.com") {
img[src="http://etymonline.com/graphics/header.jpg"] {
   height:0px!important;
   width:0px!important;
   border:0px!important;
   display:inline-block!important;
   visibility:hidden!important;
}

#logo:before {
   content: url(http://i.imgur.com/HZHo8fH.png)!important;
   margin-bottom: 0.5em;
}
}

/*For Chrome & Opera*/

#logo { -webkit-margin-bottom: 0.5em; display:block; }
#logo img { -webkit-border: 0; content:url(http://i.imgur.com/HZHo8fH.png)!important;}

#browse, .paging {
   width: 100%;
   text-align: center;
   margin: 0.5em auto;
}
#browse ul, .paging ul {
   list-style: none;
   display: inline;
   padding: 0 0.25em 0;
   border: 1px solid #000!important;
   font-family: "Trebuchet MS", Verdana, Arial, sans-serif;
}
#browse li, .paging li { display: inline; }
#browse li a, .paging li a {
   padding: 0 0.25em;
   border-width: 0 1px;
   border-style: solid;
   border-color: #000!important;
}

#browse li a:hover,
.paging li a:hover,
.paging li a.current {
   background-color: #000!important;
   border-color: #000!important;
}

#dictionary {
   background-color: #000!important;
   padding-top: 0.5em;
   border: 1px solid #000!important;
   margin-bottom: 1em;
   padding: 0.5em;
   color: #FFF!important;
}
#dictionary p { padding: 0; margin: 0; }
#dictionary dd, #dictionary dt {
   background-color: #000!important;
   margin-left: 0;
   padding: 0 0.5em 0;
}
#dictionary dt { padding-top: 0.5em; }
#dictionary dd { padding-bottom: 0.5em; }
#dictionary .highlight { background-color: #000!important; }

#google-adsense { margin: 1em; text-align: center; }

#footer {
   border: 1px solid #000!important;
   background-color: #000!important;
   color: #FFF!important;
}
#footer ul {
   list-style: none;
   padding: 0;
   margin: 0.5em;
}
#footer li {
   padding: 0;
   margin: 0;
}
#footer #links   { float: left;  text-align: left;  }
#footer #credits { float: right; text-align: right; }


#sponsor-link {
   text-align: center;
   font-weight: bold;
   margin: 1.5em 0;
}
#sponsor-link p { margin: 0; }
#sponsor-link #paypal-button { float: right; }

#login-form {
   text-align: center;
   margin: 2em auto;
   border: 1px solid #000!important;
}

.form-row .form-label {
   text-align: right;
}
.form-row .form-element {
   text-align: left;
} 

#addterm, #search_and_replace { text-align: right; }


#main-content-edit label { display: block; }
#main-content-edit textarea { width: 98%; }
#main-content-edit input, #main-content-edit textarea { background-color: #fffbec!important; border: 2px solid #000!important;}


/* ----( Utility Styles )--------- */
.blank {
   clear: both;
   line-height: 0;
   height: 0;
   margin: 0;
   padding: 0;
   visibility: hidden;
}


/* ---- HACKS ---- */
/* Hides from IE5-mac \*/
* html #container {height: 1%;}
* html #footer {height: 1%;}
* html #dictionary dl {height: 1%;}
/* End hide from IE5-mac */

#ad-icon {
   margin-left: -390px;
}

#site-banner {
  color:red;
  font-family: sans-serif;
  font-size: 2em;
  font-weight: bold;
}

#alert_message {
   background-color: #800020!important;
   color: #ffffff!important;
   font-family: "Trebuchet MS", Verdana, Arial, sans-serif;
   font-size: 1.25em;
   margin: 0 auto 1em;
   padding: 1em;
   width: 700px;
   line-height: 1.4em;
}
#alert_message p:last-child {
  margin-bottom: 0;
}
}
