css:
@-moz-document url-prefix("https://friends.nico/web"),url-prefix("https://pawoo.net/web"),url-prefix("https://mstdn.jp/web") {

.column {
    flex-grow: 1;
    flex-shrink: 1;
}
body {
    font-size:0!important;
    line-height:0!important;
}
.drawer {
    display: none;
}
.status__content {
    display: none;
} 
.status__relative-time {
    display: none;
} 
.status__display-name {
    display: none!important;
} 
.icon-button {
    display: none!important;
} 
.column-icon{
    display: none!important;
}
.status {
    margin:0;
    padding:0;
    min-height:0!important;
    border:none !important;
}
.status:nth-of-type(1) {
    display: none!important;
} 
.status:nth-of-type(2) {
    display: none!important;
} 
div.status div div:nth-of-type(4) {
    height:0!important;
}
div.status>div {
    margin:0!important;
    padding:0!important;
} 
.status__prepend {
    display: none!important;
} 
.column:nth-of-type(3) {
    display: none!important;
} 
.media-spoiler span:nth-of-type(2) {
    display: none!important;
} 

}
