css:
@-moz-document regexp("^https?://gamedev.stackexchange.com/questions.*"), regexp("^https?://gamedev.stackexchange.com/tags.*"), regexp("^https?://gamedev.stackexchange.com/users(/|\\?.*)?"), regexp("^https?://gamedev.stackexchange.com/unanswered.*"), regexp("^https?://gamedev.stackexchange.com(/|/\\?.*|/#)?"), regexp("^https?://gamedev.stackexchange.com/search.*"), regexp("^https?://gamedev.stackexchange.com/posts/[\\d]*/edit(/)?"), regexp("^https?://gamedev.stackexchange.com/edit-tag-wiki/.*"), regexp("^https?://gamedev.stackexchange.com/posts/\\d+/revisions(/)?") {
/* background color */
body, #content {
	background: #1b1b1b !important;
}

/* normal panel */
.question-summary, .question>table, .answer, .wmd-preview, .tag-cell, #user-browser .user-info, #revisions .post-text {
	border: none !important;
	background: #2a2a2a !important;
}
.question-summary, .tag-cell, #user-browser .user-info, .excerpt, .original-question, .revision-comment {
	color: #bbb !important;
}
.post-text, .wmd-preview {
	color: #ccc !important;
}
#user-browser .user-details, .item-multiplier-count {
	color: #bbb !important;
}
.question-summary .started-link, .question-summary .started-link:visited, .statscontainer>.views:not(.warm):not(.supernova):not(.hot), .relativetime-clean, .user-action-time, .community-wiki, .badgecount {
	color: #999 !important;
}
.reputation-score  {
	color: #aaa;
}
.tag-cell {
  border-bottom: 4px solid #1b1b1b !important;
}
.tag-cell:not(:last-child), #user-browser .user-info {
	border-right: 4px solid #1b1b1b !important;
}

/* emphasized panel */
.bottom-notice, .new-post-activity, .module.newuser, .answer-help-background, .question-status, .mdhelp, .community-bulletin, .welovestackoverflow, #tag-menu, #hero-content, .edit-tag-wiki #excerpt, .preferred-login, .new-login-right>.form-item, .module.system-alert, #revisions .skip {
  border: 1px solid #888 !important;
}
.bottom-notice, .new-post-activity, .module.newuser, .answer-help-background, .question-status, .mdhelp, .mdhelp-tabs, .welovestackoverflow, #tag-menu, #hero-content, .edit-tag-wiki #excerpt, .preferred-login, .new-login-right>.form-item, .module.system-alert, #revisions .skip {
	background: #3c3c3c !important;
}
.bottom-notice, .new-post-activity, .module.newuser, .answer-help-background, .mdhelp-tab, .question-status, .mdhelp .module.newuser>h4, #sidebar .module.newuser>h4, .answer-help, .welovestackoverflow, #tag-menu, #hero-content, #hero-content #hiw>li, .edit-tag-wiki #excerpt, .preferred-login, .new-login-right>.form-item, .module.system-alert, #revisions .skip {
	color: #ddd !important;
}
.new-post-activity:hover {
	background: #666 !important;
}
.preferred-login, .new-login-right>.form-item {
  box-shadow: none !important;		
}

/* blockquote */
blockquote { background: #444; }

/* community bulletin */
.community-bulletin {
	background: #121212 !important;
}
.bulletin-title {
	color: #ccc !important;
}

/* no panel */
.subheader {
	border-bottom: 1px solid #bbb !important;
}
.subheader:first-child>*, .page-description, #question-suggestions>label, .advanced-tips>table>tbody>tr>td:nth-child(2), .advanced-tips>div, .ask-title-cell-key, .ask-page .form-item>table>tbody>tr>td:first-child, .form-item>label, .post-editor>h2, #filter-form>label, #synonyms-table>thead>tr>th, .tags-page #questions>h2, .fl>h2, .results-header>*, .new-post-login h3 {
	color: #ccc !important;
}
#question-header>h1>a { color: #eee !important; }
#question-header { padding: .67em; }
#question-header>h1 { margin: 0; }
.answers-subheader>h2, #post-form h2.space, .post-editor>h2, .content-inside>h2, .edit-tag-wiki #mainbar>h1, .edit-tag-wiki .input-section>h3 {
	color: #5ECC5E !important;
}
#sidebar>.module>h4, #ignored-wrapper>h4 { /* #sidebar h4 */
	color: #bbb !important;
}
#qinfo>tbody>tr>td:first-child>p, .content-inside>table>tbody>tr>td:nth-child(3), .tags-page .content-inside>p:last-child, .tags-page #mainbar>#questions>p:last-of-type, .form-submit>.privacy-policy-agreement {
	color: #888 !important;
}
#sidebar>.module, #qinfo>tbody>tr>td:last-child>p>b, .advanced-tips>table>tbody>tr>td:first-child, .advanced-note, #question-suggestions .answer-link>span, .rename-count, #synonyms-table>tbody>tr>td, .content-inside>table>tbody>tr>td:nth-child(-n+2) {
	color: #aaa !important;
}

/* normal link */
a, .started>a:nth-child(2):visited, .user-details a:visited, .comment-user:visited, .suggest-edit-post:visited, .user-action-time>a:visited, .tag-synonym-link:visited, #synonyms-table .creator>a, .synonyms-table .creator>.mod-flair, #users-legend>b>a:visited, .tags-page .post-menu a, .cancel-edit:visited, .question-status>h2 a:visited {
  color: #69B7B7 !important;
}
/* sidebar link */
#sidebar a, #interesting-tags>.ar>a:visited, #feed-link-text>a:visited, #hot-network-questions>h4>a:visited {
	color: #479A9A !important;
}
/* normal link hover */
a:hover, a:visited, .stats-row, .question-summary .started-link:hover, .started>a:nth-child(2):hover, .user-action-time>a:hover, .user-details>a:hover, .tag-synonym-link:hover, #synonyms-table .creator>a:hover, #users-legend>b>a:hover, .tags-page .post-menu a:hover, #sidebar a:hover, #sidebar a:visited, #interesting-tags>.ar>a:hover, #hot-network-questions>h4>a:hover, .question-status>h2 a:hover {
	color: #91D2D2 !important;
  text-decoration: none !important;
}
.question-status .mod-flair, .ownwer .user-details>.mod-flair {
	color: #69B7B7 !important;
}
/* button link */
#feed-link-text>a:hover, #addInterestingTag:hover, #edit-interesting:hover, #addIgnoredTag:hover, .advanced-tips-toggle:hover, .discard-answer:hover, .discard-question:hover, .cancel-edit:hover, .comments-link:hover, .show-more.js-show-more:hover, .relativetime-clean:hover, .short-link:hover {
	color: #328A8A !important;
}

/* tags */
.post-tag, .post-tag:visited, #sidebar .post-tag, #sidebar .post-tag:visited {
	background: #444 !important;
  color: #bbb !important;
}
.post-tag:hover, #sidebar .post-tag:hover, .welovestackoverflow .post-tag:hover, .bottom-notice .post-tag:hover {
	background: #aaa !important;
  color: #222 !important;
}
.welovestackoverflow .post-tag, .bottom-notice .post-tag {
	text-decoration: none !important;
	border: 1px solid #777 !important;
}

/* badges */
.badge, #sidebar .badge {
	background-color: #444 !important;
	color: #bbb !important;
}

/* listing */
.summary>h3>a, .summary>.result-link>span>a, .tags-page #mainbar-full .post-tag, .tags-page #questions h2 .post-tag, #user-browser .user-details>a, #user-browser .user-details>.mod-flair, .question-mini-list>.answer-summary .answer-link a {
	color: #5ECC5E !important;
}
.summary>h3>a:hover, .summary>h3>a:visited, .summary>.result-link>span>a:hover, .summary>.result-link>span>a:visited, #user-browser .user-details>a:hover, .question-mini-list>.answer-summary .answer-link a:hover, .question-mini-list>.answer-summary .answer-link a:visited {
	color: #A1E8A1 !important;
}

/* page numbers */
.pager>a>.page-numbers, .pager-answers>a>.page-numbers {
	color: #5ECC5E !important;
}
.pager>.page-numbers.current, .pager>a>.page-numbers:hover, .pager-answers>.page-numbers.current, .pager-answers>a>.page-numbers:hover {
	color: #000 !important;
  background: #5ECC5E !important;
	border: 1px solid #5ECC5E !important;
}
/* page sizers */
.page-sizer>a {
	color: #479A9A !important;
}
.page-sizer>.page-numbers.current, .page-sizer>a:hover {
	color: #000 !important;
  background: #479A9A !important;
  border: 1px solid #479A9A !important;
}
/* tabs */
.subheader #tabs>a, .mdhelp-tabs>li, .subtabs>a, .diff-choices>a {
	color: #ddd !important;
}
.subheader #tabs>a:hover, .subheader #tabs>a.youarehere, .mdhelp-tabs>li:not(:last-child):hover, .mdhelp-tabs>.selected, .subtabs>a:hover, .subtabs>a.youarehere, .diff-choices>a:hover, .diff-choices>a.selected {
	background-color: #217D7D !important;
  color: #eee !important;
}

/* comments */
.comments {
	color: #ccc !important;
}
.comments, .comment * {
	border-color: #888 !important;
}
.new_comment {
	background: none !important;
}

/* post stats */
.votes, .votes>.mini-counts>span, .unanswered, .unanswered>.mini-counts>span, .vote-count-post>strong, .answer-votes, .vote-count-post, .favoritecount>b {
	color: #bbb !important;
}
.answered, .answered>.mini-counts, .answered>strong, .answered-accepted, .answered-accepted>.mini-counts {
	color: #A9D74E !important;
}
.answer-votes {
	background: #353535 !important;
}
div.answered-accepted {
	background: #505e35 !important
}
.votecell>.vote>a {
	filter: invert(80%) !important;
}
.vote-accepted-on {
	opacity: .80 !important;
}
.question-page .vote {
	border-left: 5px solid #5ECC5E !important;
}

/* post owner */
.post-signature.owner, .comment-user.owner {
	background: #344A34 !important;
}

/* navs */
.nav.mainnavs a {
	background: #666 !important;
	color: #eee !important;  
}
.nav.mainnavs a:hover {
	background: #8c8c8c !important;
	color: #fff !important;
}
#nav-askquestion {
	background: #93C334 !important;
	color: #fff !important;
}
#nav-askquestion:hover {
	background: #fff !important;
	color: #7ba424 !important;
}

/* inputs */
.container input:not([type="submit"]):not([type="button"]), .tag-editor, #revisions-list {
	border: 1px solid #5ECC5E !important;
}
.wmd-input, .wmd-button-bar, .mdhelp, .grippie, textarea {
	border: 1px solid #2da72d !important;
}
input:not([type="submit"]):not([type="button"]), .tag-editor, #revisions-list, textarea {
	background: #333 !important;
	box-shadow: inset 0 1px 2px #555, 0 0 0 #666 !important;
	color: white !important;
}
div.form-item>div.tag-editor>input[type="text"] {
  border: none !important;
  box-shadow: none !important;
}
button, input[type="submit"], input[type="button"] {
	border: none !important;
  box-shadow: none !important;
  color: #fff !important;
}
button:hover, input[type="submit"]:hover, input[type="button"]:hover {
	background: #eee !important;
}
button, input[type="submit"] {
	background: #93C334 !important;
}
button:hover, input[type="submit"]:hover {
  color: #93C334 !important;
}
.search-page .col2>input, .copySnippet.btn-secondary, .snippet-ctas>button:first-child, input[type="button"] {
		background: #43B843 !important;
}
.search-page .col2>input:hover, .copySnippet.btn-secondary:hover, .snippet-ctas>button:first-child:hover {
		color: #43B843 !important;
}

/* post editor */
.wmd-button-bar {
	border-bottom: 1px solid !important;
}
.wmd-button.wmd-help-button {
	background: none !important;
}
.wmd-button:nth-child(-n+13) {
	filter: invert(100%) !important;
}

.wmd-input {
	background: #111 !important;
  border-top: none !important;
  border-bottom: none !important;
  color: #ddd !important;
}
.grippie {
	width: 726px !important;
	background-color: #444 !important;
}
.wmd-preview {
  padding-left: .67em !important;
  padding-right: .67em !important;
}

.discard-answer:hover, .discard-question:hover, .cancel-edit:hover {
	background: none !important;
}

/* code & syntax highlighting (thanks to abhishekchakraborty. taken from his StackExchange Dark theme.) */
pre:not(.CodeMirror-line), code, .pln, .youarehere {
 background: #111 !important;
 color: #ccc !important;
}

.str { color: #0a0 !important; }
.lit { color: #d65 !important; }
.pun { color: #999 !important; } /* parenthesis */
.kwd { color: #08f !important; } /* keyword */
.tag { color: #999 !important; } /* html tag */
.atn { color: #088 !important; } /* attribute */
.atv { color: #0a0 !important; } /* attribute value*/
.dec { color: #90a !important; } /* doc type */
.spaces { background: #111 !important; }

/* ads */
.everyonelovesstackoverflow {
	opacity: .75 !important;
}

/* formatting */
.question-summary {
	margin-bottom: 4px !important;
}
.question-summary>.cp {
	margin: 0px 5px 0px 5px !important;
}

/* hacky post form formatting*/
.question>table, .answer, .wmd-button-bar, .wmd-input, .wmd-preview {
	width: 728px !important;
}
.answer-help-background {
	width: 726px !important;
  height: 229px !important;
	padding: 0 !important;
  left: 0;
}
#post-form>div:nth-child(4)>div:last-child {
	width: calc(728px - 1.33em) !important;
	padding: .67em;
}
.mdhelp {
	width: 726px !important;
	right:	-13px !important;
}

/* question page formatting */
.votecell {
	padding-right: 6px !important;
}
.postcell, .answercell {
	padding-top: .67em !important;
}
table.fw {
	margin-right: .67em !important;
  width: calc(671px - .67em) !important;
}
.answer {
	padding: 0 !important;
  margin-bottom: 8px !important;
}
.question>table>tbody>tr:last-child>td:last-child, .answer>table>tbody>tr:last-child>td:last-child {
	padding-bottom: .67em !important;
}
#answers>.everyonelovesstackoverflow {
	margin: 0 0 4px 0 !important;
}
.question-status {
	padding-left: 8px !important;
  margin-left: 52px !important;
  margin-right: .67em !important;
}
.pager-answers {
	padding: 0 0 20px 0 !important;
}
.comments-link {
	opacity: 100 !important;
}

/* tags page formatting */
.tag-cell, #user-browser .user-info {
  padding: .67em !important;
}
#tags-legend {
	margin-top: .67em !important;
}

/* users page */
#user-browser .user-info {
	margin: 0 0 4px 0 !important;
}

/* search page */
.result-highlight, .search-highlight { color: #eee !important; background: none !important; }
.search-page .summary {
	padding-right: 1em !important;
}

/* ask question page */
.ask-title-cell-value>#title, .ask-mainbar #title {
	width: 666px !important;
}
.ask-mainbar, .ask-title-table, .tag-editor, .ask-mainbar .form-item>table {
	width: 728px !important;
}
#question-only-section {
	padding: 0 !important;
}
.ask-sidebar, .ask-sidebar>#scroller {
	width: calc(365px - 68px) !important;
}

/* edit post page */
#edit-comment {
	width: 708px !important;
}

/* tag synonyms page */
.pending {
	color: #888 !important;
}

/* tag menu */
.tm-favorite {
	color: #dd1 !important;
}
.tm-favorite:hover, .tm-favorite-ignore {
	color: #c21 !important;
}

/* herobox */
#herobox-mini {
	border: none !important;
  background: none !important;
}

/* revisions page */
#revisions td, #revisions .user-action-time, #revisions td>.revision-comment {
  color: #ccc !important;
}
.vote-revision>td, .revision>td {
	background: #444 !important;
}
.owner-revision>td {
	background: #4A554A !important;
}
.revcell5 {
	margin-left: 0 !important;
}
.revcell5>.diff-choices, .revcell5>.diffs>.tags-diff {
	margin-left: 10px !important;
}
#revisions .post-text {
	padding: .67em !important;
  margin-left: 0 !important;
}
span.diff-delete { /* just reverse the background/foreground colors */
	color: #E5BDB2 !important;
  background: #A82400 !important;
}
span.diff-add {
	color: #D1E1AD !important;
	background: #405A04 !important;
}
.markdown-diff .deleted>div {
	background: #552222 !important;
}
.markdown-diff .inserted>div {
	background: #225522 !important;
}
.diff-choices>a {
	border: none !important;
}

/* header */
#header {
	background: none !important;
  height: 80px !important;
}
#header>.cbt {
	display: none !important;
}
#hmenus {
	top: 30px !important;
}
.icon-help.js-help-button { color: #ccc !important; }
}
