css:
@-moz-document domain(nijiura.xyz), domain(ftb.moe), domain(mstdn.jp),  domain(pawoo.net), domain(friends.nico) {
/* 
マストドンをふたば風にするスタイルシート です
*/
.drawer__inner,
.ui {
    background-color: #FFFFEE
}
.scrollable ,
.drawer__inner.darker,
.search-results__section *,
.tabs-bar__link:hover,
.drawer__header>a:hover,
.drawer__header>a:hover *,
.empty-column-indicator{
    background-color:  #F0E0D6 !important;
    color: #800000 !important;
}
.scrollable *:not(.fa) {
    background-color: #F0E0D6;
    color: #800000;
}
/* コメントとコメントの間は罫線ではなく隙間を開ける */
.scrollable .status, .scrollable .account { 
    border-bottom: 5px solid #FFFFEE !important;
}
/* 引用は緑色 */
.scrollable .muted *:not(.fa) {
    color: #789922;
}

.status__prepend + .status  *:not(.fa) {
    color: #789922;
}

/*
.scrollable .muted strong {
    color: #800000 !important;
}
*/

/* ブーストのボタンだけなぜか色が変えられないので他のボタンもブーストの色に合わせる  */
.scrollable .icon-button, 
.scrollable .muted .icon-button,
.status__prepend + .status .icon-button {
    color: #606984;
    opacity: 0.5;
}

.compose-form__buttons {
    background-color: #F0E0D6;
}
.compose-form__buttons + div {
    color: #800000;
}

.column-link {
    background-color: #F0E0D6;
    color: #800000;
}
.column-link:hover {
    background-color: #800000;
    color: #FFFFEE;
}

.privacy-dropdown.active .privacy-dropdown__value {
    box-shadow: none;
    background: #F0E0D6;
}
.icon-button.overlayed {
    border-radius: 0px;
}

.account__header div *:not(.account__header__avatar),
.account__header span,
.account__header__content,
.account__header .fa,
.muted a.status__content__spoiler-link,
.status__content__spoiler-link:hover >span,
.drawer__tab > .fa,
.drawer__tab:hover > .fa  {
    background: rgba(0, 0, 0, 0) !important;
}
.account__header>div {
    background: rgba(255, 255, 255, 0.7)!important;
}
.account__header *{
    color:#800000 !important;
}

.account__header a {
    text-decoration: none;
}

.columns-area .column-settings--outer {
    background-color: #FFFFEE;
}
.columns-area .column-icon-clear {
    margin-right: 5px;
}
.columns-area .column-icon.collapsable:hover {
    background-color: #800000;
}
.reply-indicator {
    background-color: #EEEEEE;
}
.compose-form__buttons {
    box-shadow: none;
}

.status__content a,
.status__content a>span,
span.ellipsis,
.active,
.search__icon * {
    color: #2B90D9 !important;
}

.compose-form__buttons .active{
    background-color:  #282c37;
}

.button{
    background-color: #2B90D9;
    opacity: 1;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: clip;
}
.button:hover{
    background-color: #2B90D9;
    opacity: 0.8;
}
.search *,
.compose-form__buttons .active {
    border-radius: 0.4em;
}
.search__input,
.search__input:focus {
    background-color: white;
}
.status__content__spoiler-link *:hover {
    color: #EE0000;
}

/* ヘッダは色をつけない */
.column-header,
.column-icon,
.tabs-bar,
.drawer__header,
.drawer__header *,
.column-back-button,
.search-results__header {
    background-color: #FFFFEE;
    color: #800000;
}

/* 縦長画面にした時に出てくるタブバー */
.tabs-bar__link.active{
    background-color: #F0E0D6;
    border-color: #2B90D9 ;
}
.tabs-bar .fa-fw {
    color: #800000;
}
.tabs-bar span {
    color: #800000;
}

/*
.column-header:focus {
    box-shadow: 0 0 10px red;
}
*/

/* 名前 */
.status__display-name .display-name strong,
.detailed-status .display-name strong ,
.navigation-bar strong { 
    color: #CC1105;
    font-weight: 900;
    text-decoration: none;
}

.status__display-name .display-name span,
.detailed-status .display-name span,
.navigation-bar span {
    opacity: 0.7;
}
.navigation-bar span {
    color: #789922;
}

.getting-started,.getting-started * {
    background-color: #FFFFEE !important; 
}
}
