/* Fond de page - background of website */

index > .connected {
	background-color: #FFFFFF !important;
	background-image: url("http://savepic.net/9427555.png") !important;
	background-repeat: no-repeat !important;
	background-position: center center !important;
	background-attachment: fixed !important;
	background-size: cover !important;
}
/* Bannière - header */
/* logo AS */

#site-logo > a > img[src*="/logo-as.png"] {
	height: 0 !important;
	width: 0 !important;
	background: url("http://savepic.ru/14175794.png") no-repeat !important;
	padding-left: 427px !important;
	padding-top: 142px !important;
}
.halo {
	background-image: url("http://orig10.deviantart.net/2145/f/2017/061/e/1/250980sanshalo_by_mvpsora-db101fg.png") !important;
}
/*infos joueur - player infos */

panel-connected {
	background-image: url("http://orig03.deviantart.net/de0d/f/2017/061/2/d/menu2_by_mvpsora-db0zufk.png") !important;
}
#pseudo-container > .blue {
	background: #ff0072 !important;
	box-shadow: 0 1px 1px rgba(125, 125, 125, 0.75) !important;
}
.title-container a {
	color: #000000 !important;
	text-shadow: 0 1px 1px rgba(125, 125, 125, 0.75) !important;
}
#episode-container {
	background: #a30000 !important;
}
#episode-container span {
	color: #FFFFFF !important;
}
#episode-container .number {
	color: #FFFFFF !important;
}
.trombone {
	background-image: url("http://www.amoursucre.com/image/paper-clip/left.png") !important;
}
#currencies-container .values {
	background: #fc6c8b !important;
	border: 2px solid #FFFFFF !important;
	color: #FFFFFF !important;
}
#panel-menu a {
	background: #ff0072 !important;
	color: #000000 !important;
}
/* Infos survolées : allez-y, personnalisez-les ^__^
        #panel-menu a:hover {
