        }
*/
/* Menu de navigation - nav */

navigation-menu > div > .pink,
navigation-menu > div > .blue {
	background: #a30000 !important;
	color: #FFFFFF !important;
}
/* Menu survolé : allez-y, personnalisez-le ^__^
   
    navigation-menu > div > .active, navigation-menu > div > a:hover {
        background : #ff0072 !important ;
        color : #ff0072 !important ;
    }
*/

navigation-menu > div > .border {
	border: none !important;
}
/* Menu latéral - nav on left */

side-links > .link-entry {
	background: #a30000 !important;
}
body,
a,
a:hover {
	cursor: url(http://orig12.deviantart.net/b804/f/2017/061/9/9/cursor_by_mvpsora_dat8b20_by_mvpsora-db0yww9.png), auto;
}
