@-moz-document domain("friends.nico") {
  body{
    background: #fff;
    color: #000;
  }
  
  .drawer__header, .drawer__inner, .search__input:focus, .collapsable-collapsed, .column-settings--outer, .column > .scrollable, .detailed-status, .detailed-status__action-bar{
    background: #fff;
  }
  
  .autosuggest-textarea__textarea, .column-icon, .column-back-button{
    background: #efefef;
  }
  
  .navigation-bar strong, .getting-started p, .account__action-bar__tab strong, .status__display-name strong, .account__display-name strong, .notification__message, .detailed-status__display-name strong, .information-board .section strong{
    color: #313543;
  }
  
  .search__input{
    background: #fff;
    color: #313543;
  }
  
  .column-header, .column-link{
    background: #efefef;
    color: #313543;
  }
  
  .getting-started{
    background-color: #fff;
  }
}
