css:
@-moz-document domain("madfanboy.com") {


html, body { 
    background: #c3cfdd /* Обои и цвет */                                         
    } 

.alt_block li.header {
    /* background: #343840 !important; */
}

body {
	font-family: "Roboto", Arial, Helvetica, sans-serif;
	font-size: 12px;
    color: #000000;
}
.navbar ul.linklist {
    padding: 0% 2%;
    list-style-type: none;
    margin: -14px 15px 0 0;
}

.body-layout-Fluid #inner-wrap {
    width: 98%;
    margin: 0 auto;
}

    .site-description {
    text-align: center;
    position: relative;
    z-index: 2;
    display: inline-block;
    margin: 0% 0% 0%;                                        
    }

    #wrap {
    width: 100%;   /*Ширина Доски*/
    margin: 0 auto;
    }

    .pagination {  /*Перемещаем нумерацию страниц в поиске на левую сторону*/
    float: left;
    text-align: right;
    width: auto;
    }

    ul.forums, ul.topics {  /*убираем рамки в списке форумов*/
    padding: 0px 1px 0px;                                      
    }

   .navbar_in_header {  /*Верхняя панель ссылок*/
    background: rgb(87, 128, 173);
    background: linear-gradient(to bottom, rgb(53, 92, 135) 0%, rgb(31, 65, 103) 100%);
    /*height: 46px;*/
    border-bottom: 1px solid #1b3759;
    bottom: 10px;
    top: 100%;
    z-index: 5;                           
   }

    #contentcolumn {  /*Восстанавливаем размер доски*/
    margin: 0 0 0 0;
    padding: 0 0;
    }


    table.table1 tbody td { /*Убираем белые полосы в благодарностях*/
    padding: 10px;
    border-top: 0px solid transparent;
    }


    #rightcolumn { /*Убираем правую панель феди*/
    display: none;                                        
    float: left;
    width: 300px;
    margin-left: -300px;
    }

    .headerbar {  /* Убираем баннер лого*/
    /*opacity: 0;*/
   } 

    div.rules {  /*Убираем красный блок с правилами*/
    display:none;                                        
    font-size: 0;
    }

   .action-bar {
    margin: 0 0 0px;
   }

   .username-coloured {
        color: #ffffff;
   }

    .postprofile_Left .postprofile {  /*Постпрофиль*/
    /*box-shadow: 0px 0px 5px 2px rgba(0,0,0,0.75); */
    border-width: 0 0px 0 0;
    }

    blockquote {  /*Цитаты*/
    background: transparent none 6px 8px no-repeat;
    margin: 30px 1px 30px 25px;
    overflow: hidden;
    padding: 15px;
    /* border-left: 5px solid; */
    outline: 0.5px solid #d2d1d1;
    border-left: 0px solid
    /*box-shadow: 0px 0px 1px 1px rgba(0,0,0,0.75); */         
    }

	.post { /*Посты*/
    padding: 15px;
    background-repeat: no-repeat;
    background-position: 100% 0;
    margin-bottom: 0px;
    border-bottom: 0px solid;
    position: relative;
    background: linear-gradient(to bottom, rgba(255,255,255,1) 0%, rgba(247,247,247,1) 0%, rgba(249,249,249,0) 9%, rgba(255,255,255,0) 47%);
    }

    .post-buttons .button, .format-buttons .button {
    border: 0px;
    background-color: rgba(210, 209, 209, 0);                                    
    }

    .action-bar .pagination { /*Панель с нумерацией страниц*/
    float: right;
    margin: 3px 0;
    margin-right: 1%; /*Отступ слева*/
    }
    
   p {
                                  
    margin-left: 1%;
    }                          

    a.specialbutton {
        background: #5780ad;
    }

    a.topictitle {
    font-size: 11pt;
    font-weight: 500;
                                            
    }


    ul#nav-breadcrumbs { /*Панель "Список форумов" и т.д"*/
    margin-bottom: 5px;
    margin-left: 1%;   /*Отступ слева*/                                     
    }

	h2 {
	font-weight: normal;
    font-size: 2em;
    margin-bottom: 10px;
    margin-left: 1%;
    }  

	a {
    color: #204d77;    
    font-weight: normal;
    }	
	
	p.author, p.author a {
    color: #83888b;
    }
	
	#inner-wrap /*Основноя доска*/
	{
    box-shadow: -2px 1px 10px 0px rgba(0,0,0,0.75); /* Тень от доски*/                                    
    padding: 0px; /*Ширина*/
    /*border-radius: 10px;*/    /*Закругление углов*/   
    margin-top: 1.6% !important;
    border: solid white;                                        
  }
	.forabg, .forumbg, .viewtopic_wrapper 
	{
    background-color: #C6D2E0;
  }
	.viewtopic_wrapper /* Рамки тредов */
	{
    padding: 0px; /*  Ширина боковых рамок */
    background-color: #3b4559;  /*Цвет боковых рамок*/ 
  }
                                            
	.bg2, .bg1 
	{
    background-color: #e0e8ec;
  }
  .postbody .content 
	{
    font-size: 1.2em;
		/*font-weight: 400;*/
		color: #212121;
  }
	.postprofile
	{
    padding: 0;
		width: 10%;
		border: 1px solid;                                  
  }
	.postbody 
	{
		padding: 0px;
		width: 87%;
  }
	.profile-rank img
	{

		max-width: 172px;
	}
	
	li.row 
	{
    padding: 8px;
    margin-bottom: 0px !important;
    /*background: linear-gradient(to bottom, rgba(255,255,255,1) 0%, rgba(247,247,247,1) 0%, rgba(249,249,249,0) 2%, rgba(255,255,255,0) 47%);*/ 
    border-top: solid 1px #F4F9FB;                                        
  }
	
	.responsive-hide .username-coloured 
	{
		margin: 0px 0px 0px 0.5em;
	}
	
	.navbar a
	{
    color: #636669;
  }
	.action-bar.bar-bottom 
	{
    margin-top: 30px;
  }
  .specialbutton, input.specialbutton, .panel .specialbutton, a.specialbutton, .scrollToTop    
	{
    background: #5780ad;
  }
  ul.topiclist dt {
    font-size: 1.1em;
    font-weight: 500;
  }
	a.forumtitle {
		color: #000000;
  }
	
	.panel  /*Рамки всех панелей*/
  {                     
    background-color: #e0e8ec; 
    border: 0px solid; /*Ширина*/                    
  }
	.message-box 
	{
    width: 99%;
  }
	.smiley-box
	{
		width: 100%;
		float: none;
	}	
	.fields1
	{
		display: flex;
		flex-flow: column;
	}
	div#format-buttons{ 
    	order:1;
    }
	div#message-box{ 
    	order:2;
    }
	div#smiley-box{ 
    	order:9;
  }
	
	a.button1, input.button1, input.button3, a.button2, input.button2 {
    color: #FFFFFF;
    background-color: #5780ad;
    border-color: #D9D9D9;
  }
	
  .navbar_footer a, .copyright_bar a, .icon.fa-file.icon-red, .navigation .active-subsection a, .tabs .activetab > a, a.postlink, a.postlink:visited, .navbar_in_header .badge, .button:focus .icon {
    color: #054488;
  }
	a:hover, .social_links_footer a:hover span, a:hover .icon.fa-file.icon-red, .navigation .active-subsection a:hover, .navigation a:hover, .tabs .tab > a:hover, .tabs .activetab > a:hover, .button:hover .icon, a.postlink:hover {
    color: #054488;
  }
	
	.postbody h3 {
    font-size: 1.9em;
  }
	
	 .headerbar, body.content_block_header_block li.header, body.content_block_header_stripe li.header::before, .no_avatar, .social_links_footer, .badge, thead tr, .sidebar_block_stripe::before, .fancy_panel::before 
	{
    background: linear-gradient(to bottom, rgb(53, 92, 135) 0%, rgb(31, 65, 103) 100%);
    border-bottom: 1px solid #000000;                                        
    } 


	.particles_container
	{
		background-image: url(http://i.imgur.com/CQmGULR.png); 
		background-size: 300px 117px;
		background-position: left 45%;
        /*height: 150px; */                                   
    background-repeat: no-repeat;
    background: linear-gradient(to right, rgb(255, 255, 255) 0%, rgb(230, 239, 246) 41%, rgb(52, 90, 132) 100%);
    text-align: left;
	}
	.cp-main .pm
	{
		border: 0px solid;
        background-color: transparent;                                   
	}
	.topicreview .bg1, .topicreview .bg2 {
		border: 0px solid;
		background-color: #e6eff6;
  }
	.panel-container .postbody p.author
	{
		color: #55585a;
  }
	.navbar_footer a, .copyright_bar a
	{
		color: #0489b9;
	}
	
	blockquote
	{	
    background-color: #f4f6f9;
  }
	blockquote blockquote 
	{
    background-color: #f4f6f9;
  }
	blockquote blockquote blockquote 
	{
    background-color: #f4f6f9;
  }
	blockquote blockquote blockquote blockquote
	{
    background-color: #f4f6f9;
  }	
	blockquote blockquote blockquote blockquote blockquote 
	{
    background-color: #f4f6f9;
  }
	blockquote blockquote blockquote blockquote blockquote blockquote
	{
    background-color: #f4f6f9;
  }
	blockquote blockquote blockquote blockquote blockquote blockquote blockquote
	{
    background-color: #f4f6f9;
  }
	blockquote blockquote blockquote blockquote blockquote blockquote blockquote blockquote
	{
    background-color: #f4f6f9;
  }
}
