css:
@-moz-document
domain("addyourdomain.here"),
domain("mastodon.social") {

.fa-bars, .fa-asterisk, .fa-users, .fa-globe, .fa-cog, .fa-sign-out, .column-header__button { color: white}
.column-icon, .column-header__button, .column-header__back-button, .column-back-button { background-color:transparent !important; color: white !important; }

.drawer__header { background-color: orange; }
.column-header, .column-header__button { background-color:cadetblue }

div.column:nth-child(2) h1 {background-color: dodgerblue }
div.column:nth-child(3) h1 {background-color: tomato }
div.column:nth-child(4) h1 {background-color: olive }
div.column:nth-child(5) h1 {background-color: #7D3A78}
div.column:nth-child(6) h1 {background-color: red }
div.column:nth-child(7) h1 {background-color: saddlebrown }

}
