css:
@-moz-document domain(proxer.me) {
/*INSPIRED BY Proxer Material "https://userstyles.org/styles/135692/proxer-material" */
/*Icons made by madebyoliver from http://www.flaticon.com/authors/madebyoliver */

@font-face {
    font-family: 'Roboto';
    font-style: normal;
    font-weight: 300;
    src: local('Roboto Light'), local('Roboto-Light'), url(https://fonts.gstatic.com/s/roboto/v15/0eC6fl06luXEYWpBSJvXCBJtnKITppOI_IvcXXDNrsc.woff2) format('woff2');
}



body {
    font-family: 'Rubik', 'Roboto', sans-serif !important;
    background-color: #4e4e4e;
    color: rgba(0, 0, 0, 0.80);
}

a {
    color: #0e0075;
    transition: all 0.5s;
    text-decoration: none;
}

a:hover {
    color: #750002;
    transition: all 0.3s;
    text-decoration: underline;
}

.profile font {
    background: transparent !important;
}

.inner {
    padding: 0 5px 10px 5px;
}




/*Placeholders ###################################################*/




::-webkit-input-placeholder { /* WebKit, Blink, Edge */
    color: rgb(175, 175, 175);
}

:-moz-placeholder { /* Mozilla Firefox 4 to 18 */
    color: rgb(175, 175, 175);
    opacity: 1;
}

::-moz-placeholder { /* Mozilla Firefox 19+ */
    color: rgb(175, 175, 175);
    opacity: 1;
}

:-ms-input-placeholder { /* Internet Explorer 10-11 */
    color: rgb(175, 175, 175);
}

input:focus::-webkit-input-placeholder { /* WebKit, Blink, Edge */
    color: transparent;
    transition: 0.4s;
}

input:focus:-moz-placeholder { /* FF 4-18 */
    color: transparent;
}


input:focus::-moz-placeholder {  /* FF 19+ */
    color: transparent;
}



input:focus:-ms-input-placeholder { /* IE 10+ */
    color: transparent;
}




/*Inputs ###################################################*/




#login-form>input[type="submit"] {
    border: 0px solid #fafafa;
    background: rgba(0, 0, 0, 0);
    color: #fafafa;
    padding: 5px;
}

#login-form>input[type="submit"]:hover {
    border-radius: 2px;
    transition: 0.4s;
    background: rgba(0, 0, 0, 0.2);
}

input[type="submit"] {
    border: 0px solid #fafafa;
    background: rgba(0, 0, 0, 0.2);
    color: #343434;
    padding: 5px;
}

input[type="submit"]:hover {
    border-radius: 2px;
    transition: 0.4s;
    background: rgba(0, 0, 0, 0.4);
}

input[type="text"]#name {
    background-color: #fafafa;
    border: 1px solid #000000;
    color: #fafafa;
    padding: 3px;
    margin-right: 10px;
    margin: 0 10px 5px 10px;
}

#mod_login_remember {
    margin: 0px 5px 0px -4px !important;
    vertical-align: middle;
}

form > .mod_login {
    color: #ffffff;
    margin: 0 0 0 15px;
}

#mod_logout_submit {
    color: #ffffff;
}




/*Main View Area ########################################################*/



hr {
    border: 1px solid rgba(0, 0, 0, .54) !important;
}


#main {
    padding: 0 0 10px 0;
    background-color: #f5f5f5;
    border: 0px solid rgb(40, 40, 55);
    border-radius: 3px !important;
    box-shadow: 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12), 0 2px 4px -1px rgba(0, 0, 0, .2);
}

.calendarHeadNav table {
    background: transparent;
    border: 0px solid #000000;
    padding: 0px;
}

/*Picture Anime View Proxer*/
.infocell {
    margin: 5px 0px 10px 2%;
    box-shadow: 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12), 0 2px 4px -1px rgba(0, 0, 0, .2) !important;
    background-color: #fafafa;
    border-color: #777777;
}

/*Table Anime View Proxer*/
.fa-list {
    height: 15px;
    margin: 5px;
}

.fa-th-large {
    height: 15px;
    margin:0px;
}

#box-table-a {
    text-align: left;
}

#box-table-a tr {
    height: 30px;
    border: 1px solid #777777 !important;
}

#box-table-a th {
    background: #777777;
    color: #FFF;
}

#box-table-a td {
    border-bottom: 1px solid #777777;
    color: rgba(0, 0, 0, .54);
    background: #f5f5f5;
}

#box-table-a tr:hover td {
    color: #777;
    background: #f5f5f5;
}

#box-table-a tr:hover a {
    color: #000;
}

img.tip {
    box-shadow: 0 4px 5px 0 rgba(0, 0, 0, 0), 0 1px 10px 0 rgba(0, 0, 0, 0), 0 2px 4px -1px rgba(0, 0, 0, 0) !important;
    height: 30px;
}

/*  Start!!! Airing/Finished Anime Image*/
[title~=Abgeschlossen] {
    content:url("http://cdn.proxer.me/f/LRRkTw26");
}

[title~=Airing] {
    content:url("http://cdn.proxer.me/f/QTh2x67s");
}

[title~=Nicht] {
    content:url("http://cdn.proxer.me/f/CJqlvcZO");
}/*  Stop Airing/Finished Anime Image*/

/*START !!! Changes /calendar/month */
.calendar table .calendarEntryList {
    background: #fafafa !important;
    
}

.listEntry {
    background: #fafafa !important;
    
}

.dayTable td {
    padding: 5px 2px 15px 2px;
    border-bottom: 1px solid black;
}

.listEntry .animeLink {
    color: #0e0075;
    transition: all 0.5s;
    text-decoration: none;
}

.listEntry .animeLink:hover {
    color: #750002;
    transition: all 0.3s;
    text-decoration: underline;
}

.calendarEntryList th {
    color: #ffffff;
    text-align: center;
    background: #0e0075;
    border-radius: 0 0 0 0;
    padding: 2px;
}

.calendarMainTable {
    border-collapse: collapse;
}

.calendarMainTable>thead {
    margin: 5px !important;
}

.calendarEntryList {
    border-width: 2px;
}

.calendar th {
    height: 25px;
}

/*STOP !!! Changes /calendar/month */



/*START   changes applied to gallery?s=detail&id=   */
.commentScroll {
    width: 18% !important;
}

textarea {
    width:100% !important;
    padding: 2px;
}

.commentScroll .viewport .overview {
    padding-right: 10px;
}
/*STOP   changes applied to gallery?s=detail&id=   */



/*##################### Start Page ####################################*/





.proxerSliderImage, .proxerSliderImageMini {
    border: 1px solid rgba(0, 0, 0, .54) !important;
}



/*Main Nav ##############################################################*/




#nav {
    background-color: #777777;
    box-shadow: 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12), 0 2px 4px -1px rgba(0, 0, 0, .2);
    height:40px;
    border: 0 !important;
}

#nav ul {
    padding: 0 0 1px 0 !important;
}

#nav a {
    color: #fafafa;
    line-height: 40px;
}

#nav hr {
    border: 1px solid rgba(0, 0, 0, 0.40) !important;
}

.topmenu ul {
    background: #f5f5f5;
    box-shadow: 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12), 0 2px 4px -1px rgba(0, 0, 0, .2);
    background-repeat: repeat-x;
    border: 0px solid #fafafa;
    border-radius: 0px !important;
    border-top: 0;
    margin: 0px 0 0 5px !important;
}
.topmenu ul li {
    color: #fafafa !important;
}

.topmenu>ul>li>a {
    color: rgba(0, 0, 0, 0.80) !important;
}




/*##################### Main Nav Components ##################################*/



.searchBox {
    width: 256px !important;
    background: #fafafa !important;
}

.fastSearchEntry {
    background: #fafafa;
    color: rgb(175, 175, 175);
}

.mod_login.textfield {
    background-color: rgba(0, 0, 0, 0)!important;
    border: 0px solid #fafafa;
    border-bottom: 2px solid rgb(175, 175, 175);
    color: #fafafa;
    padding: 3px;
    margin-right: 10px;
}

#globalsearch_section {
    background: #fafafa;
    border: 0px solid;
    margin-right: 10px;
    color: rgba(0, 0, 0, .54);
    width: 150px;
    font-size: 20px !important;
    height: 33px;
}

.submitSearch {
    border: 0px solid #fafafa;
    background: rgba(0, 0, 0, 0);
    color: #fafafa !important;
    padding: 5px 5px 5px 5px;
}

.submitSearch:hover {
    border-radius: 2px;
    transition: 0.4s;
    background: rgba(0, 0, 0, 0.2);
}

#proxerSearch0 {
    height: 27px;
    background-color: #fefefe;
    border: 0px solid #fafafa;
    padding: 3px;
    margin-right: 4px;
}

#proxerSearch0:focus {
    outline: none;
    transition: 0.4s;
    border-bottom: 2px solid #fafafa;
}

#loginBubble>ul>li>a {
    color: #fafafa !important;
    text-align: left;
    padding: 0 0 0 5px;
}

#notificationBubble, #loginBubble, .customBubble, .pointerBubble {
    box-shadow: 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12), 0 2px 4px -1px rgba(0, 0, 0, .2) !important;
    background: repeat scroll 0 0 #777777;
    border-radius: 0px !important;
    text-shadow: 0px 0px 0 #000;
    border: 0px solid #fafafa;
    color: #fafafa !important;
    margin: 10px 0 0 0;
    
}
#notificationBubble { 
    font-size: 14px !important;
}

#notificationBubble a, #loginBubble a,
.customBubble a,
.pointerBubble a {
    color: #fafafa !important;
    line-height: 25px;
    margin: 0 0 5px 0;
    
}

#notificationBubble:after, #loginBubble:after,
.pointerBubble:after {
    border-color: #777777 transparent;
    top:-10px;
}




/*##################### Main Nav Icons ##################################*/




#loginNav.notLoggedIn {
    background-image: url(http://proxer.me/templates/proxer14/images/key.png);
}

#newsNav {
    background-image: url(http://proxer.me/templates/proxer14/images/notification_0.png);
}

#miscNav {
    background-image: url(http://proxer.me/templates/proxer14/images/notification_4.png);
}

#messageNav {
    background-image: url(http://proxer.me/templates/proxer14/images/notification_2.png);
}

#requestNav {
    background-image: url(http://proxer.me/templates/proxer14/images/notification_1.png);
}

#searchNav {
    background-image: url(http://proxer.me/templates/proxer14/images/notification_5.png);
}




/*#########################  Down Nav ########################*/


/* Changes on /anime/animeseries/rating/all/1#top */
a.menu {
    border-radius: 0px !important;
    border-top: 1px solid #777777;
    border-bottom: 1px solid #777777;
    border-left: 0px solid #777777;
    border-right: 0px solid #777777;
    color: #777777;
    padding: 6px 11px 6px 11px;
    margin-left: -2px !important;
    margin-right: -2px !important;
}

.fa-list {
    padding: 7px 11px 6px 11px !important;
    height: 16px;
    margin: 0px 0px 0px 0px;
}

.fa-th-large {
    padding: 7px 11px 6px 11px !important;
    height: 16px;
    margin: 0px 0px 0px 0px;
}

a.menu.active, a.menu:hover {
    transition: all 0.7s;
    background-color: #0e0075;
    color: #fff!important;
}

ul#simple-navi-wiki li a, ul#simple-navi li.active a, ul#simple-navi li a:hover, ul#simple-navi li a, ul#simple-navi-wiki li.active a, ul#simple-navi-wiki li a:hover {
    padding: 6px 10px !important;
    transition: all 0.8s;
    color: #ffffff;
    letter-spacing: 1px;
    margin-right: 0px;
    background-color: #FFF;
    background: #777777;
    border-radius: 0px !important;
}

ul#simple-navi li.active a {
    border-bottom: 5px solid #4e4e4e;
}

ul#simple-navi, ul#simple-navi-wiki {
    background-color: #777777;
    font-weight: bold;
    border-radius: 0px !important;
}

#proxerSearch1 {
    margin-top: 8px !important;
    background-color: #f5f5f5;
    border: 0px solid #fafafa;
    color: #000000;
    padding: 4px;
    margin-right: 0px;
}

#proxerSearch1:focus {
    outline: none;
    transition: 0.4s;
    border-bottom: 2px solid #fafafa;
}



/*#########################  Additionals ########################*/

/*Correcting the Navigation menu on /wiki/* */
.mediawiki {
    background: #fafafa;
}

#chat {
    border: 0px solid rgb(40, 40, 55) !important;
    background-color: #fafafa;
    border-radius: 0px !important;
    box-shadow: 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12), 0 2px 4px -1px rgba(0, 0, 0, .2);
}

.messagebuttons {
    width: 93%;
    margin: 6px 0;
}

.message_item {
    clear: both;
    min-height: 50px;
    padding: 5px;
    border-bottom: 1px solid;
    margin: 3px 0 3px 0;
}

.countNav {
    display: inline;
    background-color: #CF0000;
    color: #FFFFFF;
    font-size: 10px;
    left: 20px;
    max-width: 20px;
    padding: 2px 4px 2px 4px;
    position: relative;
    top: 8px;
}

.scrollable.default-skin .scroll-bar .thumb {
    background-color: #263238;
}

/* displays other Proxer themes*/
.colorbox {
    display: none;
}


    
/*#########################  Forum ########################*/
    

    
#Kunena #ktop {
    background: #777777;
}

#Kunena #ktop #ktopmenu #ktab li a, #Kunena #ktab div.moduletable ul.menu li {
    float: left; 
    padding: 8px 10px 0px 10px;
    border: 0;
    background: #777777 !important;
    line-height: 40px;
    margin: 0 5px 0 5px !important;
    color: #FFF;
    text-shadow: rgba(0, 0, 0, 0.5) 0px -1px 1px;
}

#Kunena #ktop #ktopmenu #ktab li {
    height: 44px;
}

#Kunena #ktop #ktopmenu #ktab #current a{
    border-bottom: 4px solid #4e4e4e;
}

#Kunena #ktab ul {
     background-color: #777777;
    font-weight: bold;
    border-radius: 0px !important;
}



/*From Material Design Unsorted ###################################################*/



#contentList td img {
    box-shadow: 0 4px 5px 0 rgba(0, 0, 0, 0), 0 1px 10px 0 rgba(0, 0, 0, 0), 0 2px 4px -1px rgba(0, 0, 0, 0);
}

#ui-tooltip {
    border: 0px solid #fafafa !important;
    background: #fafafa !important;
    box-shadow: 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12), 0 2px 4px -1px rgba(0, 0, 0, .2) !important;
    border-radius: 0px !important;
}

.moduletable {
    background-color: #5E5E5E;
    border: 1px solid #777777;
    border-radius: 6px;
}

table.ptable > tbody > tr > td {
    background: #fafafa !important;
    color: rgba(0, 0, 0, .54) !important;
}

.borderLeft {
    border-left: 1px solid #777777 !important;
}

.details td {
    background-color: #fafafa;
}

.ptable input[type="text"] {
    background-color: #fafafa;
    border: 0px solid #fafafa;
    border-bottom: 2px solid rgb(175, 175, 175);
    color: rgba(0, 0, 0, .54);
    padding: 3px;
    margin-right: 10px;
}

.ptable input[type="text"]:focus {
    outline: none;
    transition: 0.4s;
    border-bottom: 2px solid #777777;
}
}
