css:
@-moz-document domain("mstdn.jp"),
domain("mastodon.cloud"),
domain("mastodon.social"),
domain("friends.nico"),
domain("ika.moe"),
domain("pawoo.net"),
domain("mstdn-workers.com"),
domain("mastodon.xyz"),
domain("mstdn.wiki"),
domain("mastodon.com.tw"), 
domain("mstdn-tw.com"), 
domain("emojidon.global"),
domain("sakaba.space"), 
domain("gunmastodon.com") {
.app-body {
  color: #fafefa;
  font-family: -apple-system, BlinkMacSystemFont, "Helvetica Neue", "Noto Sans Japanese", "ヒラギノ角ゴ ProN W3", Hiragino Kaku Gothic ProN, Arial, Meiryo, sans-serif;
}
.column-link {
  color: #fafefa;
}
.getting-started p {
  color: #669966;
}
.getting-started a {
  color: #4e6f4e;
}
.columns-area {
  background-color: #354a35;
  background-repeat: no-repeat;
  background-position: bottom left;
}
.column-header,
.drawer__header {
  height: 48;
}
.drawer a:hover {
  background-color: transparent;
}
.drawer__header {
  background-color: transparent;
}
.drawer__tab:hover {
  background-color: transparent;
  border-bottom: solid 2px #7fca7f;
  text-shadow: 0px 0px 10px #519c51;
}
.drawer__tab .fa-asterisk {
  color: #7fca7f;
}
.column-header .fa-asterisk {
  color: #669966;
}
.drawer__tab .fa-users {
  color: #7fca7f;
}
.column-link .fa-users {
  color: #669966;
}
.drawer__tab .fa-globe {
  color: #7fca7f;
}
.column-link .fa-globe {
  color: #669966;
}
.drawer__tab .fa-cog {
  color: #7fca7f;
}
.column-link .fa-cog {
  color: #669966;
}
.drawer__tab .fa-sign-out {
  color: #7fca7f;
}
.column-link .fa-sign-out {
  color: #669966;
}
.drawer__tab .fa-star,
.column-link .fa-star {
  color: #669966;
}
.column-link .fa-ban {
  color: #669966;
}
.column-link .fa-volume-off {
  color: #669966;
}
.column-link .fa-book {
  color: #669966;
}
.column-link .fa-image {
  color: #669966;
}
.icon-button.active:not(.star-icon) {
  color: #669966;
}
.icon-button .fa-user-times:hover {
  text-shadow: 0px 0px 10px #519c51;
}
.icon-button .fa-user-plus:hover {
  color: #707b97;
  text-shadow: 0px 0px 10px #707b97;
}
.search {
  height: 37px;
}
.search__input {
  border-radius: 5px;
  padding-right: 35px;
  padding-left: 20px;
}
.search__input::-webkit-input-placeholder {
  color: transparent;
}
.drawer__inner {
  border-radius: 5px 5px 0 0;
  background: transparent;
}
.autosuggest-textarea__textarea {
  color: #111;
  background-color: #FFF;
  resize: vertical;
}
.autosuggest-textarea__textarea::-webkit-input-placeholder {
  color: #030303;
}
.autosuggest-textarea__textarea:focus::-webkit-input-placeholder {
  color: transparent;
}
.spoiler-input__input:focus::-webkit-input-placeholder {
  color: transparent;
}
.drawer__inner > div {
  background: #282C37;
}
.navigation-bar {
  background: #282C37;
}
.compose-form {
  background: #282C37;
  border-radius: 0 0 5px 5px;
}
.announcements {
  background: #282C37;
  border-radius: 0 0 5px 5px;
}
.compose-form__modifiers {
  background-color: #FFF;
}
.privacy-dropdown__option.active {
  background: #669966;
}
.privacy-dropdown__option:hover,
.privacy-dropdown__option.active:hover {
  background: #669966;
}
.text-icon-button.active {
  color: #85a585;
}
.button {
  background-color: #537A53;
  border-radius: 4px;
  color: #fff;
}
.button:hover {
  background-color: #669966;
}
.column-header {
  color: #fefefe;
  border-radius: 5px 5px 0 0;
}
.column-header:hover>i,
.column-link:hover >i {
  text-shadow: 0px 0px 10px #519c51;
}
.column-header.active .fa,
.column-header .fa-home {
  color: #669966;
}
.column-header .fa-bell {
  color: #669966;
}
.column-header .fa-users {
  color: #669966;
}
.column-header .fa-star {
  color: #669966;
}
.column-header .fa-ban {
  color: #669966;
}
.column-header .fa-volume-off {
  color: #669966;
}
.column-header .fa-heart {
  color: #669966;
}
.collapsable-collapsed {
  border-radius: 5px 5px 0 0;
}
.column-back-button {
  color: #669966;
  border-radius: 5px 5px 0 0;
}
.column-back-button:hover>i {
  text-shadow: 0px 0px 10px #519c51;
}
.react-toggle--checked .react-toggle-track {
  background-color: #669966;
  border-color: #669966;
}
.react-toggle--checked .react-toggle-thumb {
  border-color: #669966;
}
.react-toggle--checked:hover:not(.react-toggle--disabled) .react-toggle-track {
  background-color: #537a53;
}
.notification__message .fa-user-plus {
  color: #669966;
}
.column-header.active .fa-users {
  color: #669966;
}
.column-header.active:hover>i {
  text-shadow: 0px 0px 10px #519c51;
}
.column-header .fa-globe {
  color: #669966;
}
.account__header>div {
  background: rgba(49, 53, 67, 0.65);
}
.account__header .account__header__username {
  color: #669966;
}
.tabs-bar__link.active {
  border-bottom: 2px solid #669966;
  color: #669966;
}
div.status .avatar,
div.account .avatar {
  opacity: 0.8;
}
div.status.muted .avatar {
  opacity: 0.6;
}
.emoji-dialog {
  background: rgba(33, 33, 33, 0.7);
}
.emoji-dialog.with-search {
  height: 420px;
}
.emojione {
  opacity: 0.6;
}
.emoji img {
  opacity: 1.0;
}
.loading-bar {
  background-color: #537A53;
}
:focus {
  outline: -webkit-focus-ring-color auto 0px;
  outline-width: 0px;
}
.status__content__spoiler-link {
  width: auto;
}
a.status__content__spoiler-link {
  display: block;
  color: #000;
  text-align: center;
  margin: 15px auto 0 auto;
}
.status__content .status__content__spoiler-link {
  background: #466f46;
}
.status__content .status__content__spoiler-link:hover {
  background: #669966;
}
.drawer {
  resize: horizontal;
}
.column {
  flex-grow: 1;
  flex-shrink: 1;
}
}
