css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("coop-land.ru") {
  body{
    background-image: url("https://i.snag.gy/8SUrYR.jpg")!important;
    background-attachment: fixed;
    background-repeat: no-repeat;
    background-size: 100%;
    margin-top: -12px;
  }
  .category{
    max-height: 40px!important;
    font-size: 14px;
  }
  .news .article .category {
    padding: 1px 1px;
  }
  .header{
    transform: scale(0.8)!important;
      z-index: 10;
      width: 1125px;
      margin: 0 auto;
  }
  .search-panel .bar{
    width: 350px;
  }
  .button-up {
    right: 96.6%;
  }
  .button-up.full.show {
    background-position: bottom 40px center;
  }
  .button-up.onscroll.show {
    background: url(http://coop-land.ru/templates/CoopLand2016/images/up.png) bottom center no-repeat;
  }
  .slider .main-img{
    width: 83%;
  }
  .news{
    height: 180px;
  }
  .news .article {
    padding: 10px 10px;
  }
  .news .article .title {
    color: #407938;
    padding-bottom: 10px;
    white-space: nowrap;
    overflow: hidden;
    max-width: 650px;
  }
  .news .article .inform-panel {
    float: right;
    max-width: 650px;
    position: absolute;
    margin-top: 145px;
  }
  .news .article .preview-text {
    max-width: 650px;
  }
  .news .article .soc-panel {
    padding: 1px 0;
    height: 15px;
  }
  .news .article .image {
    min-width: 200px;
  }
  .news .article .article-content {
    margin: 0 auto!important;
    width: 100%!important;
    min-height: 172px;
    margin-top: 0px!important;
  }
/* Аватар */
  #dle-comments-list .comment .user .user-avatar img {
    border-radius: 0px;
  }
  .user-avatar {display:table-footer-group;}
  .group {display:table-header-group;}
  .rating{margin-right: 15px;}
#dle-comments-list .comment .user .user-avatar::after {
    left: 0px;
    top: 0px;
    width: 79px;
    height: 79px;
    border-radius: 0px;
    border: 0px;
    background-color: transparent;
  }
#dle-comments-list .comment .user .user-avatar.online::after {
    background-color: transparent;
    border: 2px solid #4e9644;
  }
/* Теги */
  .full-story .tags {
    padding: 1px 1px 1px 1px;
  }
/* Мне нравится */
  .full-story .social {
    float: right;
    position: relative;
    padding: 1px 0;
    margin-top: -40px;
  }
/* Ошибка */
  .full-story .note {
    margin: 30px 0;
    padding: 1px 1px 1px 60px;
    min-height: 26px;
  }
  .title,
  .preview-text,
  .news {
    margin: 0 auto!important;
    width: 100%!important;
  }
  .wrapper {
    margin: 30px auto 0 auto;
  }
  .wrapper,
  .flex-stretch,
  .content {
    width: 900px;
  }
  .footer-back{
    height: 240px;
    width: 882px;
    margin: 0 auto;
  }
  .footer > .clr{
    width: 990px;
    transform: scale(0.9)!important;
    margin-top: -25px;
    margin-left: -45px;
  }
  .ui-effects-wrapper{
    height: 0px!important;
  }
  #helpus,
  #contentShifter,
  .vk,
  .share42init,
  .bottom-panel,
  .bottom-block,
  .game-rating,
  .sidebar,
  .horizontal-slider,
  .interesting,
  .chat,
  .relnews,
  .base > center:nth-child(24){
    display: none;
  }
}
