css:
@-moz-document domain("youtube.com") {

.ytp-ce-element,
.branding-img-container,
.branded-page-related-channels-list,
.branded-page-related-channels-title{   
	display:none!important;
}
  /* Плейслист фуллскрин */ 
.ytp-playlist-menu{
  background:none!important;
  margin-left:1160px!important;
  transition:none!important;
}
.ytp-playlist-menu-button{
    float:right!important;
    opacity:0!important;
  }
.ytp-playlist-menu-button:hover{
    float:right!important;
    opacity:1!important;
  }
.ytp-video-menu-item-title{
      text-shadow:
   -1px -1px 0 #000,  
    1px -1px 0 #000,
    -1px 1px 0 #000,
     1px 1px 0 #000;
  }
 
  /* Превью видео плейлиста */ 
.watched-badge{display: none!important;}
.video-thumb {opacity: 1!important;}

.ytp-chrome-controls{
  background-color: rgba(0,0,0,0.0)!important;
}
  /* Время */
.ytp-tooltip-text {
  background-color: transparent!important;
  padding: 0px!important;
  font-size: 130%!important;
  text-shadow:
   -1px -1px 0 #000,  
    1px -1px 0 #000,
    -1px 1px 0 #000,
     1px 1px 0 #000;
}
.ytp-time-separator,
.ytp-time-current,
.ytp-time-duration{
  font-size: 130%!important;
  text-shadow:
   -1px -1px 0 #000,  
    1px -1px 0 #000,
    -1px 1px 0 #000,
     1px 1px 0 #000;
}
.ytp-time-current{padding-left: 5px!important;}
.ytp-time-duration{padding-right: 5px!important;}
  
-badge {opacity: 0.2!important;}

  /* Убрать нижний отступ */
#content-container, #body-container {padding-bottom: 0px!important;}
  /* Убрать нижнюю часть */
#footer-container {display:none!important;}

  /* Уменьшить количество видео до "еще" */
#watch-related > .video-list-item:nth-child(n+8) {display:none !important}


/* Makes the likes bar green and red again */
    .video-extras-sparkbar-likes {background: #559900 !important;}
    .video-extras-sparkbar-dislikes {background: #cc0000 !important;}

  /* Makes the rest transparent to let the image through */
    .all-comments, .all-comments a {background: transparent !important;}
    #yt-comments-sb-standin .box {background: transparent !important;}
    .branded-page-related-channels, /* Related channels */
    #watch-header, /* Video title */
    #watch-action-panels, /* Video options */
    #action-panel-details, /* Video description */
    #watch7-sidebar-contents, /* Sidebar */
    #watch-discussion, /* Comments frame */
    #footer-container, /* Footer */
    .yJa, .Mza, .uPa, .DJa /* Google Plus Comments */
    {background: transparent !important;}

  /* Backgroung video in playlist */
    #player-playlist :not(.watch-queue-nav) .playlist-videos-list > li.currently-playing,
    #player-playlist :not(.watch-queue-nav) .playlist-videos-list > li.currently-playing .yt-ui-ellipsis {background-color: #0D558E!important;}

  /* Background loading bar */
    .ytp-hover-progress-light {background: #1F9BFC;}
  /* Background select bar */
    .playing-mode .ytp-progress-bar-container:not(.ytp-pull-ui) .ytp-load-progress {background: #0D558E!important;}
  /* Background load bar */
    .ytp-load-progress {background: #0D558E!important;}

  /* Menu button */
    #yt-masthead #logo-container .logo {margin-left: -130px!important;}
    #appbar-guide-button {margin-left: 80px!important;}

  /* Scrubber-button */
    .ytp-scrubber-button {
    width: 3px;
    margin-left: 3px;
    border-radius: 0px;
    background: #FFFFFF;
}
    .ytp-big-mode .ytp-scrubber-button {
    width: 3px;
    margin-left: 7px;
    border-radius: 0px;
    background: #FFFFFF;
}
  /* Subtitles */
.caption-window{
  margin-bottom: 4%!important;
}


.ytp-volume-slider-handle::before {
    left: -60px;
    background: red!important;
    border-right: 2px solid threedshadow !important;
    border-right-color: white !important;
}
.ytp-volume-slider-handle,
.ytp-volume-slider-handle::after{
    background: transparent!important;
}
    /* Volume not hide */
    .ytp-volume-panel {
      width: 4em!important; /* 58px */
}

    /* Hide top full screen */
    .ytp-title,
    .ytp-watch-later-button,
    .ytp-share-button,
    .ytp-gradient-bottom,
    .ytp-gradient-top {display: none!important;}

}
