@-moz-document domain("wikipedia.org"), domain("wikimedia.org"), domain("wikibooks.org") {
#www-wikipedia-org {
	background: #F5F5F5;
}
.central-textlogo__image {
	width: 280px;
	height: 50px;
	margin-top: 15vh;
	margin-bottom: 90px;
	background-size: 280px auto;
	background-position: 0 -405px;
	filter: drop-shadow(0 1px 2px rgba(50,50,50,0.25)) opacity(70%);
}
.central-textlogo strong, #www-wikipedia-org > hr, .central-featured-lang small, .central-featured-logo, .lang-list-button-wrapper, .lang-list-border, .lang-list-container {
	display: none;
}
.search-container {
	width: 640px;
	margin-top: -300px;
}
#search-input {
	width: calc(100% - 55px);
}
#www-wikipedia-org #searchInput {
	width: calc(100% + 55px);
	padding-left: 13px;
	border: 0;
	border-radius: 3px;
	box-shadow: 0 1px 3px 0 rgba(0,0,0,0.15);
	transition: 0.3s;
}
#www-wikipedia-org #searchInput:focus {
	box-shadow: 0 1px 5px 0 rgba(0,0,0,0.3);
}
#search-input .styled-select {
	right: 0;
}
.pure-button {
	border: 0 !important;
	margin-left: -15px !important;
	background: none !important;
	filter: invert(100%) opacity(60%);
}
.central-featured {
	width: 510px;
	height: 200px;
	margin-bottom: 20px;
}
.central-featured-lang {
	width: 90px;
	height: 35px;
	border-radius: 5px;
	border-bottom: 2px solid rgba(0,0,0,0.2);
	background: #78909C;
	transition: background 0.3s, box-shadow 0.2s;
}
.central-featured-lang:hover {
	background: #607D8B;
	box-shadow: 0 1px 4px 0 rgba(0,0,0,0.4);
}
.central-featured-lang a {
	height: 100%;
	font-size: 14px;
    padding-top: 7px !important;
	color: #FFF !important;
	outline: none !important;
}
.central-featured-lang strong {
	font-weight: normal !important;
	font-size: 15px;
	text-decoration: none !important;
}
.central-featured-lang.lang1 {
	top: 20px;
	left: 90px;
}
.central-featured-lang.lang2 {
	top: 20px;
	left: 205px;
}
.central-featured-lang.lang3 {
	top: 20px;
	left: 320px;
}
.central-featured-lang.lang4 {
	top: 75px;
	left: 35px;
}
.central-featured-lang.lang5 {
