}
.central-featured-lang.lang10 {
	top: 130px;
	left: 320px;
}
.site-license {
	display: none;
}
.footer {
	position: absolute;
	top: 100vh;
	background: #EEE;
}
.app-badges {
	background: none;
}
.app-badges .app-badge {
	position: relative;
	left: 15px;
}

/*Header*/
#mw-head-base {
    margin-left: 0;
    background: #03A9F4;
    box-shadow: 0 140px 0 110px #03A9F4;
}
#p-logo {
	position: absolute;
	top: -340px;
	left: 35px;
	display: block;
	width: 100px;
	height: 100px;
	background-image: url("https://drive.google.com/uc?id=0B2K6AHAKA5EXWVFIelE5M2pVNXM");
	background-position: 20px 35px;
	background-size: 75px auto;
	background-repeat: no-repeat;
	filter: opacity(0.9) drop-shadow(0 1px 2px rgba(50,50,50,0.25));
	transition: filter 0.2s;
}

#p-logo:hover {
	filter: opacity(0.9) drop-shadow(-1px 1px 0 rgba(255,255,255,0.3));
}
