div#simpleSearch {
	position: absolute;
	top: 10px;
	left: 35%;
	right: 30%;
	width: auto;
	max-width: none;
	height: 40px;
	border: 0;
	border-radius: 5px;
	margin-top: 15px;
	margin-left: -10px;
	background: rgba(0,0,0,0.1);
	transition: background 0.3s;
}
div#simpleSearch #searchInput {
	width: calc(100% + 22px);
	color: rgba(0,0,0,0.6);
	font-size: 16px;
	border-radius: 5px;
	padding: 11px 8px 11px 11px;
	transition: background 0.3s, box-shadow 0.2s, border-radius 0.2s;
}
div#simpleSearch #searchInput:focus {
	border-radius: 3px;
	background: #FFF;
	box-shadow: 0 1px 4px 0 rgba(0,0,0,0.2);
}
div#searchInput::-moz-placeholder {
	color: rgba(255,255,255,0.7);
	transition: color 0.25s;
}
div#simpleSearch #searchInput:focus::-moz-placeholder {
	color: rgba(0,0,0,0);
}
div#simpleSearch #searchInput::-webkit-input-placeholder {
	color: rgba(255,255,255,0.7);
	transition: color 0.25s;
}
div#simpleSearch #searchInput:focus::-webkit-input-placeholder {
	color: rgba(0,0,0,0);
}
div#simpleSearch #searchButton, div#simpleSearch #mw-searchButton {
	margin-right: 10px;
	filter: opacity(0.4) brightness(0);
}
#p-personal {
	top: 78px;
	right: 10px;
    height: 0;
	visibility: hidden;
	transition: background 0.3s;
}
#p-personal:before {
    content: '';
	visibility: visible;
	display: block;
	position: absolute;
	top: -50px;
	right: 10px;
	width: 40px;
	height: 40px;
	padding: 10px;
	background: url('https://drive.google.com/uc?id=0B2K6AHAKA5EXRXQ1Yy12RzZzc0k');
	background-position: 10px 0;
	background-size: 40px 40px;
	background-repeat: no-repeat;
	cursor: pointer;
	filter: drop-shadow(0 1px 2px rgba(0,0,0,0.15));
	transition: filter 0.3s, box-shadow 0.3s;
}
#p-personal:hover:before {
	filter: drop-shadow(0 1px 3px rgba(0,0,0,0.3));
}
#p-personal > * {
    pointer-events: none;
	opacity: 0;
	transition: opacity 0.5s;
}
#p-personal ul {
	padding: 0 !important;
}
#p-personal:hover {
	visibility: visible;
    width: 170px;
    height: auto;
	padding: 10px;
	border-radius: 3px;
	background: #FFF;
    box-shadow: 0 1px 8px rgba(0,0,0,0.25);
	opacity: 1;
}
#p-personal:hover > * {
    pointer-events: auto;
	opacity: 1;
}

