.notifier_baloon {
    width: 320px;
    color: #000;
    background-color: rgba(255, 255, 255, 0.1) !important;
    background-size: cover;
    font-size: 12.5px;
    backdrop-filter: blur(2px);
    overflow: hidden;
    border: 1px solid rgba(219, 219, 219, 0.5) !important;
    -webkit-box-shadow: 0px 1px 2px 0px rgba(50, 50, 50, 0.4);
    -moz-box-shadow: 0px 1px 2px 0px rgba(50, 50, 50, 0.4);
    box-shadow: 0px 1px 2px 0px rgba(50, 50, 50, 0.4);
}
.notifier_baloon_title {
    font-weight: 400;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    overflow: hidden;
    color: inherit;
    font-size: 12.5px;
}
.notifier_baloon_wrap a, 
.notifier_baloon_msg span.mem_link, 
.notifier_baloon_msg span.group_link {
    color: #4a76a8;
