css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("forumsdev.darklordpotter.net") {
	html, #header {
		background-color: black!important;
	}
	body {
		margin-top: 25px;
	}
	/* New Unread Icon */
	.node .forumNodeInfo .nodeIcon, .node .categoryForumNodeInfo .nodeIcon, .node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
		background-image: url('http://i.imgur.com/mN7HH04.png');
	}
	/* Coloring Backgrounds */
	#content .pageContent, .sectionMain, .primaryContent, .node .nodeLastPost, .profilePage .tabs.mainTabs li.active a, .tabs {
		background-color: #21301F;
	}
	.navTabs {
		background-color: rgb(33, 48, 31);
	}
	.navTabs .navTab.selected .tabLinks, .navTabs .navTab.selected .navLink, nodeList .categoryStrip, .tabs li a, .tabs.noLinks li, .footer .pageContent {
		background-color: #0A1C0B;
	}
	.secondaryContent, .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo, .profilePage .tabs.mainTabs, .sectionFooter {
		background-color: #2C4129;
	}
	.nodeList .categoryStrip {
		background-color: #0a1c0b;
		border-top: 1px solid #000;
		border-bottom: 1px solid #f9bc6d;
	}
	/* Coloring Fonts */
	body, .pairs dt, .pairsInline dt, .pairsRows dt, .pairsColumns dt, .pairsJustified dt, body .muted, body a.muted, body .muted a, .sidebar .section .secondaryContent .footnote, .navTabs .navTab.selected .navLink, .sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
		color: #BAD6C2;
	}
	.nodeList .categoryStrip {
		color: #809775;
	}
	a, .primaryContent a, .secondaryContent a, .sidebar .section .primaryContent h3 a, .sidebar .section .secondaryContent h3 a, .navTabs .navTab.selected .tabLinks a, .sectionFooter a, .subHeading {
		color: #93BE91 !important;
	}
	a:hover {
		color: #38D238;
	}
	/* Coloring Borders */
	.textCtrl {
		border-color: #0E290E;
	}
	.sectionFooter, .subHeading {
		border-bottom-color: #0E290E;
	}
	.tabs, .tabs li a, .tabs.noLinks li {
		border-color: #0a1c0b;
	}
	.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo, .node .nodeLastPost {
		border-color: #0a1c0b;
	}
	/* Changing Borders */
	.navTabs, .sectionMain {
		border: none;
	}
	.nodeList .categoryStrip {
		border-bottom: 0px;
		border-bottom-left-radius: 0px;
		border-bottom-right-radius: 0px;
	}
	/* I hate gradients */
	.navTabs .navTab.selected .tabLinks, .nodeList .categoryStrip, .node .nodeLastPost, .secondaryContent, .sectionFooter, .navTabs .navTab.selected .navLink, .profilePage .tabs.mainTabs li.active a, .tabs li a, .tabs.noLinks li, .subHeading, .tabs {
		background-image: none !important;
	}
	/* Misc - I got to lazy to sort the rest of the changes */
	.navTabs .navTab.selected .navLink {
		border: 1px solid #000;
		border-bottom: 1px none #0A1C0B;
	}
	.textWithCount.subHeading .count {
		background-color: transparent;
	}
	.navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus {
		color: #38D238;
		background-color: #2c4129;
		border-color: #0a1c0b;
		text-shadow: none;
	}
}
