css:
@-moz-document domain("www.oploverz.in") {
#eyeDiv, .text-share, #innity-in-post, #wc-comment-header, #sidebar, .recommended, .box, .strip, .headads, #btm_banner, .boxed .left, center iframe, #sidebar .section:nth-child(3), #sidebar .section:nth-child(4), #sidebar .section:nth-child(5), #sidebar .section:nth-child(6), #sgpb-popup-dialog-main-div-wrapper, .sgpb-popup-overlay, #wpd-bubble-wrapper, #ctr_banner, iframe[__idm_frm__], a[title=" 1xbet "] {display:none !important;visibility: hidden!important;}
.postbody .boxed .right .lts {float: none;width: auto;}
.postbody .boxed .right .lts ul li .dtl {width: auto;float: none;padding-left: 155px;}
.postbody {width: 100%;float: none;margin-right: 0;}
.animeinfo{border-bottom:0}
.container-op.genre, #content {margin-bottom: 15px;border: 1px solid #e5e6e9}
#header .logo img {margin: 25px 0;}
#wpcomm .wc-comment-title{text-align:center;}
.wpdiscuz-subscribe-bar, .wpdiscuz-front-actions{border: 1px solid #f1f1f1;}
#wpcomm {max-width: 100%;margin: 0;}
#wpcomm, #wpcomm .wc-thread-wrapper{margin:0;padding:0;}
.animeinfo .left {width: 50%;margin:0;}
.animeinfo .right {width: 49%;}
.op-download {background: #f7f7f7;border-radius: 0;}
.op-download .title-download {background: #3c9ccd;text-transform: capitalize;}
.op-download .list-download {margin-top: 0;background: #dddddd;}
.next-prev-eps {display: block;text-align: center;}
.next-prev-eps .btn-full-eps, .next-prev-eps .btn-next, .next-prev-eps .btn-prev{width: 30.4%;border-radius: 0;margin: 0 4px;}
.box-share {min-height: 0;}
#op-single-post .dtl{padding:10px;}
.movlist:last-child {margin-bottom:0px;}
#footer{text-align: center;padding: 15px 0px !important;}
#footer .subscripe-footer{padding:0;}
}
