css:
@-moz-document domain("demo.papoo.de"){
  h1 {
    text-transform: capitalize;
  }
  .login h1::after {
    content: 'Modern Papoo';
    font-size: 8px;
    margin-left: 20px;
  }
  div.oben {
    height: 70px;
  }
  div.oben div.container div.oben_innen{
    transform: scale(0.6);
    margin-top: -10px;
    filter: brightness(0) invert(1)
  }
  .navbar {
    margin-bottom: 5px;
  }
  .navbar .nav > li > a {
    padding: 8px 15px 8px 15px;
    text-transform: capitalize;
    font-size: 19px;
  }
  .login .content, .account-container {
    border: none;
    border-radius: 5px;
    background-color: white;
  }
  #logo img {
    filter: brightness(0) invert(1);
  }
  body.login {
    background-color: #ddddddd;
  }
  body.login input[type=text], body.login input[type=password], body.login div.field, body.login select {
    color: #333333;
    background-color: #eeeeee;
    box-shadow: none !important;
    border: none !important;
    border-radius: 5px;
    
  } 
  
  .span3 {
    width: 200px;
  }
  .bookmarks {
    display: none;
  }
  .bookmarks2 .tile {
    display: none;
  }
  .artikel {
    width: 79%;
  }
  .span5 {
    width: 100%;
  }
  .span6 {
    display: none;
  }
  h1 {
    text-transform: capitalize;
  }
  .alert-success strong::before {
   font-family: FontAwesome;
    content: '\f05a';
    margin-right: 20px;
    font-weight: normal;
    font-size: 18px;
  }
  .alert {
    width: 65%;
  }

  
  #footer {
    margin: 0px;
    padding: 0px;
    padding-left:30px;
  }
  legend {
    margin-bottom: 10px;
  }
  h2{
    text-transform: capitalize;
    user-select: none;
    cursor: default;
  }
  #footer h2 {
    font-size: 16px;
    text-transform: capitalize;
  }
  #footer p {
    font-size: 12px;
  }
  .media-body {
    position: relative;
    border: none;
    background-color: #ececec;
  }
  .media-body h2.media-heading {
    text-transform: capitalize;
  }
  .media-body .teaserdat {
    margin-bottom: 50px;
  }
  .media-body .authormessage {
    font-size: 10px;
    float: right;
    font-style: normal;
  }
  .media-body a {
    float:right;
    display: block;
    font-weight: normal;
    background-color: #8BB73C;
    margin-bottom: 20px;
    padding: 5px 8px 5px 8px;
    position: absolute;
    bottom: 13px;
    right: 20px;
   
    color: white !important;
    background-color: #8BB73C!important;
    border-radius: 5px;
  }
  
  .media-body a:hover {
    text-decoration: none;
    color: white;
    background-color: #8BB73C;
  }
  
  button.btn, input.btn[type="submit"] {
    padding: 8px 15px 8px 15px;
    text-transform: capitalize;
    font-size: 19px;
    color: white !important;
    background-color: #8BB73C!important;
    box-shadow: none;
    transition: background-color 0.2s, color 0.2s;
    border-radius: 5px;
  }
  .media-body, fieldset {
    border-radius: 5px;
    background-color: #eeeeee;
    border: none;
  }
  legend {
    border: none!important;
    margin-left: 5px;
    display: inline;
    background-color: #eeeeee!important;
    padding: 10px 5px 10px 5px;
    width:auto;
    border-radius: 5px;
  }
  input[type=text] {
    background-color: white;
    color: #333333;
    border:none;
    border-radius: 5px;
   
  }
  
  button.btn, input.btn[type="submit"]:hover {

    background-color: #8BB73C !important;
    box-shadow: none;
    color: white;
  }
  
  .btn.btn-danger {
    border: 1px solid red !important;
    color: red !important;
  }
  .btn.btn-danger:hover {
    background-color: red !important;
    color: white !important;
  }
  textarea:focus, input[type=text]:focus {
    box-shadow: none;
    border-color: black;
  }
  a{
    color: #8BB73C;
  }
  a:hover {
    color: #8BB73C;
  }
  ul.nav.nav-tabs li{
    background-color: #eeeeee;
    margin: 0px;
    overflow:hidden;
    border: none !important;
    padding:0px;
  }
  ul.nav.nav-tabs li a {
    padding: 8px 5px 8px 5px;
    margin: 0px;
    border: none;
  }
  .nav-tabs{
    border:none;
  }
  ul.nav.nav-tans li.active {
    border: none;
    margin:0px;
  }
  ul.nav.nav-tabs li:first-child {
    border-radius: 5px 0px 0px 5px;
  }
  ul.nav.nav-tabs li:last-child {
    border-radius: 0px 5px 5px 0px;
  }
  
  fieldset {
    line-height: 60px;
    padding-bottom: 20px;
  }
  .artikel_sitemap a {
    background-color: #ececec;
    transition: background-color 0.2s;
  }
  .artikel_sitemap a:hover {
    background-color: #cecece;
  }
  .artikel_sitemap a img {
    display: inline-block;
    filter: brightness(0) invert(0.5);
  }
  .artikel_sitemap a:hover img {
    display: inline-block;
    filter: brightness(0) invert(1);
  }
  #columns {
    display: flex;
    flex-wrap: wrap;
    flex-direction: row;
    justify-content: space-between;
  }
  #columns .pin {
    margin-right: 20px;
    box-shadow: none;
    border-radius: 0px;
    border: 1px solid #cecece;
  }
  .navbar .nav > li > a.menuxaktiv_back, .navbar .nav > li > a.menuy_aktiv, .navbar .nav > li > a:hover {
    border-radius: 0px 0px 5px 5px;
    border:none!important;
  }
  .artikel2 ul, .artikel2 li {
    display: none;
  }
  body {
    background-color: white;
  }
  .navbar  {
    border-bottom: none;
  }
  .navbar-inner {
    border:none;
  }
  .alert {
    border: none;
    background-color: #8BB73C;
    color: white;
    border-radius: 5px;
  }
  .bs-docs-sidenav li a {
    border-radius: 5px;
    border: none;
  }
  input.inputnav_zwischen.vtip {
    filter: saturate(0%)!important;
  }
  
  ul.artikel_sitemap li a{
    border-radius: 5px;
    background-color: #eeeeee;
    margin-bottom: 4px;
  }
  ul.artikel_sitemap li span.lia_toggle {
    transform:scale(0.7);
  }
  div.sitemapheader {
    background-color: #eeeeee;
    border-radius: 5px;
    margin-bottom: 10px;
  }
  input.btn.btn-danger {
    background-color: red!important;
    color: white;
    border:none!important;
  
  }
  input.btn.btn-danger:hover {
    background-color: #aa0000!important;
    color: white;
    border:none!important;
  
  }
  .btn {
    font-size: 16px!important;
  }
  table.table tbody tr {
    border-radius: 5px!important;
    border:none!important;
    overflow: hidden!important;
    background-color: #eeeeee;
    display: table-row!important;
    margin-top: 5px;
  }
  table.table tbody tr:nth-child(even) {
    background-color:#cccccc;
  }
  table.table tbody th, table.table tbody td {
    background-color: transparent!important;
    border: none!important;
    
  }
  table.table tbody{
    border-radius: 5px!important;
    overflow: hidden!important;
   display: block;
  }
  fieldset input.btn[value=Speichern] {
    float:left!important;
  }
  div.untermenu2 ul {
    margin-left: 0px;
  }
  div.untermenu2 ul li a{
    border-radius: 0px!important;
  }
  div.untermenu2 ul li:last-child a {
    display: block;
    border-radius: 0px 0px 5px 5px!important;
    
  }
  .menuxaktiv_back {
    border-radius: 5px 5px 0px 0px !important;
  }
  select, textarea {
    border:none!important;
    border-radius: 5px!important;
  }
  ul.nav a.menuxaktiv_back {
    border-radius: 0px 0px 5px 5px!important;
  }
}
