css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("(https?://en\\.wikipedia\\.org/)(?!wiki/Main_Page).+") {
  
  /* hide tagline */
  #siteSub {
    display: none;
  }

  /* hide header */
  #mw-head {
    display: none;
  }
  #mw-page-base {
    display: none;
  }

  /* hide irrelevant menu sections */
  #p-logo {
    display: none;
  }
  #p-navigation {
    display: none;
  }
  #p-interaction {
    display: none;
  }
  #p-tb {
    display: none;
  }
  #p-coll-print_export {
    display: none;
  }
  div#mw-panel #p-wikibase-otherprojects.portal {
    background-image: none;
  }
  div#mw-panel #p-lang.portal {
    background-image: none;
  }
  
  div#mw-panel {
    top: 0px;
    left: 1220px;
    opacity: 0.65;
  }

  /* prevents sub/superscripts from throwing off line spacing */
  sub, sup {
    font-size: 75%;
    line-height: 0;
    position: relative;
    vertical-align: baseline;
  }
  sup {
    top: -0.33em;
  }
  sub {
    bottom: -0.33em;
  }
  
  table.ambox {
    opacity: 0.6;
    font-size: 0.8em;
    margin: 0;
  }
  
  td.mbox-image {
    display: none;
  }


  /* White all over background */
  html {
    background-color: white;
  }
  
  body {
    background-color: white;
  }
  
  .mw-body {
    border: 0px none;
  }
  
  h1, h2, h3, h4, h5, h6 {
    font-family: Baskerville, "Libre Baskerville", serif !important;
  }

  /* header */
  body div#content.mw-body h1#firstHeading.firstHeading {
    font-size: 2em;
    font-weight: 500;
    display: block;
    border: 0px none;
  }
  
  table.ambox-content, table.ambox {
    border: 0px none;
  }

  /* content width */
  #content {
    max-width: 1000px !important;
    margin-left: 200px;
  }

  /* content font */
  div#content div#bodyContent {
    font-family: Baskerville, "Libre Baskerville", serif;
    font-size: 1em;
    color: black;
  }
  
  #bodyContent #mw-content-text p {
    line-height: 24px;
  }

  /* TOC */
   #toc {
     font-family: Open Sans, sans-serif !important;
     border: none;
     font-size: 12px;
     font-weight: 400;
     position: fixed !important;
     display: inline-block;
     top: 0;
     bottom: 0;
     left: 0px;
     width: 200px;
     padding: 0.4em 0;
     margin: 0;
     overflow: auto;
  }

  #toc > ul {
    overflow-y: auto !important;       
    overflow-x: hidden !important;
    padding-top: 2em;
    padding-right: 0;
    padding-bottom: 2em;
    padding-left: 0;
    margin: auto 0 !important;
  }

  .toc ul ul {
    margin-left: 1em !important;
    padding-top: 0.2em;
    padding-bottom: 0.5em;
  }
  
  .toc a {
    display: block;
    padding: 0.1em 1em;
    line-height: 1.25 !important;
  }

  .toclevel-1 > a > .toctext {
    text-transform: uppercase;
    display: inline-block;
    padding-top: 0.4em;
    padding-bottom: 0.1em;
  }

  .tocnumber {
    display:none;
  }

  .toc h2 {
    display:none !important;
  }
  
  div.toctitle {
    text-decoration: none !important;
  }

  span.toctoggle {
    display:none !important;
  }

  #toc a:link {
    color: #111 !important;
    text-decoration: none !important;
  }

  #toc a:visited {
    color: #888 !important;
  }

  #toc a:hover {
    color: #000 !important;
    text-decoration: none !important;
    background: rgba(0,0,0,0.05);
  }

  #toc a:active {
    color: #00A6FF !important;
    outline:none;
  }

  /* thumb tweaks */
  div.thumbinner, .infobox {
    border: 0px none;
    color: #212121;
    font-size: 0.8em;
  }
  
  .thumbcaption {
    margin-top: 0.5em;
    margin-bottom: 0.5em;
  }
  
  img.thumbimage {
    border: 0px none;
  }
  
  /* codeblock tweak */
  
  pre, code, .mw-code {
    border: 0px none;
  }
}
