css:
@-moz-document domain("animeindo.co") {
html {
    font-family: MyriadPro;
    background: #000;
    background: url(http://datamaldini.url.ph/gambar/file/background_animeindo.co.jpg) bottom no-repeat fixed #000;
}
.nyaaheader {
    background: rgba(0, 132, 239, 0);
    height: 90px;
    width: 728px;
    float: right;
    margin: 30px 0 20px 0;
    overflow: hidden;
}
.menufloat {
    display: none;
    position: fixed;
    top: 0;
    z-index: 2010;
    width: 100%;
    height: 40px;
    overflow: hidden;
    background: url(http://animeindo.co/wp-content/themes/animanga/images/menu.jpg) #232323;
    box-shadow: 0 0 15px 0 #000;
}
#footer {
    background: url(http://datamaldini.url.ph/gambar/file/footer_animeindo.co_3.jpg) top center no-repeat #8f8d8e;
    min-height: 50px;
    padding: 10px 0 10px;
    margin-bottom: -20px;
}
.letterhead {
    font-size: 14px;
    color: #fff;
    width: 337px;
    height: 18px;
    padding: 35px 10px 9px;
    background: url(http://animeindo.co/wp-content/themes/animanga/images/letterhead.png) top left no-repeat;
}
.newepisodetitle {
    background: url(http://animeindo.co/wp-content/themes/animanga/images/blacktr.png);
    position: absolute;
    width: 100%;
    height: 70px;
    bottom: 0;
    left: 0;
    margin-bottom: -100px;
    transition: all 5.2s;
    -webkit-transition: all 0.2s;
    -moz-transition: all 0.2s;
}






.latest h2 {
    background: #2b5a82;
    color: #fff;
    font: 16px MyriadPro;
    text-transform: uppercase;
    padding: 5px 15px;
    font-weight: bold;
}
.side_ongoinglist h3 {
    width: 92%;
    background-color: #4267b2;
    color: #fff;
    font-size: 16px;
    padding: 2% 4%;
    margin-bottom: 5px;
}
#scrollganteng::-webkit-scrollbar-thumb {
    background-color: #4267b2;
    background-image: -webkit-gradient(linear,0 0,0 100%,color-stop(.5,rgba(255,255,255,.2)),color-stop(.5,transparent),to(transparent));
}
.wp-pagenavi span.current {
    border-radius: 2px;
    font-weight: bold;
    background: #4267b2;
    color: #eee;
}
.footercol li a:hover {
    color: #4267b2;
}
.featured:nth-child(odd) {
    background: #243754;
}
.featured:nth-child(even) {
    background: rgb(37, 48, 64);
}
#footer_affiliates {
    background: #092944;
    min-height: 50px;
}
}
