css:
@-moz-document domain("cbre.service-now.com") {
/* CBRE OSCURO  rgba(0,114,65,1)
	CBRE CLARITO #00a759 */

  .navpage-header
  {
    background: linear-gradient(135deg, rgba(0,114,65,1) 0%,rgba(0,185,58,1) 100%);
  }

   .navpage-layout .navbar-divider  {
    background: linear-gradient(135deg, rgba(0,114,65,1) 0%,rgba(0,185,58,1) 100%);
  }

  .sn-frameset-header
  {
    background: transparent;
  }

  .navbar-divider
  {
    background: transparent;
  }

  a.navbar-brand
  {
    background-image: url('http://i.imgur.com/g0XHKnA.png') !important;
  }

  .sn-navhub.sn-navhub_themed .sn-navhub-btn:active::before, .sn-navhub.sn-navhub_themed .sn-navhub-btn.state-active::before
  {
    background: linear-gradient(135deg, rgba(0,114,65,1) 0%,rgba(0,185,58,1) 100%);
    border-bottom: 3px solid rgba(0,114,65,1);
    box-shadow: none;
    color: #FFF;
    z-index: inherit;
  }

.sn-navhub.sn-navhub_themed .sn-navhub-btn:active::after, .sn-navhub.sn-navhub_themed .sn-navhub-btn.state-active::after{
    background: linear-gradient(135deg, rgba(0,114,65,1) 0%,rgba(0,185,58,1) 100%);
    border-bottom: 3px solid rgba(0,114,65,1);
    box-shadow: none;
    color: #FFF;
    z-index: inherit;
  }

 /* navbaricons */
.sn-navhub.sn-navhub_themed .sn-navhub-btn::before {
    color: rgba(0,114,65,1);
}

 /*navbar text */
.magellan_navigator .sn-widget-list_indentation .nav-favorites-list .sn-widget-list_v2 .sn-widget-list-item .sn-widget-list-title, .magellan_navigator .sn-widget-list_indentation .nav-favorites-list .sn-widget-list_v2 .sn-widget-list-item .sn-widget-list-subtitle, .magellan_navigator .sn-widget-list_indentation .nav-favorites-list .sn-widget-list_v2 .sn-widget-list-item .sn-widget-list-action {
    color: rgba(0,114,65,1);
}

/* tile background lighter */

.magellan_navigator .sn-widget-list_indentation .nav-favorites-list .sn-widget-list_v2 .sn-widget-list-item {
    background-color: #fafafa;
}
}
