css:
@-moz-document domain("mstdn.jp"), domain("pawoo.net"), domain("mastodon.social"), domain("mastodon.cloud"), domain("friends.nico"), domain("mastodon.xyz"), domain("social.tchncs.de"), domain("mastodon.network"), domain("octodon.social"), domain("mamot.fr"), domain("social.lou.lt"), domain("mstdn.io") {
/* Around space */
.ui {
    background-color: #DDDDDD !important;
}

/* Timeline elements */
.column>.scrollable{
 background: white;   
}

/* Detail Toot */
.detailed-status,
.detailed-status__action-bar{
 background: #EAEAEA;
}

/* Elements */
.display-name,
.status__display-name *,
.account__display-name strong,
.status__content,
.notification__display-name:hover,
.notification__display-name:active,
.detailed-status,
.detailed-status__action-bar,
.detailed-status__display-name strong, 
.detailed-status__display-name span,
.account__action-bar__tab *,
.getting-started p{
    color: black !important;
}


/* Reply Toot Textbox */
.reply-indicator{
    background-color: #EAEAEA;
}

/* Scroll bar thumb default color */
::-webkit-scrollbar-thumb	{
 	background: #E1E8ED !important;   
}

/* Scroll bar thumb is acrive and hover */
::-webkit-scrollbar-thumb:hover,
::-webkit-scrollbar-thumb:active{
	background: #A5A688 !important;   
}

/* Scroll bar background  */
::-webkit-scrollbar-track{
    background: white !important;
}


/* Left sidebar */
.drawer__inner, .navigation-bar{
 	background: #2B7BB9;
    color: white;
}

.autosuggest-textarea *{
	color: black;
}

/* Toot button */
.button {
    background-color: #3B94D9;
}

/* Search result list */
.search-results__hashtag, .search-results__hashtag:hover{
    color: #2B7BB9;  
}

/* Search Text Input */
.search__input,
.search__input:hover,
.search__input:active,
.search-results__section{
    color: black;
    background: white !important;
}

.search__icon 
.fa-search.active,
.search-results__section,
.search__icon .fa-times-circle.active{
	color: #292F33;   
	opacity: 1;
}

.drawer__inner.darker{
    background: white;
}

/* Link */
.status__content,
.status__content a,
span.ellipsis,
.active{
    color: #2B7BB9;
}

/* showmore */
.status__content .status__content__spoiler-link,
.status__content .status__content__spoiler-link:active,
.reply-indicator__content .status__content__spoiler-link
{
    background-color: transparent !important;
    color: #2B7BB9 !important;
    outline: 0px !important;
    border: 1px solid #2B7BB9 !important;
    margin: 0px 0px 0px -1px !important;
}

/* showmore hover */
.status__content .status__content__spoiler-link:hover{
        background-color: #2B7BB9 !important;
	    color: white !important;
    	outline: 0px;
    	border: 1px solid #2B7BB9;    
}

/* media hidden */
.media-spoiler, .video-error-cover{
 	color: black;
    background: gray;
}

/* Left header */
.tabs-bar,
.drawer__header,
.drawer__header *,
.search-results__header {
    background-color: #292F33;
    color: #F5F8FA;
}


/* TL column header */
.column-header,
.column-icon,
.column-back-button{
    color: black !important;
	background-color: #EAEAEA !important;
}

.column-settings__outer{
    background-color: #F5F8FA;
}

.column-settings__section, .setting-toggle{
    color: #8899a6;
}

.setting-text{
 color: black !important;
}

.column-icon:hover, .column-collapsable__button:hover{
    background-color: #EAEAEA !important;
    color: #2B7BB9 !important;
}

/* Navigation */
.column-subheading{
	color: #F5F8FA;
	background: #131619;
}

.column-link {
    color: #F5F8FA;
	background: #292f33
}

.fa-reply:hover, .fa-reply-all:hover, .dropdown__icon:hover{
	color: #2b90d9;
}

.fa.fa-fw.fa-retweet:hover{
    background-position: 0 100% !important;
}

.empty-column-indicator, .empty-column-indicator{
	background: white;
}

.media-spoiler__warning, .media-spoiler__trigger{
 color: white;   
}

/* Fav */
.fa-star:hover{
 color: #ca8f04;   
}


/* Follow, Follower Load more */
.load-more:hover{
    background: #EAEAEA;
}

.dropdown--active .dropdown__content>ul, .dropdown__content-list-link{
    background: white !important;
}

.dropdown__content-list-link:hover, .dropdown__content-list-link:active{
    background: #2b7bb9 !important;
    color: white !important;
}

/*
.account__header>div:hover{
    background: transparent;
}*/

}
