css:
@-moz-document domain("mstdn.jp"),
domain("mastodon.cloud"),
domain("mastodon.social"),
domain("friends.nico"),
domain("ika.moe"),
domain("pawoo.net"),
domain("mstdn-workers.com"),
domain("mastodon.xyz"),
domain("mstdn.wiki"),
domain("gunmastodon.com") {

body {
    color: snow;
    font-family: "Noto Sans JP", "游ゴシック Medium", "游ゴシック体", "Yu Gothic Medium", YuGothic, "ヒラギノ角ゴ ProN", "Hiragino Kaku Gothic ProN", "メイリオ", Meiryo, "ＭＳ Ｐゴシック", "MS PGothic", sans-serif;
}
body.app-body {
    color: #000;
}
.columns-area {
    background-color: #333;
}
div.drawer__inner {
    background-color: #F0EFE7;
    border: solid #CCCCCC 1px;
}
.account__header__username {
    color: #555 !important;
}
.account__header__content {
    color: #222 !important;
}
.account {
    border-bottom-color: #ccc !important;
}
.account__header__avatar {
    border: solid 1px #ccc;
}
.account__action-bar {
    border-color: #ccc;
}
.account__action-bar__tab {
    border-color: #ccc;
}
.account__action-bar__tab span {
    color: #000;
}
.account__action-bar__tab strong {
    color: #111;
}
.account__header__display-name {
    text-shadow: 0px 0px 5px #000000;
}
.display-name {
    color: #666;
}
.column-header.active .fa {
    color: #FFFFFF;
}
.drawer {
    background-color: #fff;
}
.column {
    background-color: #fff;
}
.drawer__header {
    border: solid #CCCCCC 1px;
}
.drawer__header a {
    background-color: #F0EFE7;
}
.drawer__header a:hover {
    background-color: #DADBC0;
}
.column-header {
    background-color: #F0EFE7;
    border: solid #CCCCCC 1px;
    display: flex;
    align-items: center;
    padding: 14px!important;
}
.column-back-button {
    background-color: #F0EFE7;
    border: solid #CCCCCC 1px;
    display: flex;
    align-items: center;
    padding: 14px!important;
    text-decoration: none;
}
.column-back-button:hover,
.column-header:hover {
    background-color: #DADBC0;
    text-decoration: none;
}
.display-name strong {
    color: #000000;
}
.column>.scrollable {
    background: #F0EFE7;
}
.scrollable::-webkit-scrollbar-thumb {
    background-color: #999;
}
.scrollable::-webkit-scrollbar-track {
    background-color: #fff;
}
.scrollable::-webkit-scrollbar-track-piece {
    background-color: #ccc;
}
.scrollable {
    background: #F0EFE7;
    border-style: solid;
    border-color: #ccc;
    border-width: 0px 1px 1px 1px;
}
.scrollable div {
    background: #F0EFE7;
}
.column-settings--section {
    color: #333;
}
.react-toggle--checked .react-toggle-track {
    background-color: #DADBC0;
    border-color: #DADBC0;
}
.react-toggle-thumb {
    border-color: #777 !important;
}
.setting-text {
    margin-bottom: 1px !important;
}
.column-settings--outer {
    padding-bottom: 20px !important;
    background-color: #F0EFE7;
    border-style: solid;
    border-color: #ccc;
    border-width: 0 1px 1px 1px;
}
.column-link {
    color: #000;
    background-color: #F0EFE7;
    border-style: solid;
    border-color: #ccc;
    border-width: 0 1px 1px 1px;
}
.column-link:hover {
    background-color: #DADBC0;
}
.setting-toggle {
    color: #333;
}
.status {
    border-style: solid;
    border-color: #ccc;
    border-width: 0px 0px 1px 0px;
}
.avatar {
    -webkit-filter: drop-shadow(0 0 1px rgba(0, 0, 0, .5));
}
.status__avatar {
    -webkit-filter: drop-shadow(0 0 3px rgba(0, 0, 0, .5));
}
.status__content a {
    color: #777;
}
.navigation-bar strong {
    color: #000000;
}
.notification__message {
    color: #666;
}
a.notification__display-name {
    color: #000;
}
.notification__display-name:hover {
    color: #666;
}
.detailed-status {
    border-style: solid;
    border-color: #ccc;
    border-width: 1px 0px 0px 0px;
}
.detailed-status__action-bar {
    border-style: solid;
    border-color: #ccc;
    border-width: 1px 0px 1px 0;
}
.fa-home {
    color: #FFFFFF;
    text-shadow: 2px 2px 1px #CCCCCC, -2px 2px 1px #CCCCCC, 2px -2px 1px #CCCCCC, -2px -2px 1px #CCCCCC;
}
.column-header i.fa-home {
    color: #FFFFFF;
    font-size: 1.2em;
    text-shadow: 0px 0px 5px #333333;
}
.fa-bell {
    color: #FFFFFF;
}
.column-header i.fa-bell {
    color: #FFFFFF;
    font-size: 1.2em;
    text-shadow: 0px 0px 5px #333333;
}
.fa-asterisk {
    color: #FFFFFF;
    text-shadow: 2px 2px 1px #CCCCCC, -2px 2px 1px #CCCCCC, 2px -2px 1px #CCCCCC, -2px -2px 1px #CCCCCC;
}
.column-header i.fa-users {
    color: #FFFFFF;
    font-size: 1.2em;
    text-shadow: 0px 0px 5px #333333;
}
.fa-users {
    color: #FFFFFF;
    text-shadow: 2px 2px 1px #CCCCCC, -2px 2px 1px #CCCCCC, 2px -2px 1px #CCCCCC, -2px -2px 1px #CCCCCC;
}
.fa-globe {
    color: #FFFFFF;
    text-shadow: 2px 2px 1px #CCCCCC, -2px 2px 1px #CCCCCC, 2px -2px 1px #CCCCCC, -2px -2px 1px #CCCCCC;
}
.inverted i {
    color: #707b97 !important;
}
.fa-cog {
    color: #FFFFFF;
    text-shadow: 2px 2px 1px #CCCCCC, -2px 2px 1px #CCCCCC, 2px -2px 1px #CCCCCC, -2px -2px 1px #CCCCCC;
}
.fa-sign-out {
    color: #FFFFFF;
    text-shadow: 2px 2px 1px #CCCCCC, -2px 2px 1px #CCCCCC, 2px -2px 1px #CCCCCC, -2px -2px 1px #CCCCCC;
}
.fa-star.button {
    color: #ca8f04;
}
.column-link i.fa-star {
    color: #FFFFFF;
    text-shadow: 2px 2px 1px #CCCCCC, -2px 2px 1px #CCCCCC, 2px -2px 1px #CCCCCC, -2px -2px 1px #CCCCCC;
}
.fa-ban {
    color: #FFFFFF;
    text-shadow: 2px 2px 1px #CCCCCC, -2px 2px 1px #CCCCCC, 2px -2px 1px #CCCCCC, -2px -2px 1px #CCCCCC;
}
.fa-book {
    color: #FFFFFF;
    text-shadow: 2px 2px 1px #CCCCCC, -2px 2px 1px #CCCCCC, 2px -2px 1px #CCCCCC, -2px -2px 1px #CCCCCC;
}
.fa-volume-off {
    color: #FFFFFF;
    text-shadow: 2px 2px 1px #CCCCCC, -2px 2px 1px #CCCCCC, 2px -2px 1px #CCCCCC, -2px -2px 1px #CCCCCC;
}
.fa-user-times {
    color: #FFFFFF;
    text-shadow: 2px 2px 1px #CCCCCC, -2px 2px 1px #CCCCCC, 2px -2px 1px #CCCCCC, -2px -2px 1px #CCCCCC;
}
.column-back-button {
    color: #FFFFFF;
}
.icon-button.active {
    color: #FFFFFF;
}
.sidebar i {
    color: #669966;
}
.search input {
    background-color: #F0EFE7;
    border: solid #CCCCCC 1px;
}
.search input:focus {
    background-color: #DADBC0;
    border: solid #CCCCCC 1px;
}
.admin-wrapper .sidebar ul ul a.selected,
.simple_form button {
    background-color: #326532;
}
.admin-wrapper .sidebar ul ul a.selected:hover,
.simple_form button:hover {
    background-color: #4c7f4c;
}
.autosuggest-textarea__textarea {
    background-color: #fcfcfc;
    border-style: solid;
    border-color: #ccc;
    border-width: 1px 1px 0 1px;
}
.autosuggest-textarea__textarea:hover {
    background-color: #ffffff;
}
div.compose-form__modifiers {
    background-color: #fcfcfc;
    border-style: solid;
    border-color: #ccc;
    border-width: 0 1px 1px 1px;
}
.compose-form__buttons {
    border: solid 1px #ccc
}
.button {
    border: solid 1px #333 !important;
    background-color: #666;
    transition: 1.0s;
}
.button:hover {
    border: solid 1px #666 !important;
    background-color: #999;
}
.column-icon.column-icon-clear {
    background-color: transparent;
    position: absolute;
}
.column-icon.collapsable-collapsed {
    border-color: transparent;
    background-color: transparent;
}
.column-icon.collapsable {
    border-color: transparent;
    background-color: transparent;
    color: #FFFFFF;
    text-shadow: 2px 2px 1px #CCCCCC, -2px 2px 1px #CCCCCC, 2px -2px 1px #CCCCCC, -2px -2px 1px #CCCCCC;
}
:focus {
    outline: -webkit-focus-ring-color auto 0px;
    outline-width: 0px;
}
.column-back-button {
    color: #666;
    display: flex;
    align-items: center;
}
div.emoji-dialog.with-search {
    height: 420px;
}
a[style*="cursor: zoom-in;"] {
    filter: blur(3px);
    opacity: 0.5;
    transition: opacity 0.3s ease-out;
}
a:hover[style*="cursor: zoom-in;"] {
    filter: blur(0);
    opacity: 0.9;
}
.status__content__spoiler-link {
    width: auto;
    background-color: #FFF;
}
a.status__content__spoiler-link {
    display: block;
    color: #FFF;
    text-align: center;
    margin: 15px auto 0 auto;
}
.status__content .status__content__spoiler-link {
    transition: 1.0s;
    background-color: #999;
    outline-color: transparent;
}
.status__content .status__content__spoiler-link:hover {
    background-color: #666;
}
.getting-started p {
    color: #000;
}
}
