css:
@-moz-document domain("mstdn.jp") {
/* Timeline elements */
.scrollable *:not(.fa) {
    background-color: #FFFFFF;
    color: #2F323D;
}

/* Around space */
.ui {
    background-color: #CFD8DD !important;
}

/* Elements */
.scrollable,
.drawer__inner.darker,
.search-results__section *,
.drawer__header>a:hover,
.drawer__header>a:hover *{
    background-color: white !important;
    color: black !important;
}

/* Scroll bar thumb default color */
::-webkit-scrollbar-thumb	{
 	background: #313443 !important;   
}

/* Scroll bar thumb is acrive and hover */
::-webkit-scrollbar-thumb:hover,
::-webkit-scrollbar-thumb:active{
	background: #A5A688 !important;   
}

/* Scroll bar background  */
::-webkit-scrollbar-track{
    background: #CED9DD !important;
}


/* Left sidebar */
.drawer__inner, .navigation-bar{
 	background: #CFD8DD;
    color: white;
}

.autosuggest-textarea *{
	color: black;
}

/* Toot button */
.button {
    background-color: #E5447A;
}

/* Search result list */
.search-results__section *{
    color: black !important;   
}

/* Search Text Input */
.search__input,
.search__input:hover,
.search__input:active{
    color: black;
    background: #F5F5FF !important;
	border-radius: 5px;
	-webkit-border-radius: 5px;
	-moz-border-radius: 5px;
}

/* Search Icon */
.status__content a,
.status__content a>span,
span.ellipsis,
.active,
.search__icon * {
    color: #9399AE !important;
}

/* Left header */
.tabs-bar,
.drawer__header,
.drawer__header *,
.search-results__header {
    background-color: #D81A60;
    color: white;
}

/* TL column */
.column-header,
.column-icon,
.column-back-button{
    color: white;
	background-color: #D81A60;   
}
}
