css:
@document url(https://github.com/) {
.Header {
    background-color: #286e74;
    
  }
  .Header .header-search-scope {
    border-right: none;
  }
  .repohead.experiment-repo-nav {
    background-color: #4BB3BB;
  }
  body {
    background-color: #ececec;
  }
  .reponav-item {
    color: #ececec;
    border:none;
  }
  .reponav-item:hover {
    background-color: #286e74;
    border-bottom: 1px solid #286e74;
    color: white;
  }
  .reponav-item.selected {
    color: #286e74;
    background-color: #ececec;
    border: none;
    border-bottom: 2px solid #ececec;
  }
  a {
    color: #286e74;
  }

  .subnav-search-context + .subnav-search .subnav-search-input {
    border: none;
    border-radius: 0px 5px 5px 0px;
    color: white;
    background-color: #cccccc;
    box-shadow: none;
  }
  .subnav-search-context .btn {
    border-radius: 5px 0px 0px 5px;
    background-color: #cccccc;
    box-shadow: none;
    color: white;
    border: none;
  }
  .subnav-search-context .btn:before {
    display: none;
  }
  .subnav-search-context .btn:after {
    display: none;
  }

  .subnav-item {
    color: white;
    background-color: #cccccc;
    border: none;
  }
  .subnav-item:first-child {
    border-right: 1px solid white;
  }
  .subnav-item:hover {
    background-color: #aaaaaa;
  }
  .btn {
    background-color: #cccccc;
    color: white;
    border: none;
  }
  .btn-primary {
    background-color: #73AC21;
    color: white;
    border: none;
    background-image: none;
  }
  .btn-primary:hover {
    background-color: #518A01;
    background-image: none;
  }
  .table-list-header {
    background-color: #cccccc;
  }
  .btn-link.select-menu-button.js-menu-target {
    color: white;
  }
  .btn-link.select-menu-button.js-menu-target::after {
    transition: transform 0.2s;
  }
  .btn-link.select-menu-button.js-menu-target.selected::after {
    transform:rotate(180deg) translate(0px, 5px);
  }
  .btn.select-menu-button.js-menu-target {
    background-color: #cccccc;
    background-image: none;
    height: 34px;
    border-right: 1px solid white !important;
  }
  .d-table.table-fixed.width-full.Box-row--drag-hide {
    background-color: white;
    border: none;
    margin-top: 5px;
  }
  .d-table.table-fixed.width-full.Box-row--drag-hide:hover {
    box-shadow: none;
    border:none;
  }
  .octicon.octicon-issue-opened.open {
    color: #4bb3bb;
  }
  .btn.btn-sm.btn-with-count {
    background-color: #286e74;
    color: #4bb3bb;
    background-image: none;
    height: 28px;
    border: none;
    border-right: 1px solid #4bb3bb !important;
    
  }
  .btn.btn-sm.btn-with-count::after {
    transition: transform 0.2s;
  }
  .btn.btn-sm.btn-with-count.selected {
    box-shadow: none;
    
    color: #cccccc;
  }
  .btn.btn-sm.btn-with-count.selected::after {
    transform: rotate(180deg) translate(0px, 5px);
  }
  .btn.btn-sm {
    color: white;
    background-color: #cccccc;
    background-image: none;
    border: none;
    box-shadow: none;
    border-radius: 5px;
  }
  .btn.BtnGroup-item {
    margin-left: 7px;
  }
  .commit-tease.js-details-container.Details {
    background-color: #cccccc;
    border:none;
    color: white;
    margin-top: 20px;
  }
  
  .social-count {
    color: #4bb3bb;
    border: none;
    background-color: #286e74;
    height: 28px;
  }
  .social-count:hover {
    color: #cccccc;
  }
  ul.site-footer-links li a {
    color: #4bb3bb !important;
  }
  .link-gray-dark.no-underline.h4.js-navigation-open:hover {
    color: #4bb3bb !important;
  }
  .table-list-header {
    border-radius: 5px;
  }
  .Box-row.Box-row--focus-gray.p-0.js-navigation-item.js-issue-row.selectable.read.navigation-focus {
    border: none;
   box-shadow: none;
    border-radius: 5px;
    overflow: hidden;
  }
  svg.octicon.octicon-mark-github {
    transition: opacity 0.2s;
  }
  svg.octicon.octicon-mark-github:hover {
    opacity: 0.3;
  }
  div.protip {
    color: #898989;
  }
  div.protip svg, div.protip a {
    color: #4bb3bb !important;
  }
  ul.site-footer-links li a {
    color: #4bb3bb !important;
  }
  ul.site-footer-links li span {
    color: #898989 !important;
  }
  .btn-link {
    color: white !important;
  }
  .btn-link.selected {
    font-size: 14px !important;
  }
  .table-list-header {
    margin-bottom: 5px;
    margin-top: 25px;
  }
  tr.js-navigation-item td span a{
    color: #898989 !important;
  }
  tr.js-navigation-item td svg {
    color: #4bb3bb !important;
  }
  .readme > h3 {
    background-color: #cccccc;
    border: none;
    color: white;
  }
  ul.numbers-summary {
    background-color: #cccccc;
    border-radius: 5px 5px 0px 0px;
  }
  ul.numbers-summary a,  ul.numbers-summary a span{
    color: white !important;
  }
  .btn-with-count {
    border-radius: 5px 0px 0px 5px !important;
  }
  .js-selected-navigation-item.selected.subnav-item {
    background-color: #4bb3bb;
    border: none;
  }
  circle.day {
    background-color: #4bb3bb !important;;
  }
  .js-selected-navigation-item.selected.menu-item::before {
    background-color: #4bb3bb;
  }
  input.form-control.form-control.input-block {
    border: none;
    background-color: #cccccc;
    color: white;
    border-radius: 5px 5px 5px 5px;
    box-shadow: none !important;
  }
  .auth-form-body.mt-3 {
    border: none;
  }
  .timeline-comment-wrapper.js-comment-container  {
    border: none;
  }
  .timeline-comment-header {
    background-color: #4bb3bb!important;
    color:white;
    border:none;
  }
  .timeline-comment-wrapper .timeline-comment.current-user::before {
    border-right-color:#4bb3bb;
  }
  .edit-comment-hide {
    border:none!important;
  }
  .discussion-timeline-actions {
    background-color: transparent!important;;
  }
  .timeline-comment {
    border:none;
    background-color: transparent;
  }
  .btn.js-comment-and-button {
    background-color: #cccccc;
    color: white;
    background-image: none;
    border: none;
  }
  .btn-link.tabnav-tab {
    color: #4bb3bb !important;
  }
  .write-content.js-write-bucket.js-uploadable-container.js-upload-markdown-image.is-default.upload-enabled {
    border: none !important;
    box-shadow: none !important;
  }
  .edit-comment-hide {
    background-color: white !important;
    border-radius: 0px 0px 5px 5px;
  }
  .topic-tag {
    background-color: #4bb3bb !important;
  }
  .topic-tag:hover {
    color: white !important;
  }
  .btn {
    background-image: none !important;
  }
  .js-repo-filter.position-relative {
    background-color: white !important;
    padding: 15px !important;
  }
button.btn-link.js-repo-topics-form-toggle.js-details-target {
color: #286e73 !important;
}
li.pinned-repo-item,div.js-calendar-graph.is-graph-loading.graph-canvas.calendar-graph.height-full {
background-color: white !important;
}
}
