css:
@-moz-document domain("2ch.hk"),
               domain("2ch.pm"),
               domain("2ch.re"),
               domain("2ch.tf"),
               domain("2ch.wf"),
               domain("2ch.yt"),
               domain("2-ch.so")
{
  /* Спиннер */
  .spinner{
    display: none!important;
  }
  /* Ссылки ответов */
  .ABU-refmap {
    float: left!important;
    padding-left: 2em!important;
  }
  .ABU-refmap > em{
    display: none!important;
  }
  /* Оповещение */
  #ABU-alertbox{
    min-width: 200px!important;
  }
  /* Пост */
  #posts-form{
    max-width: 100%!important;
  }
  .reply{
    max-width: 80%!important;
  }
  /* Название треда */
  .post-title{
    display:inline-block;
    max-width: 650px;
    white-space: nowrap;
    overflow: hidden;
    margin-block-end: -3px!important;
  }
  /* Панель разметки текста */
  .toolbar-area {
    height: 22px!important;
  }
  /* Форма рисования */
  .oekaki-images-area{
    font-size: 9px!important;
  }
  /* E-Mail и Отправить */
  .qr-reply input.qsubmit {
    width:24%!important;
  }
  .qr-reply input.qmail {
    width:75.7%!important;
  }
  /* Форма вставки файлов */
  .form-files-drag-area,
  .form-files-drag-area hover{
    z-index: 2!important;
    width: 100%!important;
    height: 22px!important;
    font-size: 16px!important;
    padding-top: 0px!important;
    margin-block-start: 0px!important;
  }
  .images-area .drag-area.hover{
    background-color: #ADADAD!important;
  }
  /* Сколько файлов можно залить */
  .form-files-limits{
    z-index: 1!important;
    text-align: right!important;
    float: right!important;
    font-size: 9px!important;
    height: 9px!important;
    width: 120px!important;
    margin-block-start: -23px!important;
  }
  /* Кнопка Обзор */
  #formimages{
    z-index: 3!important;
    width: 65px!important;
    float: right!important;
    font-size: 12px!important;
  }
  /* Превью заливаемых картинок */
  .form-files-thumbnails {
    width: 58%!important;
    float: right!important;
  }
  .form-files-thumbnails > br{
    display: none!important;
  }
  .input-thumbnail-img{
    height: 100%!important;
  }
  .form-files-box .input-thumbnail-box {
    margin: 1px!important;
    vertical-align: bottom!important;
  }
  .input-thumbnail-meta {
    opacity: .7!important;
    background: none!important;
  }
  .input-thumbnail-size{
    color: black!important;
    text-shadow:
               -1px -1px 0 #fff,  
                1px -1px 0 #fff,
                -1px 1px 0 #fff,
                 1px 1px 0 #fff;
  }
  .input-thumbnail-delete{
    color: red!important;
    margin: 0px!important;
    text-shadow:
               -1px -1px 0 #fff,
                1px -1px 0 #fff,
                -1px 1px 0 #fff,
                 1px 1px 0 #fff;
  }
  /* Превью картинка */
  .images {
    margin-left: 10px!important;
  }
  .image {
    margin: 0px 3px!important;
  }
  .preview  {
    height: auto!important;
    min-height: 10px!important;
    max-height: 250px!important;
    width: auto!important;
    max-width: 250px!important;
  }
  .images-single{
    margin-right: 10px!important;
  }
  /* Открытая картинка */
  #fullscreen-container {
    z-index: 999!important;
    border: none!important;
    padding: 0px!important;
    background:none !important;
  }
  /* Форма ответа */
  #qr{
    z-index: 777!important;
    border: 1px grey solid!important;
  }
  #qr-shampoo{
    min-width: 100%!important;
    min-height: 100px!important;
  }
  /* Счетчик символов */
  .message-byte-len{
    pointer-events: none!important;
  }
  /* Капча */
  .qr-reply .captcha-image img {
    width: 42%!important;
    filter: invert(13%)!important;
  }
  /* Ввод капчи */
  .qr-reply #qr-captcha-value {
    width: 42%!important;
  }
  /* Вк */
  .qr-reply .qr-vk-public {display: none!important;}
  #plashque {display: none!important;}
  /* Топ тредов */
  #boardstats-box {
    z-index: 0!important;
    right: 35.5em!important;
  }
  .boardstats-title{
    display:inline-block!important;
    max-width: 150px!important;
  }
  .boardstats-views{
    float: right!important;
  }
  .update-stats-box,
  .update-stats-box-updating{
    position:fixed!important;
    margin-left: -10px!important;
  }
  /* Избранное */
  #favorites-box{
    z-index: 1!important;
  }
  #favorites-table{
    max-height: 209px!important;
    overflow: auto!important;
  }
  .fav-row-num,
  .qr-footer hr{
    display: none!important;
  }
  .bb.favorites {
    right: 10px!important;
  }
  .qr-footer hr, .bb hr {
    border-color: #777!important;
    margin: 0px!important;
  }
  .bb-footer > div{
    font-size: 12px!important;
  }
  .bb,
  .boardstats-box{
    min-width: 50px!important;
    margin-bottom: 0px!important;
    padding: 0px 0px 0px 5px!important;
    border: 1px gray solid!important;
    font-size: 12px!important;
  }
  .bb{
    opacity: 0.2!important;
    transition: 0ms!important;
  }
  .bb:hover{
    opacity: 0.9!important;
    transition: 0ms!important;
  }
  .bb-header-text-new {
    font-weight: unset!important;
  }
  .fav-row-title{
    display:inline-block;
    max-width: 250px;
    white-space: nowrap;
    overflow: hidden;
    text-decoration: underline!important;
    margin-block-end: -3px!important;
  }
  .fav-row-remove,
  .fav-row-newreplies,
  .fav-row-newposts{
    float: right!important;
  }
  /* Видео */
  video {
    height: 100%!important;
  }
  /*
  #fullscreen-container {
    width:auto!important;
  }
  */
  /* Реклама */
  .nb,
  .moneymoneymoney{display: none!important;}
}
