@-moz-document domain("www.rescuetime.com") {
HEADER NAV {
    background: #94dca0;
}

BODY.dashboard DIV#not-enough-time-logged-notice P {
    color: #fff;
}

DIV.report-actions-container UL LI.browse-action {
    margin: 0;
    padding: 0;
    list-style: none;
    background: #2699fb;
    border-radius: 3px;
