}

HEADER NAV UL.nav-list LI.expandable A#settings-toggle, HEADER NAV UL.nav-list LI.expandable A#notifications-toggle {
    background:  url(https://www.rescuetime.com/assets/icons/navigation/tools-white.svg) center no-repeat;}

FOOTER {
    padding-top: 10px;
    background-color: #45535d;
    background: -moz-linear-gradient(top, #607382 0%, #6d8394 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #607382), color-stop(100%, #6d8394));
