    background: -o-linear-gradient(top, #607382 0%, #6d8394 100%);
    background: -ms-linear-gradient(top, #607382 0%, #6d8394 100%);
    background: linear-gradient(to bottom, #607382 0%, #6d8394 100%);
        -webkit-box-shadow: inset 0px 10px 30px 0px rgba(50, 50, 50, 0.6);
    -moz-box-shadow: inset 0px 10px 30px 0px rgba(50, 50, 50, 0.6);
        box-shadow: inset 0px 10px 30px 0px rgba(50, 50, 50, 0.6);
    color: white;
}

SECTION#page-title-block DIV#page-nav-bar DIV#page-nav-tabs {
    padding-bottom: 13px;
}

DIV#browse-taxon-details {
    background: #6d6d6d;
    border-radius: 20px 20px 0px 0px;}

DIV#browse-taxon-details UL LI CANVAS {
    position: absolute;
    top: 4px;
    left: -24px;
    visibility: hidden;
}
DIV#browse-taxon-details UL LI {
    margin: 0 0 0 20%;
    padding: 0;
    list-style: none;
    font-size: 0.75em;
    line-height: 27px;
    position: relative;
    color: #0083f3;
    font-weight: 900;}

FOOTER A:hover {
    color: #a2d4ff !important;
    font-weight:200;
}

UL#top-report-navigation Li A#settings-account, UL#settings-navigation Li A#settings-account {
    fill: #606060 !important;
}

FOOTER A.footer-status-upgrade {
    display: none;}

DIV#report-details-premium-features-block {
display: none;
}

DIV#hidden-rows-toggle a{
    color:#fff !important;
}

DIV#hidden-rows-toggle a:hover{
