/*================ nav-bar ================*/
/*Remove Buttons in Urlbar or on Nav-bar*/
#identity-box,
#plugins-notification-icon,
#notification-popup-box,
#notification-anchor-icon,
#private-browsing-indicator,
#page-proxy-favicon,
#reader-mode-button,
#readinglist-addremove-button,
.autocomplete-history-dropmarker,
#feed-button,
#bookmarks-menu-button, 
#nav-bar-overflow-button,
#downloads-button,
#home-button, 
#pocket-button, 
#stylish-toolbar-button,  
#search-container {
    display: none !important;
} 

/*urlbar searchbar style*/
/*#searchbar > .searchbar-textbox,*/
#urlbar {
    border: none !important;
    border-radius: 0px;
    background: transparent !important;
    margin-top: 4px !important;
    /*padding: 0px !important;  */  
    height: 12px;
    font-family: "Century Gothic", "Microsoft YaHei UI", sans-serif !important;
    font-size: 12px;
    color: #aaaaaa !important;
    text-align: center !important;
}

#main-window[sizemode="normal"]:not([inFullscreen]) #urlbar {
    margin-top: 2px !important;
}
#main-window[sizemode="maximized"]:not([inFullscreen]) #urlbar {
    margin-top: 7px !important;
}
.urlbar-input-box { 
  border-inline-start: 1px solid transparent !important;
  border-image: none !important;
}
#nav-bar {
    background-color: #ffffff !important;
    height: 30px;
    border: 0 !important;
    margin: 1px 0 -3px -3px !important;
    position: fixed !important; top: 0 !important; left: 36px !important;
}

/*Stop-Reload-Go*/
#urlbar > toolbarbutton { 
    padding:  0 !important;
    border: none !important; 
}
#urlbar-reload-button {
    list-style-image: url('data:image/png;base64,') !important;
    -moz-image-region: rect(0px, 16px, 16px, 0px) !important;
    margin-top: 2px !important;
}
#urlbar-reload-button:not([disabled="true"]):hover {
    list-style-image: url('data:image/png;base64,') !important;
    -moz-image-region: rect(16px, 16px, 32px, 0px) !important;
}
#urlbar-stop-button {
    list-style-image: url('data:image/png;base64,') !important;
    -moz-image-region: rect(0px, 32px, 16px, 16px) !important;
    margin-top: 2px !important;
}
#urlbar-stop-button:hover {
    list-style-image: url('data:image/png;base64,') !important;
    -moz-image-region: rect(16px, 32px, 32px, 16px) !important;
    margin-top: 2px !important;
}
#urlbar-go-button {
    list-style-image: url('data:image/png;base64,') !important;
    -moz-image-region: rect(0px, 48px, 16px, 32px) !important;
    margin-top: 2px !important;
}
#urlbar-go-button:hover {
    list-style-image: url('data:image/png;base64,') !important;
    -moz-image-region: rect(16px, 48px, 32px, 32px) !important;
    margin-top: 2px !important;
} 

/*PanelUI-button*/
#nav-bar #PanelUI-menu-button {
    padding: 0 !important;
}
#PanelUI-button {
    -moz-box-ordinal-group: 0;
    background: url('http://i.imgur.com/Hef8ASW.png') center no-repeat !important;
    border: none !important;
    box-shadow: none !important;
    height:32px!important; width:32px!important;
    position:fixed; top: 0px; left:0px;
}
#PanelUI-menu-button {
    opacity: 0 !important;
}
#main-window[sizemode="normal"]:not([inFullscreen]) #PanelUI-button {
    margin-top: 1px !important;
}    
#main-window[sizemode="maximized"]:not([inFullscreen]) #PanelUI-button {
    margin-top: 7px !important;
}

/*================ TabsToolbar ================*/
/*Remove Australis Tab*/
#TabsToolbar:not(:-moz-lwtheme)::after,
#TabsToolbar:not(:-moz-lwtheme)::before,
.tab-background-start,
.tab-background-end {
    display: none !important;
}
.tab-background,
.tab-background-middle {
    margin: 0 !important;
    background: none !important;
}

#tabbrowser-tabs .scrollbutton-up,
#tabbrowser-tabs .scrollbutton-down {
    display: none !important;
} 

/*TABS*/
#TabsToolbar, #tabbrowser-tabs {
    margin-top: 1px !important;
    margin-left: 200px !important;
}
.tabbrowser-tabs:not([drag=detach]) > .tabbrowser-tab:not([pinned])[fadein] {
    min-width: 10px !important;
    max-width: 100px !important;
}
#main-window .tabbrowser-tab {
    height: 26px !important;
    min-height: 26px !important;
}
#TabsToolbar {
    margin-top: 0px !important;
    margin-bottom: 0px !important;
    padding-left: 0px !important;
}
.tabbrowser-tab:not([pinned]):not([selected]) .tab-throbber,
.tabbrowser-tab:not([pinned]):not([selected]) .tab-icon-image {
   visibility: collapse !important;
}

/* Standard Tabs */
#main-window .tabbrowser-tab {
    border: 1px solid transparent !important;
    font-family: "Century Gothic", "Microsoft YaHei UI", sans-serif !important;
    font-size: 12px;
    color: #aaaaaa !important;
    text-align: left !important;
}

/* Tab Hover */
#main-window .tabbrowser-tab:not([selected]):hover {
    background-color: #f6f6f6 !important;
    border-left: 1px #f6f6f6 !important;
    border-bottom: 1px solid #dddddd !important;
    padding-left: 1px !important;
    color: #6e6e6e !important;
    transition: .2s linear !important;
}

/* Selected Tab */
#main-window .tabbrowser-tab[selected] {
    background-color: #dddddd !important;
    border-bottom-color: #6e6e6e !important;
    color: #393939 !important;
    transition: .2s linear !important;
}

/* Pinned tab titlechange */
.tabbrowser-tab[titlechanged] .tab-icon-image {
    animation: newpost .9s ease; 
}

@keyframes newpost {
    25% {  transform: scale(1.1);   }
}

/*================ combined favicon and close ================*/
/*box-ordinal-group*/
.tabbrowser-tab[selected="true"] .tab-icon-image,
.tabbrowser-tab[selected="true"] .tab-throbber {
    -moz-box-ordinal-group: 2 !important;
}
.tabbrowser-tab[selected="true"] .tab-label-container {
    -moz-box-ordinal-group: 3 !important;
}
.tabbrowser-tab[selected="true"] .tab-icon-sound {
    -moz-box-ordinal-group: 4 !important;
}
/*.tabbrowser-tab[selected="true"] .tab-counter {
    -moz-box-ordinal-group: 5 !important;
}*/

/* Always show a favicon, unless there's a throbber or close button */
/* (sometimes the New Tab tab shows as progress="true", not sure why) */
.tabbrowser-tab:not([busy="true"]):not([progress="true"]):not(:hover) .tab-icon-image,
.tabbrowser-tab:not([busy="true"]):not([progress="true"]):not(:hover) .tab-icon-image:not([src]):not([pinned]):not([crashed]),
.tabbrowser-tab[label="New Tab"]:not([busy="true"]):not(:hover) .tab-icon-image {
	display: block !important;
}

/* Hide the close button initially */
.tabbrowser-tab[selected="true"] .tab-close-button {
	display: none !important;
    margin-inline-start: -4px !important;
}

/*tab-close-button*/
.tabbrowser-tab[selected]:hover .tab-close-button {
    list-style-image: url('data:image/png;base64,') !important;
    -moz-image-region: rect(0, 16px, 16px, 0);
}
.tabbrowser-tab:not([selected]):hover .tab-icon-sound {
    display: none !important;
}
.tabbrowser-tab:not([selected]):hover .tab-close-button {
    list-style-image: url('data:image/png;base64,') !important;
    -moz-image-region: rect(0, 64px, 16px, 48px);
}
.tabbrowser-tab[selected]:hover .tab-close-button:hover,
.tabbrowser-tab:not([selected]):hover .tab-close-button:hover {
    list-style-image: url('data:image/png;base64,') !important;
    -moz-image-region: rect(0, 32px, 16px, 16px);
}
.tabbrowser-tab[selected]:hover .tab-close-button:active,
.tabbrowser-tab:not([selected]):hover .tab-close-button:active {
    list-style-image: url('data:image/png;base64,') !important;
    -moz-image-region: rect(0, 48px, 16px, 32px);
}
.tabbrowser-tab[selected]:hover .tab-text {
    margin-inline-start: 8px !important;
}

/* Show the close button and hide the throbber/favicon on tab hover */
.tabbrowser-tab:hover .tab-close-button {
	display: block !important;
}
.tabbrowser-tab:hover .tab-icon-image,
.tabbrowser-tab:hover .tab-throbber {
	display: none !important;
}

/* Pinned tabs should not behave like regular tabs, let them stay normal */
.tabbrowser-tab[pinned]:hover .tab-close-button {
	display: none !important;
}
.tabbrowser-tab[pinned]:hover .tab-icon-image {
	display: block !important;
}

/* Add extra space to the right of the tab to keep the text centred */
.tabbrowser-tab:not([pinned]):not([group^="{"]):not([soundplaying="true"]) .tab-text {
	margin-inline-end: 4px !important; /* 16px icon width + 4px icon margin */
}

/* new tab button */
#TabsToolbar .tabs-newtab-button {
    list-style-image: url('data:image/png;base64,') !important;
    -moz-image-region: rect(0, 16px, 16px, 0px);
    margin: 0 0 0 4px !important;
    width: auto !important;
}
#TabsToolbar .tabs-newtab-button:hover {
    list-style-image: url('data:image/png;base64,') !important;
    -moz-image-region: rect(0, 32px, 16px, 16px);
    background-color: #f6f6f6 !important;
    border-bottom: 1px solid #dddddd !important;
    box-shadow: none !important;
}
#TabsToolbar .tabs-newtab-button:active {
    list-style-image: url('data:image/png;base64,') !important;
    -moz-image-region: rect(0, 48px, 16px, 32px);
    background-color: #f6f6f6 !important;
    border-bottom: 1px solid #dddddd !important;
}

/* remove tab scrolling */
.scrollbutton-up, .scrollbutton-down, #alltabs-button {
    display: none !important;
}
scrollbar[orient="vertical"] {
    min-width: 16px !important;
    max-width: 16px !important;
    margin-left: -1px !important;
}
scrollbar[orient="horizontal"] {
    min-height: 16px !important;
    max-height: 16px !important;
    margin-top: -1px !important;
}


/*================ main-window ================*/
#main-window[sizemode="normal"]:not([inFullscreen]) {
    margin: 0 !important;
    background-color: #ffffff !important;
}
#main-window[sizemode="maximized"]:not([inFullscreen]) {
    margin: -2px 0 0 0 !important;
    background-color: #FFFFFF !important;
}

/*================ addon-bar ================*/
statusbarpanel,
#addon-bar {
    display: none !important;
}

 }
