/* ---------- NextInpact_Epuré ---------- */

/* ---------- v1.0 / 26-04-2017 ---------- */

/* by woam */



@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.nextinpact.com") {

	


/* Suppression espace entre la barre de navigation et l'article */
#container_ban_pub_jauge {
	display:none!important;
	}


	/* Couleurs Titres Articles Page Principale Liste Illustrée */
	.compact-item .title_area > h2 > a {
	color: #D74D0B!important;
	}
	/* Couleurs Titres Articles Page Principale Liste Illustrée : Survolés */
	.compact-item .title_area > h2 > a:hover {
	color: #2595CB!important;
	}
	/* Couleurs Titres Articles Page Principale Liste Compacte */
	.color_title a {
	color: #D74D0B!important;
	}
	/* Couleurs Titres Articles Page Principale Liste Compacte : Survolés */
	.color_title a:hover {
	color: #2595CB!important;
	}
 /* Couleur Texte Barre Navigation */
	ul#menu li a, ul#responsive_menu li, ul#responsive_menu li a {
	color:#FF8000!important;
	}
	 /* Couleur Texte Barre Navigation : Survolé*/
	ul#menu li a:hover, ul#responsive_menu li:hover, ul#responsive_menu li a:hover {
	color:#01384B!important;
	}
	
	/* Couleur pseudo membre */
	.color_text_socialbar {
	color:#FF8000!important;
	}
	
/* Couleur Bouton "Abonnez-vous" */
#btn_action_toolbar .top-abo {
	display:none!important;
	background-color: #076381;
	}
	            /* Suppression Bouton "Abonnez-vous" */
		                    .btn_abonne_right_encart {
		                           display:none!important;
		                                                     }
	
/* Couleur étiquette Abonnement */
	.btn_abonne_right_encart {
	color: #11BAF2;
	background-color: #076381;
	}
	.txtBtnJauge, .txtBtnJauge:hover {
	color: #11BAF2;
	}
		
	            /* Suppression étiquette Abonnement */
	                     .leftSideNbAbonne {
	                           display:none;
	                                                      }
	
	/* Suppression Bandeau "A Ne Pas Manquer" */
	#container_hot_now {
	display:none!important;
	}
	
	/* Couleur Bouton catégories sur l'Image */
	#blog-btn.active-cat, #blog-btn:hover, .next-inpact-bgcolor {
	background-color: #076381 !important;
	}

	/* Couleur Rédacteur Article */
	#infos a {
	color: #F2F200;
	}
	
	/* Couleur "En Continu" */
	.on_the_side_section h2, .on_the_side_responsive_section h2 {
	color: #3CA7DB;
	}	
	
	/* Couleur Texte News "En Continu" */
	.text_in_continue h3 a {
	color: #B7B7B5;
	}
		
	/* Couleur Texte News "En Continu" : Survolé */
	.text_in_continue h3 a:hover {
	color: #2595CB;
	}
		
/* Couleur Texte articles */
	.article_content p, .article_content {
	color: #949494!important;
	}

/* Couleur Liens */
.article_content p a, .article_content a {
	color: #BD6A0F;
	}	

/* Couleur Liens survolés */
.article_content p a:hover, .article_content a:hover {
	color: #2595CB;
	}		
	
	/* Couleur Pseudos Commentaires */
	.username_comment {
	color: #2595CB;
	}
	/* Couleur Pseudos Commentaires : Survolés */
	.username_comment:hover {
	color: #D74D0B;
	}
	
	
	/* Couleur Texte Commentaires */
	.text_comment {
	color: #BFBFBD;
	}
	
	/* Couleur Liens Commentaires */
	.text_comment a, #textarea_comment a, #textarea_edit_content a {
	color: #9A8D4C;
	}
	
	/* Couleur Titre Article */
	.color_title {
	color: #D74D0B;
	}
	
	/* Couleur sous-titres */
	.soustitre_article .sub_title {
	color: #98984E;
	}
	
  /* Suppression de la loupe	
.search_btn .icon-search {
	display:none!important;
	}	
	*/

	/* Couleur Titres Chapitres */
	.article_content h3 {
	color: #7A7A3F;
	}

	/* Couleur Fond bandeau "En Continu" */
	.list_article_side {
	background-color: #131618 !important;
	}
	
	/* Bulle Une Erreur */
	.tooltip_error {
	font-size: 13px;
	color: #fff !important;
	background-color: #000;
	opacity: .75;
	}
	
	/* Couleur Dates Page Principale */
	.compact_mode .date_header_actu {
	color: #FF8000;
	}
		
	/* Suppression Titre Carroussel */
	.carousel-title, .carousel-container .carousel-ico {
	display:none!important;
	}
	
	/* Couleur ou Suppression Carroussel */
	.carrousel_section, #carousel-dossier, #carousel-bp {
	background-color: #0F1314;
		/* Pour supprimer le Carroussel, effacer les /* autour de la ligne suivante : */
		/*display:none!important;*/
	}
	
	
	/* Suppression Bandeaux Abonnement & Réseaux sociaux - Bas de page principale */
	section#wide-tlf-section + section#bandeau_abonnez_vous, #social_network_nxi_section {
	display:none!important;
	}
	
	/* Suppression Bas de Page */
	footer {
	display:none!important;
		}
	
	/* Suppression Bas de page Abonnement Articles Premium */
	.premium-footer {
	display:none!important;
	}

	/* Couleurs Bons Plans */
	.recap_bonplan_content h3, .recap_bonplan_content a {
	color: #9E9E9E;
	}
	.recap_bonplan_content .strip_title {
	background-color: #7A7A3F;
	}
	/* Couleur texte Liste Bons Plans */
	div.selector, div.selector *, div.radio, div.radio *, div.checker, div.checker *, div.uploader, div.uploader *, div.button, div.button * {
	color:#9F9F9F!important;
	}
	/* Couleur Bouton sélection Bons Plans - Date/popularité */
	#bonplan_entete .ui-radio label.ui-radio-on {
	color: #fff;
	background-color: #D74D0B;
	}
	
	
	
	
	
	
	
	
/* ----------------------------- END --------------------------- */  
}
