css:
@-moz-document domain("mstdn.kemono-friends.info") {
/*
 * Rounded Mplus 1c (Japanese) https://fonts.google.com/earlyaccess
 */
@font-face {
    font-family: 'Rounded Mplus 1c';
    font-style: normal;
    font-weight: 400;
    src: url(https://fonts.gstatic.com/ea/roundedmplus1c/v1/RoundedMplus1c-Regular.eot);
    src: url(https://fonts.gstatic.com/ea/roundedmplus1c/v1/RoundedMplus1c-Regular.eot?#iefix) format('embedded-opentype'), url(https://fonts.gstatic.com/ea/roundedmplus1c/v1/RoundedMplus1c-Regular.woff2) format('woff2'), url(https://fonts.gstatic.com/ea/roundedmplus1c/v1/RoundedMplus1c-Regular.woff) format('woff'), url(https://fonts.gstatic.com/ea/roundedmplus1c/v1/RoundedMplus1c-Regular.ttf) format('truetype');
}

@font-face {
    font-family: 'Rounded Mplus 1c';
    font-style: normal;
    font-weight: 700;
    src: url(https://fonts.gstatic.com/ea/roundedmplus1c/v1/RoundedMplus1c-Bold.eot);
    src: url(https://fonts.gstatic.com/ea/roundedmplus1c/v1/RoundedMplus1c-Bold.eot?#iefix) format('embedded-opentype'), url(https://fonts.gstatic.com/ea/roundedmplus1c/v1/RoundedMplus1c-Bold.woff2) format('woff2'), url(https://fonts.gstatic.com/ea/roundedmplus1c/v1/RoundedMplus1c-Bold.woff) format('woff'), url(https://fonts.gstatic.com/ea/roundedmplus1c/v1/RoundedMplus1c-Bold.ttf) format('truetype');
}

@media screen and (-webkit-min-device-pixel-ratio: 0) {
    @font-face {
        font-family: "Rounded Mplus 1c", -apple-system, BlinkMacSystemFont, "Helvetica Neue", "Yu Gothic", YuGothic, Verdana, Meiryo, "M+ 1p", sans-serif !important;
    }
}

body {
    font-family: "Rounded Mplus 1c", -apple-system, BlinkMacSystemFont, "Helvetica Neue", "Yu Gothic", YuGothic, Verdana, Meiryo, "M+ 1p", sans-serif !important;
    font-style: normal;
    font-weight: normal;
}

/* 全体の背景色 */
body,
body.admin,
body.app-body,
.tabs-bar,
.ui {
    background-color: #333300;
}
.admin-wrapper .sidebar-wrapper {
    background-color: #224422;
}
/* ログインページ */

.flash-message {
    color: #090;
}
/* ドロワー */

.drawer__header {
    background-color: #224422;
}
.drawer__tab {
    color: #669933;
}

/* 検索ボックス */
.search__input {
    color: #ffffff;
    background-color: #331100;
}
.search__input:focus {
    background-color: #442211;
}

/*
　　　投稿フォーム
　　　--------------------------------------------------
*/

.drawer__inner {
    background-color: #553322;
}

/* 「プロフィールを編集」　*/
.navigation-bar {
    color: #996633;
}

/* カメラアイコン、公開範囲、NSFW, CW アイコン　*/
.icon-button.inverted,
.text-icon-button {
    color: #006600;
}
.icon-button.inverted:hover,
.icon-button.inverted:active,
.icon-button.inverted:focus,
.text-icon-button:hover,
.text-icon-button:active,
.text-icon-button:focus {
    color: #339933;
}

/* トゥート！ の入力欄 */
.autosuggest-textarea__textarea,
.spolider-input-__input {
    color: #336633;
}

/* リプライ表示欄 */
.reply-indicator {
    background-color: #f8f3cc;
}

/*
　　各カラム
　　--------------------------------------------------
*/
.column>.scrollable {
    color: #003300;
    background: #f8f4cc;
}
/* カラムのヘッダ */
.column-header {
    font-size: 1.25rem;
    font-weight: bold;
}
.column-header,
.column-icon,
.collapsable-collapsed,
.column-back-button {
    background-color: #336633;
    color: #99cc66;
}
.column-back-button {
    color: white;
}
.column-icon {
    color: #669933;
}
.column-header.active .fa {
    color: white;
    text-shadow: 0 0 10px white;
}
/* 各カラムの設定 */
.column-settings__outer {
    background-color: #ccee99;
}
.column-settings__section {
    font-size: 1rem;
    font-weight: bold;
    color: #339933;
}
.setting-toggle {
    color: #336633;
}
/*
　　トゥート詳細
　　--------------------------------------------------
*/
.detailed-status {
    background-color: white;
}
/* ユーザー表示名 */
.detailed-status__display-name strong {
    font-weight: bold;
    color: #669933;
}
/* @ユーザー名 */

.detailed-status__display-name {
    color: #99cc33;
}
/* CW フラグ */
.muted a.status__content__spoiler-link,
.reply-indicator__content .status__content__spoiler-link,
.status__content .status__content__spoiler-link {
    background: #d5e9bd;
    color: #669933;
}
.muted a.status__content__spoiler-link:hover,
.reply-indicator__content .status__content__spoiler-link:hover ,
.status__content .status__content__spoiler-link:hover {
    background: #dcedc8;
    text-decoration: none;
}

/* リンク */
.status__content a,
.reply-indicator__content a {
    color: #009900;
}
/* OGP */

.status-card__title,
.status-card__description {
    color: #339933;
}
/* リアクション */

.detailed-status__action-bar {
    background-color: white;
}
/*
  スタート カラム
  --------------------------------------------------
*/
.getting-started p {
    color: #336633;
}

.getting-started a {
    color: #669933;
}
.column-subheading {
    font-size: 1.125rem;
    font-weight: bold;
    color: #CCFF99;
    background-color: #339933;
}

/*
　　ホームタイムライン
　　--------------------------------------------------
*/

/*「 〇〇さんにブーストされました」 */
.status__prepend,
.status__prepend .status__display-name strong {
    color: #669933;
}

/* ユーザー名 */
.status__display-name,
.status__display-name strong,
.account__display-name strong {
    color: #006600;
    font-weight: bold;
}

/* 「○秒前」　*/
.status__relative-time {
    color: #99cc33;
}

/* 投稿ごとに付いたアイコン */
.icon-button {
    color: #99cc33;
}
.icon-button:hover,
.icon-button:focus,
.icon-button:active {
    color: #bbee55;
}

/* 通知欄のメッセージ */
.notification__message,
.notification__display-name:hover {
    color: #006600;
}

/*　通知欄の投稿抜粋 */
.muted .status__display-name strong,
.muted .status__content p,
.muted .status__content a {
    color: #669933;
}

/* @ユーザー名@インスタンス */
.account .account__display-name {
    color: #669933;
}

/* アカウントプロフィール */
.account__header {
    background-color: #223322;
}
.account__header>div {
    background-color: rgba(34, 51, 34, 0.5);
    /* #223322 の別表現　*/
}
.account__action-bar__tab>span {
    color: #669933;
}
.account__action-bar__tab strong {
    color: #336633;
}

/* about page */
.sidebar-layout .panel .panel-header {
    color: #99CC66;
}

/*
　　　スクロールバー
　　　--------------------------------------------------
*/
/* スクロールバーつまみ */
::-webkit-scrollbar-thumb {
    background-color: #CCDD88;
}
::-webkit-scrollbar-thumb:hover {
    background-color: #99CC66;
}

/* スクロールバー背景 */
::-webkit-scrollbar-track {
    background-color: rgba(0, 0, 0, 0.1);
}
::-webkit-scrollbar-track:active,
::-webkit-scrollbar-track:hover {
    background-color: rgba(0, 0, 0, 0.2);
}

}
