css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.nicovideo.jp") {
.WatchAppContainer-main{
	width:100%;
	height: 720px;/*add*/
}
.MainContainer {
	height: 757px;/*add*/
}
.VideoContainer {
	position: relative;
	width: 1280px !important;
	height: 720px !important;
}
.HeaderContainer {
	width:90% !important;
	position: absolute;
	top:800px;/*old:760px*/
    margin:32px;
}
.BottomContainer,.BottomContainer-main,.BottomContainer-side,.NicoSpotAdContainer,.ContextMenuContainer,.FacebookSDKLoader,.MiniBannerAdContainer,
.PlayerSkipNextButton{
	display:none !important;
}
.CommentPanelContainer{
	bottom:0px !important;
}
.FooterContainer{
margin-top:800px;
}
.VideoDescriptionContainer{
	width:1280px !important;
    margin:16px;
}
.MainContainer-player {
	width: 1280px !important;
	height: 720px;/*add*/
}
.MainContainer-commentPanel,.CommentPanelContainer,element,.react-grid-Container{
	width:calc(100vw - 1297px) !important;/*100% - player's 1280px - scrollar's 17px*/
	background:#000 !important;
}
.react-grid-HeaderCell{
	background:#000 !important;
}
.react-grid-HeaderRow{
    border-bottom:1px #fff solid;
}
.CommentPanel,.react-grid-Main{
	color: rgb(255, 255, 255) !important;
}
.MarqueeContainer {
 	height: 0px !important;
}
.WatchAppContainer-switchToFlash{
	width:1280px;
	height:30px;
	line-height: 30px;
	position: absolute;
	z-index:2;
	opacity: 0;
	-moz-transition: 0.8s;
	-webkit-transition: 0.8s;
}
.WatchAppContainer-switchToFlash:hover{
	opacity: 0.7;
}
.ControllerContainer{
	background: #000;
	position: absolute;
	z-index: 2;
	bottom: 38px;
	opacity: 0;
	-moz-transition: 0.8s;
	-webkit-transition: 0.8s;
}
.ControllerContainer:hover{
	opacity: 0.7;
}
body{
	overflow-x:hidden;
    overflow-y: auto;
}
.WatchAppContainer{
	background: #000;
}
.VideoTitle{
	color: #eee;
    margin-left:32px;
}
input{
	background: #000;
	color:#fff;
}
.CommentPostContainer-commandInput,.CommentPostContainer-commentInput,.CommentInput,textarea {
    background-color: rgb(0, 0, 0) !important;
    color:#fff !important;
}
.ControllerBoxContainer{
border-bottom:1px solid #eee !important;
}
.MiniBannerAd{
    height:0px;
}
.VideoDescription-html{
    color:#aaa !important;
}
.VideoDescriptionExpander .VideoDescriptionExpander-switchExpand {
    padding: 2px 16px;
    border-radius: 2px;
    color: #57f;
}
.SeekBarContainer {
    position: relative;
    padding-top: 0px;
    padding-left: 0px;
    padding-right: 0px;
    padding-bottom: 1px;
}
.react-grid-Cell:hover{
	background: #fff !important;
	color:#000 !important;
}
}
