css:
@-moz-document domain(ono.unity3d.com) {
table.code-difftable .add pre {
    background-color: #99FF99 !important;
}
table.code-difftable .add ins {
    background-color: #66FF66;
}
table.code-difftable .del pre {
    background-color: #FF9999;
}
table.code-difftable .del del {
    background-color: #FF6666;
}
.inline-comments .comment .comment-wrapp {
    border-color: #b562bd;
    border-width: 4px;
}
.firstlink a,
div.comment-prev-next-links div.prev-comment a,
div.comment-prev-next-links div.next-comment a {
    display: inline-block;
    padding: 3px;
    border: 4px #21bed2 ridge;
    border-radius: 10px;
    background-color: #b5dde2;
}
/* Always show complete commit messages */
#content div.panel div.message {
    height: auto;
    overflow: initial;
    margin: 8px 0;
}
#graph_content_pr .compare_view_commits .expand_commit, #graph_content .container .expand_commit {
    visibility: hidden;
    width: 0px !important;
    padding: 0px !important;
    margin: 0px !important;
}
}
