css:
@-moz-document domain("userstyles.org") {
/*[[theme-d-l]]*/


.style_card  {
	box-shadow: none !important;
}


/*  BG1  */
body, #nav-view, #navigation, #landing_page_div .top_landing_page_div, #nav-view-responsive {
	background: var(--bg1) !important;
}

/*  BG2  */
.style_card, #footer, #style_installed_button, .edit-setting, .advancedsettings_shown, .us-stylecard--short, .us-stylecard--long {
	background: var(--bg2) !important;
}

/*  BG3  */
.navigation_link:hover, #header, #Head, #landing_page_div .bottom_landing_page_div, .navigation {
	background: var(--bg3) !important;
}

/*  BG4  */
.navigation .links-box .navigation-link:hover, .contact-input, .contact-input-large, #login-existing, #password-existing, #password, #password_confirmation, .Info, ul.token-input-list, .TextBoxWrapper textarea, .TextBoxWrapper input:not([type="radio"]):not([type="checkbox"]), .header-search .search_input {
    background: var(--bg4);
}
/* BG5 */
.header-search .search_input.focused {
    background: var(--bg5);
}
/* BG7 */
li.token-input-token, div.token-input-dropdown, div.token-input-dropdown *, .header-search .type_search {
    background: var(--bg7) !important;
}


/*  COLOR1 (#eee)  */
h4 a, h4, .navigation_title, #copyrights, h1, h4 a:visited, #information_value, #share_div, .title_setting, .upload_image_label, #landing_page_div .top_landing_page_div .coming_soon_div .coming_soon_subtitle, #title_long, #title_long a, .style_author_long,  .SiteTitle a, #Head a, #title_long a:visited, .navigation .links-box .navigation-link .navigation-title, .navigation .navigation-category, .contact-bold-font, #styles-writing, #styles-coding, #styles-installing, .styles-help, #input-fields label, .contact-input, .contact-input-large, #site-uploading, #site-search, .PageContent b, .PageContent h1, .PageContent i, .PageContent p, .PageContent h2, .PageContent strong, .PageContent font, #login-existing, #password-existing, #password, #password_confirmation, .TextBoxWrapper textarea, .TextBoxWrapper input:not([type="radio"]):not([type="checkbox"]), li.token-input-token, .header-search .search_input .input_text_search {
	color: var(--color1) !important;
}

/*  COLOR2 (#ccc)  */
#headline, #information_key, .text_style_page, #applies_link, #landing_page_div .bottom_landing_page_div .android_feature_div .android_feature_text div, h4#title_long a {
	color: var(--color2) !important;
}

/*  COLOR3 (#aaa)  */
#information_key_left, #infomation_value_left, #landing_page_div .top_landing_page_div .coming_soon_div .coming_soon_text, #landing_page_div .top_landing_page_div .coming_soon_div .coming_soon_helpus {
	color: var(--color3) !important;
}

/*  BLUE  */
h4 a:hover, a {
	color: var(--blue) !important;
}





/*  BUTTONS  */
.see_more, .div-arrow, #css_button, #send_feedback_button, .white_button, .css_button, .advanced_button, .customize_button {
	background: var(--bg3) !important; color: var(--color1) !important; border: transparent !important; transition: 0.2s ease !important;
}
.see_more:hover, .div-arrow:hover, #css_button:hover, #send_feedback_button:hover, .white_button:hover, .css_button:hover, .advanced_button:hover, .customize_button:hover {
	background: var(--bg4) !important; color: var(--color1) !important; border: transparent !important;
}




/*  BORDER TRANSPARENT  */
.screenshot_wrapper, .contact-input, .contact-input-large, #login-existing, #password-existing, #password, #password_confirmation, ul.token-input-list, li.token-input-token, div.token-input-dropdown, .TextBoxWrapper {
	border-color: transparent !important;
}




/*  SEPERATE RULES  */
body {
	color: var(--color2) !important;
}
.linksBox .navigation_link:last-child, #single_discussion {
	box-shadow: none;
    border-bottom: 1px solid var(--bg5);
}
#archive_style_button {
	box-shadow: none !important;
}
#archive_style_button:hover {
	background: #c14430; color: #fff;
}
#install_style_button {
	box-shadow: none;
}
#install_style_icon {
	margin-top: 5px;
}
#create_new_style:hover {
	background: var(--blue) !important; 
	border-color: var(--blue) !important;
}
#create_new_style a, h4 a {
	transition: 0.2s ease; 
	-webkit-transition: 0.2s ease;
}
#create_new_style:hover span {
	color: var(--color1) !important;
}
.form-controls input[type=text], .form-controls textarea, .form-controls input[type=url], #edit-style-settings input:not([type]), #edit-style-settings input[type="text"], .form-controls input[type=button] {
	background: var(--bg4) !important; 
	border: 1px solid #444 !important; 
	color: var(--color1) !important; 
	box-sizing: border-box;
}
#style-settings select {
	background: var(--bg4);
	border: none;
	color: #fff;
	padding: 5px
}
#style-settings select option {
	padding: 2px 0;
}
.option-user-url {
	background: var(--bg4);
	border: none;
	color: #fff;
}
#navigation .linksBox:first-child .logo_navigation_link, #navigation .linksBox:last-child .logo_navigation_link, .MeButton span, .label_image > img:first-child, #navigation .linksBox:nth-child(5) .navigation_link img, .navigation .links-box .navigation-link .logo-navigation-link {
	filter: invert(1);
	-webkit-filter: invert(1);
}
#overlay-info {
	background: var(--bg1);
	border-radius: 0 !important;
}
.style_card_long, .line_seperator {
	box-shadow: inset 0 -1px 0 0 var(--bg4)
}
.view_details {
	background: var(--bg3) !important; 
	color: var(--color1) !important; 
	border: transparent !important;
}
#title_long:hover, #title_long:hover a {
	color: var(--blue) !important;
}
.Item.Read {
	background: var(--bg2);
}
#style_installed_button {
	border: none;
	padding: 0 5px;
	box-shadow: inset 0 0 0 2px #2ecc71;
}
pre {
	background: var(--bg3);
	border-color: var(--bg4) !important;
}
code {
	color: var(--color1);
	background: var(--bg3);
    border-color: var(--bg4) !important;
}
.Button {
	background: var(--bg4);
	text-shadow: none;
	box-shadow: none;
	border: 2px solid var(--bg4);
}
.Button:hover {
	background: var(--bg7);
	border-color: var(--bg7);
}
#Head .SiteSearch .InputBox, .EditCommentForm textarea.TextBox, .CommentForm textarea.TextBox {
	background: var(--bg2);
	color: var(--color1);
}
.SiteSearch .Button, .OptionsTitle {
	filter: invert(1);
	-webkit-filter: invert(1);
}
#Body {
	padding-top: 25px;
}
	.style_category_card_wrapper {
	background: var(--bg3);
	box-shadow: none;
}
.style_category_card_wrapper:hover {
	box-shadow: none;
}
.style_category_card_wrapper #category_style_card_title {
	color: var(--blue);
}
.setting_div {
	width: 50%;
	margin-right: 0;
	margin-bottom: 10px;
}
.errorExplanation {
	background: transparent;
	border: 3px solid red;
	border-radius: 5px;
}
#stylish-code {
	font-family: 'Courier', 'Courier New', monospace;
	height: 500px;
	background: var(--bg2);
	border: none;
	padding: 10px;
	box-sizing: border-box;
	font-weight: bold;
}
.Button, .DiscussionForm .PostOptions label {
	color: #ddd;
}
.Button:hover {
    color: #fff;
}
.DiscussionForm .Category, .DiscussionForm input.InputBox, .DiscussionForm textarea {
    background: var(--bg2);
    color: #eee;
}
.text_setting_input {
    background: var(--bg4) !important;
    border-color: var(--bg4) !important;
    color: var(--color1) !important;
}
#landing_page_div .top_landing_page_div .coming_soon_div .coming_soon_helpus {
    width: 100%;
}
#top_android_button {
    background: var(--bg5);
    box-shadow: none !important;
    width: 46px !important;
}
#top_android_button:hover {
    background: var(--bg6);
}
#top_android_button .android_button_text {
    display: none;
}
#search_input {
    background: var(--bg5) !important;
}
#search_input:hover {
    background: var(--bg7) !important;
}
#search_input input {
    color: #fff !important;
}
#landing_page_div .top_landing_page_div .coming_soon_div .coming_soon_seperator {
    border-bottom-color: var(--bg7);
}
.adLong, .style_card_long:empty, .catalogBanner.box, .navigation_ad, .adContainer {
    display: none;
}
#title_div {
    box-shadow: inset 0 -1px 0 0 var(--bg7);
}
.advancedsettings_shown {
    max-width: 668px;
}
.style_card_long:nth-child(3), .style_card_long:nth-child(13) {
	display: none;
}
.statistic table, th, td {
	border-color: var(--bg6);
	padding: 5px;
}
.statistic table a, th a, td a {
	text-decoration: none;
}
td.obsolete, td.obsolete ~ td, .navigation-ad {
	display: none;
}
.author-styles tbody tr:nth-child(even) {
	background: var(--bg2);
}
.us-stylecard--short, .us-stylecard--short .us-stencil {
    box-shadow: none;
    border: none;
}
.navigation {
    padding: 68px 0 0 0;
    z-index: 10;
    margin: 0;
}
#header {
    z-index: 12;
}
.navigation .navigation-category {
    padding-left: 10px;
}
.us-category--stylecard-short {
    transition: 0.2s ease;
}
#footer {
    padding-left: 200px;
    box-sizing: border-box;
}
.navigation .links-box {
    box-shadow: 0 -1px 0 0 var(--bg5), 0 1px 0 0 var(--bg5);
}
.navigation .links-box .navigation-link {
    box-shadow: 0 -1px 0 0 var(--bg5);
}
.navigation .links-box:nth-child(3) .logo-navigation-link {
    filter: invert(0);
}







/***** SETTINGS *****/
/*[[hide-footer-y-n]]*/
#notes_textarea {
	background: var(--bg1) !important; 
	color: var(--color2) !important;
	height: /*[[notes]]*/px;
	max-height: /*[[notes]]*/px;
}
}
