css:
@-moz-document domain("www.politico.com") {
:root {
    --color-font: #eee;
   --color-borders: #666;
}

body {
    color: var(--color-font); 
    background-color: #393939 !important;
}

.pop-up-bar {
    background-color: #333;
}

.media-item__summary.size--small {
    background-color: #393939;
}

#onetrust-banner-sdk {
    display: none;
}

.story-text__heading-small, .story-text__heading-medium, .story-text__heading-large {
    color: #eee;
}

p,
a,
a:hover,
.headline,
.content-heading,
.story-intro .art figcaption p,
.story-frag header h3 a,
.js-tealium-tracking,
.story-intro,
.summary,
.subhead,
.qc-cmp-title, 
.qc-cmp-beta-messaging, 
.qc-cmp-main-messaging, 
.qc-cmp-messaging,
.master-menu__content a,
.widget-header h2 .icon-magazine-small, 
.widget-header h2 a:hover .icon-magazine-small, 
.widget-header h2 a:focus .icon-magazine-small,
.spotlight .summary h2,
.speedbump p, 
.story-intro .meta p,
.story-intro .meta p a,
.PibTypography-container h2.PibTypography-text,
.dek,
.PibTypography-text,
.story-tags__list-item.title-tag {
	color : var(--color-font) !important;   
}

.header,
.qc-cmp-ui,
.slide-search__content,
.header.bgc--politico-red73 .slide-search__content {
	background: #333 !important;
    color : var(--color-font);
}
.page-header > * {
    background: transparent;
}

.bt-icon,
.header.bgc--politico-red73 .header__branding b {
    color : red;
}

.hamburger {
    background: transparent;
    filter: invert(1);
}
.parent-flex {
    background-color: transparent;
}

.page-content__row,
.super,
.current,
.widget.caste-exclusive .widget-header, 
.story-interrupt.format-s.pos-right aside.widget,
.widget-header, 
.widget.caste-exclusive .widget-content, 
.story-interrupt.format-s.pos-right aside.widget .widget-content, 
.widget.caste-exclusive .widget-footer, 
.story-interrupt.format-s.pos-right aside.widget,
.widget-footer,
#sticky-nav,
.vaccine-parent-flex,
.fifty-parent-flex {
	color: var(--color-font) !important;
    background: #393939 !important;
}

.speedbump p,
.story-intro .meta p {
	color : #aaa !important;
}

.user-tools,
.tools-list li a .icon-text {
	color : var(--color-font) !important;
    background: #393939 !important;
}

.master-menu__content,
.slider__card, 
.media-quote,
.categories-list li a,
input, 
select, 
textarea{
	background: #393939;   
	color: var(--color-font);
}

.widget-nav-list li a {
	background: #393939;
}

.slide-search__content.is-active {
	background: #555;
}

.widget-nav-list li a,
.widget.caste-exclusive .widget-header, 
.story-interrupt.format-s.pos-right aside.widget .widget-header,
.widget.caste-exclusive .widget-content, 
.story-interrupt.format-s.pos-right aside.widget .widget-content,
.widget.caste-exclusive .widget-footer,
.story-interrupt.format-s.pos-right aside.widget .widget-footer {
    border: 1px solid var(--color-borders);
}


.super + .super::before, 
.super.contrast-high:first-child::before, 
.super.contrast-medium:first-child::before, 
.content-groupset + .content-groupset,
.story-related,
.story-supplement,
.story-frag-list.layout-linear > li + li, 
.story-interrupt.format-s.pos-right ul.story-list > li + li, 
.story-list.format-xs > li + li, 
.rank-list > li + li {
     border-top: 1px var(--color-borders) solid;   
}

.user-tools.sticky-content.is-stuck, 
.story-intro .art .fig-graphic + figcaption,
.story-related, 
.widget-nav-list li.current a {
     border-bottom: 1px var(--color-borders) solid;  
}

.categories-list li a::before {
	border-right: 1em solid #444;
}
#phase-graphic-labels > div {
    background-color: transparent !important;
}
#sticky-nav.current.jsx-4041443054 {
    box-shadow: 0px 5px 10px -4px #111;
}
.navigation .PibNavigation-root a svg {
    fill: #ddd !important;
}
.module-promo-grid.big-left .subtle-header h2, .module-promo-grid.big-centered .subtle-header h2 {
    color: #ddd;
}
.slider__pagination-scroll {
    background-color: #ddd;
    border: 1px solid #ddd;
}
.message {
    background-color: #333 !important;
    box-shadow: 0 2px 5px 0 #4d4d4d80 !important;
}
.message-placeholder.jsx-2210950735 {
    background: #393939 !important;
}
.fade-transition {
    height: 15px;
    background: linear-gradient(#393939,rgba(57,57,57,0.5) 70%,rgba(57,57,57,0)) !important;
}
.hide-background-line.jsx-248961517 {
    background-color: #393939;
}
.byline-identity .title {
    color: #ddd !important;
}
.byline-timestamp {
    color: #ddd !important;
}
.image .content-container {
    background: #393939 !important;
}
.banner-announce {
    background: #393939;
}
.story-full a {
    background: #444;
    border: 2px solid #353535;
}
.groundhog-nav {
    border-bottom: 1px solid #999;
    background: #393939;
}
}
