css:
@-moz-document regexp("^https?://www\\.applyabroad\\.org/(?!wiki).*$") {
@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.ttf') format('truetype');
  font-weight: normal;
}

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.ttf') format('truetype');
  font-weight: bold;
}

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.ttf') format('truetype');
  font-weight: 300;
}

* {
	font-family: Roboto, IRANSans, Vazir, sans-serif !important;
}

#wgo,
#footer,
#peng2far,
#pollinfo,
#thread_info,
#ticker-wrapper,
#navbar_notice_3,
#sidebar_container,
#forum_info_options,
#cke_top_vB_Editor_QR_editor,
a.collapse,
div.moderators,
h4.subforumlistlabel,
.userinfo_extra,
.postbit_reputation,
.postdetails .after_content,
.postbitlegacy .userinfo .rank,
.postbitlegacy .postdetails .userinfo {
    display: none !important;
}

.forumbit_post .forumactionlinks {
	visibility: hidden;
}

body {
	margin: 0;
}

.body_wrapper {
	width: 75%;
    margin: 0 auto;
}

#content_container.contentright #content {
	margin-left: 0;
}

.postbitlegacy .postbody {
	margin-right: 0;
}

.threadmeta,
.searchtitle,
.threadbit .threaddeleted,
.threadbit .threadlastpost, 
.threadbit .threadpostedin p,
.threadbit .threadlastpost dt,
.threadbit .threadlastpost dd {
	height: auto;
}

b,
strong,
.blockhead h2,
a.textcontrol,
.textcontrols a, 
.threadlisthead,
.threadlisthead a,
#forum_icon_legend h5,
h2.searchlisthead span,
#forums .L1 .forumhead a,
.toplinks ul.isuser li a,
.actionbuttons .group .button,
.threadbit .threadtitle_unread,
.forumbit_nopost .forumhead span, 
.forumbit_post .forumhead h2 span,
.forumbit_nopost .forumhead .collapse, 
.forumbit_nopost .forumhead .forumtitle, 
.forumbit_post .foruminfo .forumdata .forumtitle,
.cke_skin_kama .cke_dialog_contents a.cke_dialog_ui_button_ok, 
.cke_skin_kama .cke_dialog_contents a.cke_dialog_ui_button_cancel {
	font-weight: 500 !important;
}

.footer a,
.blockhead a, 
.postcounter,
.threadlisthead,
.threadlisthead a,
#forum_icon_legend h5,
font[color="#FF0000"],
font[color="#ff0000"],
font[color="#0000FF"],
font[color="#0000ff"],
dl.stats dt.folder_count, 
dl.stats dd.folder_count,
.forumbit_nopost .forumhead span, 
.forumbit_post .forumhead h2 span,
.forumbit_nopost .forumhead .collapse, 
.forumbit_nopost .forumhead .forumtitle, 
.forumbit_nopost .forumhead .forumtitle a {
    color: #333 !important;
}

.postbitlegacy .posthead,
.postbitlegacy .posthead a,
.postbitlegacy .posthead .time,
.postbitlegacy .posthead .postdate {
    color: #bbb !important;
    opacity: .8;
    font-size: 11px;
}


.footer,
.blockhead,
.threadlisthead,
.searchlisthead,
.searchlisthead a,
.postbit .posthead, 
.eventbit .eventhead,
.forumbits .forumhead,
#forum_icon_legend h5,
#memberlist .blockhead,
.postbitlegacy .posthead, 
.forumbit_post .forumrow, 
.forumbit_post .forumhead, 
.forumbit_nopost .forumrow, 
.forumbit_nopost .forumhead {
	background: #fafafa !important;
    color: #333 !important;
    border-radius: 0 !important;
}

#pagetitle h1 {
	font-size: 22px !important;
    font-weight: 500 !important;
    margin: 30px 0;
    text-align: center;
}

.pmbit, 
.footer,
.eventbit,
.blockhead,
.forumhead,
.formcontrols,
.visitormessage,
.searchlisthead,
.threadlisthead,
.postbit .posthead, 
.threadbit .sticky,
.eventbit .eventhead,
.threadbit .nonsticky,
#forum_icon_legend h5,
#memberlist .blockhead,
#usercp_nav .blockbody,
.postbit, .postbitlegacy, 
.postbitlegacy .posthead, 
.forumbit_post .forumrow, 
.forumbit_nopost .forumrow,
.textbox, textarea, select,
.blockbody.settings_form_border {
    border-color: #ddd !important;
}

.posthead,
.forumhead,
.threadlisthead,
#memberlist .blockhead {
	border-bottom: none !important;
}

hr,
#cse-search-box,
.navtabs li a.navtab,
.postbitlegacy .postbody,
.globalsearch .buttoncontainer span,
.globalsearch .textboxcontainer span {
	border: none;
}

.blocksubhead,
.formcontrols .blockrow {
	border-top: none;
}

.navlinks,
.threadbit .alt,
.bbcode_container .bbcode_quote_container {
	background: none;
}

.blocksubhead,
.formcontrols {
	background-color: #fff !important;
}

.formcontrols .section {
	padding: 0;
}

.navbar {
	height: 70px;
}

.navtabs ul {
	padding-top: 8px;
}

 
.postbit .posthead, 
.eventbit .eventhead,
.blockhead, .formcontrols,
.postbitlegacy .postfoot .textcontrols,
.postbitlegacy .postfoot .textcontrols a,
.postbitlegacy .postfoot .textcontrols a.quickreply, 
.postbit .postfoot .textcontrols a.quickreply, 
.postbitlegacy .postfoot .textcontrols a.forwardpost, 
.postbit .postfoot .textcontrols a.forwardpost {
	background-color: #f3f3f3 !important;
    font-weight: normal !important;
}

.postbitlegacy .postdetails, 
.eventbit .eventdetails, div.userinfo {
	background-color: #f9f9f9 !important;
}

.cke_skin_kama .cke_wrapper.cke_ltr, 
.cke_skin_kama .cke_wrapper.cke_rtl {
	background: none !important;
}

.bbcode_container div.bbcode_quote {
	border: 1px solid rgba(65, 115, 148, 0.09);
}

.pagination span a {
	font-size: 11px;
    border: none;
    background: none;
}

.navtabs {
	background: #7192A8;
}

.navtabs li a.navtab {
	font-weight: normal;
    font-size: 11px;
}

#q {
	background: #fff !important;
    border: 1px solid #fff;
}

#q,
.navbar_search input {
	border-radius: 3px !important;
}

#q,
#q + input {
	display: none;
}

ul.navbar_advanced_search {
	position: relative;
    top: 10px;
}

.threadbit .sticky,
.threadbit .nonsticky,
.forumbit_post .forumrow,
.forumbit_nopost .forumrow {
	background: #fff !important;
    padding-top: 15px;
    padding-bottom: 15px;
    line-height: 23px;
    font-weight: normal;
}

.threadbit .pagination,
.threaddetailicons {
	display: none;
}

.threadbit .threadtitle {
	background-image: none !important;
}

#reportlist_table td {
	font-size: 10px !important;
}

.arblocksubhead {
	background: #fafafa;
    margin: 0;
}

.arblocksubhead {
	border: 1px solid #ddd;
}

#reportlist_table tr:hover td {
	background: #f3f3f3;
}

.threadbit .threadmeta {
	position: absolute;
    right: 62px;
    direction: ltr;
}
}

@-moz-document url("http://www.applyabroad.org/forum/index.php"), url("http://www.applyabroad.org/forum/"), url("http://www.applyabroad.org/forum/forum.php") {
/* Homepage */
div#pagetitle {
	display: none;
}
}

@-moz-document regexp("https?://www.applyabroad.org/wiki.*") {
html, body, input, .cnotice-message p {
  font-size: 83% !important;
  font-family: Roboto, IRANSans, Iranian Sans, Arial, sans-serif !important;
}

td.MainPageBG a[href="/wiki/index.php/%D9%BE%D8%B1%D9%88%D9%86%D8%AF%D9%87:Main.jpg"] {
	display: none;
}

.mw-wiki-logo {
	background-size: 100%;
}

td.MainPageBG {
	padding: 10px 15px !important;
}

b, strong {
	font-weight: 500;
}

p, #toc {
  line-height: 27px !important;
}

.mw-body p br {
	margin-bottom: 15px !important;
    display: block;
    content: " ";
}

h1, h2, h3, h4, h5, h6, h1 i {
  font-style: normal !important;
  font-family: Droid Arabic Naskh, Iranian Sans, sans-serif !important;
}

div#simpleSearch #searchInput {
  font-size: 11px !important;
  padding: 0 10px 20px !important;
}

body,
#mw-page-base {
	background: #fff !important;
}

table.ambox-content {
	margin-bottom: 20px !important;
}

.mw-body h1, 
.mw-body-content h1, 
.mw-body-content h2 {
	padding-bottom: .3em !important;
}

.liner-switch-label {
	float: right !important;
    margin-left: 15px !important;
}
}
