css:
@-moz-document domain("twitter.com") {
@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.ttf') format('truetype');
  font-weight: normal;
}

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.ttf') format('truetype');
  font-weight: bold;
}

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.ttf') format('truetype');
  font-weight: 300;
}

.ProfileCanopy-inner {
	position: static !important;
}

.TweetTextSize.TweetTextSize--jumbo {
	font-weight: normal !important;
}

/*.ProfilePage #page-container > div.AppContainer > div > div > div.Grid-cell.u-size1of3.u-lg-size1of4,*/
.ProfilePage #page-container > div.AppContainer > div > div > div.Grid-cell.u-size2of3.u-lg-size3of4 > div > div.Grid-cell.u-size1of3 {
	visibility: hidden;
}

.selected-stream-item:focus {
 	box-shadow: 0 1px 5px 0 #b1b1b1 !important;
    background: #fff !important;
}

div.stream-item-header > a > strong > span.Emoji.Emoji--forLinks {
	display: none !important;
}

.cropper-mask {
  border: 1px dashed #ccc;
}

.TwitterCard .PollXChoice {
	background: transparent !important;
}

.top-timeline-tweetbox .tweet-user, 
.top-timeline-tweetbox .timeline-tweet-box {
	background: #fff;
}

body {
	background: #fcfcfc;
}

.stream-item:not(.no-header-background-module) {
	border-left: none;
    border-right: none;
}

.AppContent {
	padding-top: 45px;
}

.tweet {
	border-bottom: 1px solid #f7f7f7;
}

.wrapper-home .content-main,
.wrapper-connect .content-main,
body.three-col .wrapper-home .content-main,
body.three-col .wrapper-connect .content-main {
	box-shadow: 0 2px 5px #ddd;
	margin: 0 auto;
	float: none;
}

.nav .session .dropdown-toggle:before {
	box-shadow: none;
}

.stream-item.separated-module,
.stream-item.separated-module+.stream-item, 
.stream-item.separated-module+.stream-item .tweet:hover, 
.PromptbirdPrompt-streamItem.separated-module+.stream-item {
	margin-top: 0;
   	margin-bottom: 0;
    border-radius: 0;
}

.content-header,
.ProfileHeading-content,
.NotificationsSettingsButton,
.top-timeline-tweetbox .timeline-tweet-box,
.SidebarCommonModules > div {
	border: none;
}

.dashboard-left,
.dashboard-right {
	display: none;
}

.more-tweet-actions,
a[title="Facebook Share"],
.profile-card-inner[data-screen-name="delyssid"] {
  display: none;
}

.profile-card.profile-header .profile-picture img {
  border-radius: 10px !important;
}

.RichEditor {
  border-radius: 0 !important;
}

.profile-header .profile-card-inner {
  text-shadow: 0 1px 2px rgba(0, 0, 0, 1) !important;
}

.media-attribution,
.translator-large {
  display: none !important;
}

.js-tweet-text,
.js-sidenav-link,
.js-sidenav-link a,
.NewTweetButton-text,
.SummaryCard-content {
  font-size: 15px;
  font-family: Roboto, IRANSans, Vazir, Droid Arabic Naskh, Iranian Sans, Helvetica Neue, Helvetica, Arial, sans-serif;
  /*-webkit-font-smoothing: antialiased !important;*/
}

.tooltip,
.DMUpdateName,
body.ms-windows,
.js-retweet-text,
.nav > li > a .text,
 .account-summary .pretty-link,
.ProfileHeaderCard-locationText,
#profile-hover-container .profile-social-proof a,
.TwitterCard .PollXChoice-optionContainer .PollXChoice-choice,
.Trends .context-trend-item .trend-name,
.ProfileNameTruncated-link {
  font-family: Roboto, IRANSans, Vazir, Droid Arabic Naskh, Iranian Sans, Helvetica Neue, Helvetica, Arial, sans-serif;
  /*-webkit-font-smoothing: antialiased !important;*/
}

.btn-link.translate-button,
.Trends--wide .trend-item, 
.Trends--wide .promoted-text,
.ReplyingToContextBelowAuthor {
	font-size: 13px !important;
    font-family: Roboto, IRANSans, Vazir, Droid Arabic Naskh, Iranian Sans, Helvetica Neue, Helvetica, Arial, sans-serif;
}

.tweet>.context .with-icn {
    font-size: 11px !important;
    font-family: Roboto, IRANSans, Vazir, Droid Arabic Naskh, Iranian Sans, Helvetica Neue, Helvetica, Arial, sans-serif;
}

.bio,
.tweet-rtl,
.tweet-text-rtl,
input[dir="rtl"],
textarea[dir="rtl"],
div[dir="rtl"],
div[dir="rtl"] div,
.activity-supplement .tweet-text,
.activity-truncated-tweet .tweet-text,
.stream-item-activity-notification,
.ProfileHeaderCard-nameLink,
.u-dir[dir="rtl"] {
  font-size: 14px !important;
  font-family: Roboto, IRANSans, Vazir, Droid Arabic Naskh, "Gotham Narrow SSm", Iranian Sans, Arial, sans-serif !important;
  line-height: 24px !important;
  /*-webkit-font-smoothing: antialiased !important;*/
}

.js-stream-item .content .js-tweet-text {
  margin-top: 3px !important;
}

.bio {
  font-style: normal !important;
}

.fullname,
.QuoteTweet-originalAuthor,
.DMInboxItem-snippet {
  font-weight: 500;
  font-family: Roboto, IRANSans, Vazir, Droid Arabic Naskh, Iranian Sans, Arial, Helvetica World, sans-serif;
}

.avatar,
.ProfileAvatar,
.ProfileAvatar-image, 
.profile-picture,
.ProfileAvatar-placeholderImage {
	border-radius: 50% !important;
}

.DMInboxItem-snippet {
	line-height: 18px;
}

.TwitterCard-title,
.TwitterCard .SummaryCard-content p,
.TwitterCard .SummaryCard-destination {
	line-height: 18px !important;
    font-size: 13px !important;
    max-height: none !important; 
}

.js-profileClusterFollow.has-content {
	margin-top: 3px;
}

/* Twitcher */
#twitcher-donate,
li.twitcher-inserted + .dropdown-divider.twitcher-inserted {
	display: none;
}

.nav li.twitcher-inserted a {
	padding: 1px 20px;
    font-size: 11px;
}

.nav li.twitcher-inserted a img {
	width: 18px;
    border-radius: 50%;
}

.btn:focus, 
.btn.focus, 
.Button:focus, 
.EmojiPicker-item.is-focused, 
.EmojiPicker .EmojiCategoryIcon:focus, 
.EmojiPicker-skinTone:focus + .EmojiPicker-skinToneSwatch, 
a:focus > img:first-child:last-child, 
button:focus {
	box-shadow: none;
}


}
