css:
@-moz-document domain("trello.com") {
@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.ttf') format('truetype');
  font-weight: normal;
}

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.ttf') format('truetype');
  font-weight: bold;
}

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Light.ttf') format('truetype');
  font-weight: 300;
}

/* Scrollbars */
.u-fancy-scrollbar::-webkit-scrollbar {
    height: 2px !important;
    width: 2px !important;
}

/* Badges */
.badge.is-complete {
	padding: 0 1px !important;
}

.badge.is-complete .badge-text {
    font-size: 10px !important;
}

.badge.is-complete .icon-sm {
	font-size: 8.5pt !important;
}

/* Typography */
body,
.phenom-desc,
.aj-item-name,
.list-card-title,
.action-comment p,
.comment-box-input,
.checklist-title h3,
.description-content,
.window-module-title h3,
.card-label.mod-selectable,
.card-label.mod-card-detail,
.checklist-item-details-text,
.textarea.mod-card-back-title,
.window-header-inline-content a,
button, html, input, select, textarea,
#aj-nextstep-mode {
	font-family: "Helvetica Neue", Roboto-Regular, IRANSans, Vazir, serif !important;
    font-weight: normal !important;
}

code, pre, tt {
	font: 11px Fira Code, Hasklig, monospace;
}

code, pre {
	background: #efefef;
    border: none;
}

code {
	padding: 1px 4px;
}

.header-btn,
.button-link,
.list-header-name,
textarea.mod-list-name,
textarea.mod-card-back-title,
.board-header-btn-name .board-header-btn-text {
	font-family: Roboto-Medium, IRANSans, Vazir, serif !important;
    font-weight: 500 !important;
}

.card-label {
	text-shadow: none !important;
}

.list-card-labels .card-label {
	font: 9px Lucida Grande, IRANSans, Vazir, serif !important;
}

.card-label.mod-card-front {
	min-width: 15px;
}

.description-content {
	line-height: 25px !important;
}

.window-header textarea {
	min-height: 30px !important;
}

/* General */
#header {
	box-shadow: none !important;
}

.member,
.member-avatar {
	border-radius: 50% !important;
}

.header-btn .member-avatar {
	border-radius: 0 !important;
}

.checklist-items-list {
	margin-top: 20px !important;
}

.list-card-details {
	padding: 9px 6px 2px 8px !important;
}

.trelabels-tag .card-label {
    font-weight: 200 !important;
    -webkit-font-smoothing: antialiased !important;
}

.list-card-title,
textarea.list-card-composer-textarea {
	font-size: 13px !important;
}

.trelabels-tag .card-label {
    -webkit-font-smoothing: subpixel-antialiased !important;
}

.compact-board-tile-link-text-sub-name {
	font-size: 10px !important;
}

.action-comment {
	line-height: 22px !important;
}

.known-service-link {
	text-decoration: none;
    font-size: 13px;
    background: #eaeaea;
}

.known-service-icon {
    vertical-align: sub;
}

/* Checklists */
.checklist-item-details-text {
	line-height: 20px !important;
    font-size: 13px !important;
    padding: 2px 3px 5px 5px !important;
    margin-bottom: 5px !important;
}

/* Ultimello */
.by-ultimello {
	display: none;
}

.connections-button {
	height: 34px !important;
}

.card-connections-menu {
	right: 30px;
}

.notification {
	font-weight: normal !important;
    font-size: 90% !important;
}

.ultimello-cards-count {
	opacity: .2;
}

.ultimello-notifications-card-name {
	font-size: 100% !important;
}

/* Trellists */
#trellists {
	background: transparent;
}

#trellists .show-all, 
#trellists .hide-all {
	 color: silver;
}

#trellists .show-all:hover, 
#trellists .hide-all:hover {
	 color: #fff;
}

/* Next Step */
#aj-nextstep-menuitem-4,
#aj-nextstep-selector .pop-over-header,
body.aj-nextstep-display-ant .aj-nextstep-ant-icon {
	display: none !important;
}

.aj-item-name {
	font-size: 11px;
    line-height: 15px;
    position: relative;
    bottom: 2px;
}

.aj-next-step {
    margin-top: 0;
}

/* Pro For Trello */
.tpro-tag.tpro-priority,
#tpro-filter-trigger .u-text-underline {
	display: none;
}

#tpro-filter,
#tpro-filter-trigger .u-text-underline {
	text-decoration: none !important;
}

.list-card.tpro-priority-normal {
    border-left: none;
}

.list-card.tpro-priority-critical {
	border-left: none;
    border-bottom: 3px solid rgb(192,57,53);
}

.list-card.tpro-priority-medium {
	border-left: none;
    border-bottom: 3px solid rgb(241, 196, 15);
}

.list-card.tpro-priority-high {
	border-left: none;
    border-bottom: 3px solid rgb(230, 126, 34);
}


.tpro-tag.tpro-label,
.tpro-tag.tpro-point,
.tpro-tag.tpro-point .fa,
.tpro-tag.tpro-time-entry,
.tpro-tag.tpro-time-entry .fa {
	color: #8e8e8e;
    font-weight: 300;
    font-size: 9pt;
}

.tpro-time-entry .fa-calculator:before {
	content: "\f017";
}

/* Toggl Timer */
#toggl-button-project-filter, 
#toggl-button-tag-filter {
	width: auto !important;
}
}
