css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vid.me") {

	body, body.pg-video-view {
		background-color: #161616;
	}

	.header {
    	background-color: #333333;
	}

	.opaque {
		background-color: #333333;
	}

	.navbar navbar-inverse {
		background-color: #333333;
	}

	.navbar {
		background-color: #333333;
	}

	.navbar-inverse {
		background-color: #333333;
	}

	.header.opaque {
		background-color: #333333;
		color: #33333
	}

	.header.opaque .main-links > li > a {
    	color: white;
	}

	.header.opaque .main-links > li > a:hover {
    	background-color: #1b6d85;
	}

	.header.opaque .menu > li > a {
		color: white;
	}

	.header.opaque .menu > li a:hover, .header.opaque .menu > li a:focus {
		color: white
	}

	.header.opaque .menu > li > a:hover {
		background-color: #1b6d85;
	}

	.tabs-big-wrapper h1 {
		color: white;
	}

	.videos_list > li > .inner {
		background-color: #1e1e1e;
	}

	.videos_list > li .thumb_title_label {
		color: #DAD5D5
	}

	.header.opaque .search input {
		color: white;
	}

	* input[type="text"], input[type="password"], .content textarea {
    	color: white;
    	background-color: #333333;
	}

	*::selection {
    	color: white;
    	background: #1b6d85;
    	text-shadow: 1px 1px 2px #104554;
	}

	*::-moz-selection {
    	color: white;
    	background: #1b6d85;
    	text-shadow: 1px 1px 2px #104554;
	}

	.users_list .user_photo > a {
		background-color: none;
	}

	.users_list .user_username {
		color: white;
	}

	.dropdown-menu {
		background-color: #333333;
	}

	.dropdown-menu > li > a {
		color: white;
	}

	#channel_view h1, #tag h1 {
		color: white;
	}

	.tabs li.active a {
		color: white;
	}

	#video_view > .inner {
		background-color: #1e1e1e;
	}

	#video_view .video_info3 {
		background-color: #191919;
	}

	#video_view .video_info2 {
		background-color: #191919;
	}

	#video_view .video_description_wrapper_truncated {
		background-color: #191919;
	}

	#video_view .video_likes, #video_view .video_tips, #video_view .comments {
		background-color: #191919;
	}

	#video_view .video_description_wrapper {
		background-color: #191919;
	}

	#video_view .video_description_more {
		background-color: #262626;
	}

	#video_view .video_description_wrapper_expanded .video_description_more {
		border-top: 1px solid #2d2d2d;
	}

	.video_description_more_border_top_truncated {
		border-bottom: 1px solid #262626;
		background: none;
	}

	#video_view .video_title, #video_view .video_headline {
		color: white;
	}

	#video_view .video_author_wrapper .video_author_username {
		color: #777;
	}

	#video_view .sort_comments > * {
		color: white;
	}

	.dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus {
		background-color: #1b6d85;
	}

	.dropdown-menu > li > a:hover {
		color: white;
	}

	.comments_list .comment_author_username {
		color: white;
	}

	.comments_list .comment_body {
		color: #cccccc;
	}

	.footer {
		background-color: #333333;
	}

	.footer ul li a {
		color: white;
	}

	.footer ul li a:hover {
		color: white;
		background-color: #1b6d85;
	}

	#video_view .video_suggestions, #video_view .video_nav {
		background-color: #191919;
	}

	#video_view .video_suggestions .title > a {
		color: white;
	}

	#video_view .video_suggestions .owner > a {
		color: #cccccc;
	}

	#video_view .video_description {
		color: #cccccc;
	}

	#user_view > .inner {
		background-color: #262626;
	}

	.modal-content {
		background-color: #262626;
	}

	.modal .modal-title {
		color: #cccccc;
	}

	.modal .form-control {
		background-color: #333333;
		color: white;
	}

	#user_view .user_info2 .user_username {
		color: white;
	}

	body.pg-settings, body.pg-settings-notifications, body.pg-settings-social, body.pg-settings-uploads, body.pg-settings-subscriptions, body.pg-settings-monetize, body.pg-monetize-tips, body.pg-monetize-subscribers {
		background-color: #161616;
	}

	body.pg-user-manage {
		background-color: #161616;
	}

	#settings {
		background-color: #262626;
	}

	.manage_page {
		background-color: #262626;
	}

	.page_header h1 {
		color: white;
	}

	#settings .form-group > label {
		color: white;
	}

	#settings .form-settings input {
		color: white;
		background-color: #333333;
	}

	.form-radio {
		color: white;
	}

	#index .listing .headline > h2 {
		color: white;
	}

	#uploader-modal .step1 > .upload-option {
		background-color: #333333;
	}

	#index .splash {
		background-color: #262626;
	}

	#index .splash .slogan {
		color: white;
	}

	body.pg-messages {
		background-color: #262626;
	}

	#messages h1 {
		color: white;
	}

	.alert-info {
		color: #cccccc;
	}

	.pg-user-verifyemail h1 {
		color: white;
	}

	.message-composer > textarea {
		color: white;
	}
}
